import React, { useState, useEffect } from 'react';
import { apiService } from '../services/Api';
import '../styles/MyBookings.css';

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadUserBookings();
  }, []);

  const loadUserBookings = async () => {
    try {
      setLoading(true);
      
      // В реальном приложении здесь был бы запрос к API для получения броней пользователя
      // const userBookings = await apiService.getUserBookings(userId);
      
      // Пока используем localStorage для демонстрации
      const lastBooking = localStorage.getItem('lastBooking');
      const userBookings = localStorage.getItem('userBookings');
      
      const allBookings = [];
      
      if (lastBooking) {
        const booking = JSON.parse(lastBooking);
        if (booking.status && booking.status !== 'draft') {
          allBookings.push(booking);
        }
      }
      
      if (userBookings) {
        const storedBookings = JSON.parse(userBookings);
        allBookings.push(...storedBookings);
      }
      
      setBookings(allBookings);
      
    } catch (err) {
      console.error('Error loading bookings:', err);
      setError('Не удалось загрузить данные о бронированиях');
    } finally {
      setLoading(false);
    }
  };

  const getStatusInfo = (status) => {
    const statusMap = {
      'draft': { text: 'Черновик', color: '#6c757d', icon: '📝' },
      'confirmed': { text: 'Подтверждено', color: '#17a2b8', icon: '✅' },
      'active': { text: 'Активно', color: '#28a745', icon: '🟢' },
      'completed': { text: 'Завершено', color: '#6c757d', icon: '🏁' },
      'cancelled': { text: 'Отменено', color: '#dc3545', icon: '❌' },
      'payment_pending': { text: 'Ожидает оплаты', color: '#ffc107', icon: '💰' },
      'payment_completed': { text: 'Оплачено', color: '#28a745', icon: '💳' }
    };
    
    return statusMap[status] || { text: status, color: '#6c757d', icon: '❓' };
  };

  const formatDateTime = (dateString, timeString) => {
    if (!dateString) return 'Не указано';
    
    const date = new Date(dateString);
    const formattedDate = date.toLocaleDateString('ru-RU', {
      day: 'numeric',
      month: 'long',
      weekday: 'short'
    });
    
    return timeString ? `${formattedDate} ${timeString}` : formattedDate;
  };

  const handlePayNow = (bookingId) => {
    // Логика для оплаты брони
    alert(`Оплата брони #${bookingId}`);
    // В реальном приложении здесь был бы переход на страницу оплаты
  };

  const handleCancelBooking = (bookingId) => {
    if (window.confirm('Вы уверены, что хотите отменить бронирование?')) {
      // Логика отмены брони
      const updatedBookings = bookings.map(booking => 
        booking.id === bookingId 
          ? { ...booking, status: 'cancelled' }
          : booking
      );
      
      setBookings(updatedBookings);
      localStorage.setItem('userBookings', JSON.stringify(updatedBookings));
    }
  };

  const handleBackToHome = () => {
    window.location.href = '/';
  };

  if (loading) {
    return (
      <div className="my-bookings-container">
        <div className="loading-spinner">Загрузка ваших бронирований...</div>
      </div>
    );
  }

  return (
    <section id="my-bookings" className="my-bookings-section">
      <div className="background-container">
        <img src="/images/67f504fdfc00ad2f7d384258d27391b08ef7aabd.png" alt="Abstract background" className="bg-image" />
        <div className="bg-overlay"></div>
      </div>
      
      <div className="my-bookings-container">
        <div className="my-bookings-header">
          <h1>📋 Мои бронирования</h1>
          <button onClick={handleBackToHome} className="btn secondary">
            На главную
          </button>
        </div>

        {error && (
          <div className="error-message">
            ⚠️ {error}
          </div>
        )}

        {bookings.length === 0 ? (
          <div className="no-bookings">
            <div className="no-bookings-icon">📭</div>
            <h2>У вас нет активных бронирований</h2>
            <p>Забронируйте место в компьютерном клубе, чтобы увидеть его здесь</p>
            <button 
              onClick={() => window.location.href = '/booking'} 
              className="btn primary"
            >
              Забронировать место
            </button>
          </div>
        ) : (
          <div className="bookings-list">
            {bookings.map((booking) => {
              const statusInfo = getStatusInfo(booking.status);
              const isPaymentPending = booking.status === 'payment_pending';
              const canCancel = ['draft', 'confirmed', 'payment_pending'].includes(booking.status);
              
              return (
                <div key={booking.id} className="booking-card">
                  <div className="booking-header">
                    <div className="booking-info">
                      <h3>Бронь #{booking.id.replace('temp_', '')}</h3>
                      <span 
                        className="booking-status"
                        style={{ backgroundColor: statusInfo.color }}
                      >
                        {statusInfo.icon} {statusInfo.text}
                      </span>
                    </div>
                    <div className="booking-date">
                      Создано: {new Date(booking.created_at).toLocaleDateString('ru-RU')}
                    </div>
                  </div>

                  <div className="booking-details">
                    <div className="detail-row">
                      <div className="detail-item">
                        <span className="detail-label">📍 Место:</span>
                        <span className="detail-value">№{booking.selectedPlace}</span>
                      </div>
                      <div className="detail-item">
                        <span className="detail-label">🏢 Клуб:</span>
                        <span className="detail-value">{booking.formData?.address}</span>
                      </div>
                    </div>

                    <div className="detail-row">
                      <div className="detail-item">
                        <span className="detail-label">🕐 Начало:</span>
                        <span className="detail-value">
                          {formatDateTime(booking.formData?.dateFrom, booking.formData?.timeFrom)}
                        </span>
                      </div>
                      <div className="detail-item">
                        <span className="detail-label">🕐 Окончание:</span>
                        <span className="detail-value">
                          {formatDateTime(booking.formData?.dateTo, booking.formData?.timeTo)}
                        </span>
                      </div>
                    </div>

                    <div className="detail-row">
                      <div className="detail-item">
                        <span className="detail-label">⏱️ Длительность:</span>
                        <span className="detail-value">
                          {booking.calculatedData?.bookingHours || 0} часов
                        </span>
                      </div>
                      <div className="detail-item">
                        <span className="detail-label">💻 Тариф:</span>
                        <span className="detail-value">{booking.placeRate} ₽/час</span>
                      </div>
                    </div>

                    {booking.cartItems && booking.cartItems.length > 0 && (
                      <div className="booking-items">
                        <span className="detail-label">🍽️ Заказ из кафе:</span>
                        <div className="items-list">
                          {booking.cartItems.map(item => (
                            <div key={item.id} className="cart-item">
                              {item.name} × {item.quantity} - {item.price * item.quantity} ₽
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                    <div className="booking-total">
                      <span className="total-label">💰 Итого к оплате:</span>
                      <span className="total-amount">
                        {booking.calculatedData?.totalCost || booking.totalPrice || 0} ₽
                      </span>
                    </div>
                  </div>

                  <div className="booking-actions">
                    {isPaymentPending && (
                      <button 
                        onClick={() => handlePayNow(booking.id)}
                        className="btn primary"
                      >
                        💳 Оплатить сейчас
                      </button>
                    )}
                    
                    {canCancel && (
                      <button 
                        onClick={() => handleCancelBooking(booking.id)}
                        className="btn secondary"
                      >
                        ❌ Отменить бронь
                      </button>
                    )}
                    
                    <button className="btn outline">
                      📞 Связаться с поддержкой
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}

        {/* Статистика */}
        {bookings.length > 0 && (
          <div className="bookings-stats">
            <div className="stat-card">
              <span className="stat-number">{bookings.length}</span>
              <span className="stat-label">Всего броней</span>
            </div>
            <div className="stat-card">
              <span className="stat-number">
                {bookings.filter(b => b.status === 'active').length}
              </span>
              <span className="stat-label">Активные</span>
            </div>
            <div className="stat-card">
              <span className="stat-number">
                {bookings.filter(b => b.status === 'payment_pending').length}
              </span>
              <span className="stat-label">Ожидают оплаты</span>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default MyBookings;
