import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import '../styles/CombinedLayout.css';

const CombinedLayoutBooking = () => {
  const { 
    cartItems, 
    updateCartItemQuantity, 
    clearCart, 
    getTotalPrice, 
    getTotalItems,
    getCartSummary 
  } = useCart();
  
  const [formData, setFormData] = useState({
    place: '',
    date: '',
    timeFrom: '',
    timeTo: '',
    address: '',
    cart: ''
  });

  const [selectedPlace, setSelectedPlace] = useState(null);
  const [showDatePicker, setShowDatePicker] = useState(false);
  const [selectedStartDate, setSelectedStartDate] = useState(null);
  const [selectedEndDate, setSelectedEndDate] = useState(null);
  const [currentMonth, setCurrentMonth] = useState(new Date());
  
  // Состояния для выбора времени
  const [fromHours, setFromHours] = useState('');
  const [fromMinutes, setFromMinutes] = useState('');
  const [toHours, setToHours] = useState('');
  const [toMinutes, setToMinutes] = useState('');

  // Генерация часов и минут
  const hours = Array.from({ length: 24 }, (_, i) => i.toString().padStart(2, '0'));
  const minutes = Array.from({ length: 60 }, (_, i) => i.toString().padStart(2, '0'));

  // Генерация дней месяца для календаря
  const generateCalendarDays = () => {
    const year = currentMonth.getFullYear();
    const month = currentMonth.getMonth();
    
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    
    const startDay = firstDay.getDay();
    const days = [];

    // Пустые ячейки для начала месяца
    for (let i = 0; i < startDay; i++) {
      days.push(null);
    }

    // Дни месяца
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(new Date(year, month, day));
    }

    return days;
  };

  const calendarDays = generateCalendarDays();

  // Названия месяцев
  const monthNames = [
    'Январь', 'Февраль', 'Март', 'Апрель', 'Май', 'Июнь',
    'Июль', 'Август', 'Сентябрь', 'Октябрь', 'Ноябрь', 'Декабрь'
  ];

  // Названия дней недели
  const dayNames = ['Вс', 'Пн', 'Вт', 'Ср', 'Чт', 'Пт', 'Сб'];

  // Переход к предыдущему месяцу
  const prevMonth = () => {
    setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1, 1));
  };

  // Переход к следующему месяцу
  const nextMonth = () => {
    setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 1));
  };

  // Проверка, является ли дата сегодняшней
  const isToday = (date) => {
    const today = new Date();
    return date.toDateString() === today.toDateString();
  };

  // Проверка, является ли дата прошедшей
  const isPastDate = (date) => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    return date < today;
  };

  // Проверка, находится ли дата в выбранном промежутке
  const isInRange = (date) => {
    if (!selectedStartDate || !selectedEndDate) return false;
    return date >= selectedStartDate && date <= selectedEndDate;
  };

  // Проверка, является ли дата началом промежутка
  const isStartDate = (date) => {
    return selectedStartDate && date.toDateString() === selectedStartDate.toDateString();
  };

  // Проверка, является ли дата концом промежутка
  const isEndDate = (date) => {
    return selectedEndDate && date.toDateString() === selectedEndDate.toDateString();
  };

  // Выбор даты в календаре
  const handleDateSelect = (date) => {
    if (isPastDate(date)) return;

    if (!selectedStartDate || (selectedStartDate && selectedEndDate)) {
      // Если нет начальной даты или уже выбран промежуток, начинаем новый
      setSelectedStartDate(date);
      setSelectedEndDate(null);
    } else if (date > selectedStartDate) {
      // Если выбрана дата после начальной, устанавливаем конечную
      setSelectedEndDate(date);
    } else if (date < selectedStartDate) {
      // Если выбрана дата до начальной, меняем начальную
      setSelectedStartDate(date);
      setSelectedEndDate(selectedStartDate);
    }
  };

  // Подтверждение выбора даты
  const handleDateConfirm = () => {
    if (selectedStartDate) {
      const dateString = selectedStartDate.toISOString().split('T')[0];
      setFormData(prev => ({
        ...prev,
        date: dateString
      }));
      setShowDatePicker(false);
    }
  };

  // Отмена выбора даты
  const handleDateCancel = () => {
    setShowDatePicker(false);
    setSelectedStartDate(null);
    setSelectedEndDate(null);
  };

  // Обработчик выбора часов и минут
  const handleTimeChange = (field, value) => {
    switch (field) {
      case 'fromHours':
        setFromHours(value);
        if (value && fromMinutes) {
          setFormData(prev => ({ ...prev, timeFrom: `${value}:${fromMinutes}` }));
        }
        break;
      case 'fromMinutes':
        setFromMinutes(value);
        if (fromHours && value) {
          setFormData(prev => ({ ...prev, timeFrom: `${fromHours}:${value}` }));
        }
        break;
      case 'toHours':
        setToHours(value);
        if (value && toMinutes) {
          setFormData(prev => ({ ...prev, timeTo: `${value}:${toMinutes}` }));
        }
        break;
      case 'toMinutes':
        setToMinutes(value);
        if (toHours && value) {
          setFormData(prev => ({ ...prev, timeTo: `${toHours}:${value}` }));
        }
        break;
      default:
        break;
    }
  };

  // Автоматически обновляем поле корзины при изменении cartItems
  useEffect(() => {
    setFormData(prev => ({
      ...prev,
      cart: getCartSummary()
    }));
  }, [cartItems, getCartSummary]);

  // Функция для установки адреса
  const setAddressFromStorage = () => {
    const savedAddress = localStorage.getItem('selectedClubAddress');
    if (savedAddress) {
      setFormData(prev => ({
        ...prev,
        address: savedAddress
      }));
    }
  };

  // Загружаем адрес при монтировании компонента
  useEffect(() => {
    setAddressFromStorage();
  }, []);

  // Слушаем событие выбора адреса
  useEffect(() => {
    const handleAddressSelected = () => {
      setAddressFromStorage();
    };

    window.addEventListener('clubAddressSelected', handleAddressSelected);
    
    return () => {
      window.removeEventListener('clubAddressSelected', handleAddressSelected);
    };
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    if (name === 'address') {
      localStorage.removeItem('selectedClubAddress');
    }
  };

  const handlePlaceSelect = (placeNumber) => {
    setSelectedPlace(placeNumber);
    setFormData(prev => ({
      ...prev,
      place: placeNumber.toString()
    }));
  };

  // Форматирование даты для отображения
  const formatDateDisplay = (dateString) => {
    if (!dateString) return '';
    
    const date = new Date(dateString);
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);
    
    if (date.toDateString() === today.toDateString()) {
      return 'Сегодня';
    } else if (date.toDateString() === tomorrow.toDateString()) {
      return 'Завтра';
    } else {
      return date.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'long',
        weekday: 'short'
      });
    }
  };

  // Форматирование промежутка дат для отображения
  const formatDateRangeDisplay = () => {
    if (!selectedStartDate) return '';
    
    if (!selectedEndDate || selectedStartDate.toDateString() === selectedEndDate.toDateString()) {
      return formatDateDisplay(selectedStartDate.toISOString().split('T')[0]);
    } else {
      const startStr = selectedStartDate.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'short'
      });
      const endStr = selectedEndDate.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'short'
      });
      return `${startStr} - ${endStr}`;
    }
  };

  // Закрытие пикера при клике вне его
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (showDatePicker && !e.target.closest('.date-picker')) {
        handleDateCancel();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showDatePicker]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const bookingData = {
      ...formData,
      selectedPlace,
      cartItems,
      totalPrice: getTotalPrice()
    };
    console.log('Booking data:', bookingData);
    localStorage.removeItem('selectedClubAddress');
  };

  const handleClearCart = () => {
    clearCart();
  };

  return (
    <section id="combined-booking" className="combined-booking-section">
      <div className="background-container">
        <img src="/images/67f504fdfc00ad2f7d384258d27391b08ef7aabd.png" alt="Abstract background" className="bg-image" />
        <div className="bg-overlay"></div>
      </div>
      
      <div className="combined-container">
        {/* Левая часть - схема мест */}
        <div className="layout-side">
          <h2 className="section-title">Выберите место</h2>
          <div className="layout-content-box">
            <div className="layout-scaler">
              <div className="decorative-vectors">
                <img src="/images/30_51.svg" alt="decorative shape" className="decorative-shape shape-1" />
              </div>
              
              {/* Кликабельные места */}
              {[1, 2, 3, 4, 5, 6].map((num) => (
                <div 
                  key={`pc-${num}`} 
                  className={`pc-setup setup-${num} ${selectedPlace === num ? 'selected' : ''}`}
                  onClick={() => handlePlaceSelect(num)}
                >
                  <img src="/images/6356f02b474a41d638cf709af15fe1f7c6dd92c0.png" alt={`PC setup ${num}`} />
                  <span className="layout-number">{num}</span>
                </div>
              ))}
              
              {[8, 9, 12, 13].map((num) => (
                <div 
                  key={`console-${num}`} 
                  className={`console-setup setup-${num} ${selectedPlace === num ? 'selected' : ''}`}
                  onClick={() => handlePlaceSelect(num)}
                >
                  <img src="/images/1b9fb18a794f8543e1b7ff770153e91c8879c831.png" alt={`Console setup ${num}`} />
                  <span className="layout-number">{num}</span>
                </div>
              ))}
              
              {[10, 11, 14].map((num) => (
                <div 
                  key={`headphones-${num}`} 
                  className={`headphones-setup setup-${num} ${selectedPlace === num ? 'selected' : ''}`}
                  onClick={() => handlePlaceSelect(num)}
                >
                  <img src="/images/2fc05fccb4c07d9e1bb638c4487609fd22b2f1ec.png" alt={`Headphones ${num}`} />
                  <span className="layout-number">{num}</span>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Правая часть - форма бронирования */}
        <div className="booking-side">
          <h2 className="section-title">Бронь места {selectedPlace || ''}</h2>
          
          {/* Информация о корзине */}
          {getTotalItems() > 0 && (
            <div className="cart-info">
              <h3>Корзина ({getTotalItems()} товаров)</h3>
              <div className="cart-items">
                {cartItems.map(item => (
                  <div key={item.id} className="cart-item">
                    <span>{item.name} x{item.quantity}</span>
                    <div className="cart-item-controls">
                      <button 
                        onClick={() => updateCartItemQuantity(item.id, -1)}
                        className="cart-btn"
                      >-</button>
                      <span>{item.quantity}</span>
                      <button 
                        onClick={() => updateCartItemQuantity(item.id, 1)}
                        className="cart-btn"
                      >+</button>
                      <span className="cart-item-price">{item.price * item.quantity} ₽</span>
                    </div>
                  </div>
                ))}
              </div>
              <div className="cart-total">
                Итого: {getTotalPrice()} ₽
              </div>
            </div>
          )}

          <form className="booking-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="place">Выбранное место</label>
              <input 
                type="text" 
                id="place" 
                name="place"
                value={formData.place}
                onChange={handleChange}
                placeholder="Нажмите на место слева"
                readOnly
                className={selectedPlace ? 'selected-place' : ''}
              />
            </div>

            <div className="form-group">
              <label>Дата бронирования</label>
              <div className="date-input-wrapper">
                <input 
                  type="text"
                  value={formatDateRangeDisplay()}
                  placeholder="Выберите дату или промежуток"
                  readOnly
                  onClick={() => setShowDatePicker(true)}
                  className="date-input"
                />
              </div>
            </div>

            <div className="form-group time-group">
              <label>Время бронирования</label>
              <div className="time-selector-grid">
                <div className="time-period">
                  <div className="time-period-label">С</div>
                  <div className="time-inputs-row">
                    <div className="time-select-wrapper">
                      <select 
                        value={fromHours}
                        onChange={(e) => handleTimeChange('fromHours', e.target.value)}
                        className="time-select"
                      >
                        <option value="">Час</option>
                        {hours.map(hour => (
                          <option key={hour} value={hour}>{hour}</option>
                        ))}
                      </select>
                      <span className="time-select-label">ч</span>
                    </div>
                    <div className="time-select-wrapper">
                      <select 
                        value={fromMinutes}
                        onChange={(e) => handleTimeChange('fromMinutes', e.target.value)}
                        className="time-select"
                      >
                        <option value="">Мин</option>
                        {minutes.map(minute => (
                          <option key={minute} value={minute}>{minute}</option>
                        ))}
                      </select>
                      <span className="time-select-label">мин</span>
                    </div>
                  </div>
                </div>
                
                <div className="time-period">
                  <div className="time-period-label">До</div>
                  <div className="time-inputs-row">
                    <div className="time-select-wrapper">
                      <select 
                        value={toHours}
                        onChange={(e) => handleTimeChange('toHours', e.target.value)}
                        className="time-select"
                      >
                        <option value="">Час</option>
                        {hours.map(hour => (
                          <option key={hour} value={hour}>{hour}</option>
                        ))}
                      </select>
                      <span className="time-select-label">ч</span>
                    </div>
                    <div className="time-select-wrapper">
                      <select 
                        value={toMinutes}
                        onChange={(e) => handleTimeChange('toMinutes', e.target.value)}
                        className="time-select"
                      >
                        <option value="">Мин</option>
                        {minutes.map(minute => (
                          <option key={minute} value={minute}>{minute}</option>
                        ))}
                      </select>
                      <span className="time-select-label">мин</span>
                    </div>
                  </div>
                </div>
              </div>
              
              {formData.timeFrom && formData.timeTo && (
                <div className="selected-time-preview">
                  Выбрано время: <strong>{formData.timeFrom} - {formData.timeTo}</strong>
                </div>
              )}
            </div>
            
            {/* Пicker даты с промежутком */}
            {showDatePicker && (
              <div className="date-picker-overlay">
                <div className="date-picker">
                  <div className="date-picker-header">
                    <h3>Выберите дату или промежуток</h3>
                    <button 
                      type="button" 
                      className="date-picker-close"
                      onClick={handleDateCancel}
                    >
                      ×
                    </button>
                  </div>
                  
                  <div className="date-picker-body">
                    <div className="calendar-section">
                      <div className="calendar-header">
                        <button 
                          type="button" 
                          className="calendar-nav prev"
                          onClick={prevMonth}
                        >
                          ‹
                        </button>
                        <div className="calendar-month">
                          {monthNames[currentMonth.getMonth()]} {currentMonth.getFullYear()}
                        </div>
                        <button 
                          type="button" 
                          className="calendar-nav next"
                          onClick={nextMonth}
                        >
                          ›
                        </button>
                      </div>
                      
                      <div className="calendar-grid">
                        {/* Дни недели */}
                        {dayNames.map(day => (
                          <div key={day} className="calendar-day-header">
                            {day}
                          </div>
                        ))}
                        
                        {/* Дни месяца */}
                        {calendarDays.map((date, index) => (
                          <button
                            key={index}
                            type="button"
                            className={`calendar-day ${
                              date ? (
                                isPastDate(date) ? 'past' :
                                isStartDate(date) ? 'start-date' :
                                isEndDate(date) ? 'end-date' :
                                isInRange(date) ? 'in-range' :
                                isToday(date) ? 'today' : ''
                              ) : 'empty'
                            }`}
                            onClick={() => date && handleDateSelect(date)}
                            disabled={!date || isPastDate(date)}
                          >
                            {date ? date.getDate() : ''}
                            {date && isToday(date) && <div className="today-dot"></div>}
                          </button>
                        ))}
                      </div>
                    </div>
                    
                    <div className="date-picker-info">
                      <div className="selected-range">
                        <strong>Выбранный промежуток:</strong>
                        <div className="range-display">
                          {selectedStartDate ? (
                            <>
                              {selectedStartDate.toLocaleDateString('ru-RU', {
                                day: 'numeric',
                                month: 'long',
                                year: 'numeric'
                              })}
                              {selectedEndDate && selectedEndDate.toDateString() !== selectedStartDate.toDateString() && (
                                <> — {selectedEndDate.toLocaleDateString('ru-RU', {
                                  day: 'numeric',
                                  month: 'long',
                                  year: 'numeric'
                                })}</>
                              )}
                            </>
                          ) : (
                            'Выберите дату кликом'
                          )}
                        </div>
                      </div>
                      <div className="date-picker-hint">
                        💡 Нажмите на дату чтобы выбрать один день, или выберите вторую дату для промежутка
                      </div>
                    </div>
                  </div>
                  
                  <div className="date-picker-footer">
                    <div className="date-picker-actions">
                      <button 
                        type="button" 
                        className="btn secondary"
                        onClick={handleDateCancel}
                      >
                        Отмена
                      </button>
                      <button 
                        type="button" 
                        className="btn primary"
                        onClick={handleDateConfirm}
                        disabled={!selectedStartDate}
                      >
                        Выбрать дату
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            )}
            
            <div className="form-group">
              <label htmlFor="address">Адрес клуба</label>
              <select 
                id="address" 
                name="address"
                value={formData.address}
                onChange={handleChange}
                required
              >
                <option value="">Выберите клуб</option>
                <option value="ул. Рахова, 53">ул. Рахова, 53</option>
                <option value="ул. Астраханская, 15/8">ул. Астраханская, 15/8</option>
                <option value="ул. Московская, 11">ул. Московская, 11</option>
              </select>
              {formData.address && (
                <div className="auto-address-indicator">
                  ✓ Адрес автоматически выбран из карточки клуба
                </div>
              )}
            </div>
            
            <div className="booking-actions">
              <button 
                type="submit" 
                className="btn primary"
                disabled={!selectedPlace || !formData.date || !formData.timeFrom || !formData.timeTo}
              >
                Забронировать место {selectedPlace || ''}
              </button>
              <a href="#cafe" className="btn secondary">Добавить из кафе</a>
              {getTotalItems() > 0 && (
                <button 
                  type="button" 
                  className="btn secondary" 
                  onClick={handleClearCart}
                >
                  Очистить корзину
                </button>
              )}
            </div>
          </form>
        </div>
      </div>
    </section>
  );
};

export default CombinedLayoutBooking;
