import React, { useState, useEffect } from 'react';
import { apiService } from '../services/Api';
import { useAuth } from '../context/AuthContext'; // â† Ð´Ð¾Ð±Ð°Ð²Ð»ÐµÐ½Ð¾
import '../styles/MyBookings.css';

const MyBookings = () => {
  const { user } = useAuth(); // â† Ð¿Ð¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ñ‚ÐµÐºÑƒÑ‰ÐµÐ³Ð¾ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
  const [bookings, setBookings] = useState([]);
  const [payments, setPayments] = useState([]);
  const [clubs, setClubs] = useState([]);
  const [computers, setComputers] = useState([]);
  const [foods, setFoods] = useState([]);
  const [additionalMenu, setAdditionalMenu] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filterStatus, setFilterStatus] = useState('all');
  const [deletingBookingId, setDeletingBookingId] = useState(null);
  const [expandedBookings, setExpandedBookings] = useState(new Set());

  useEffect(() => {
    if (user) {
      loadUserData();
    }
  }, [user]);

  const loadUserData = async () => {
    try {
      setLoading(true);
      
      // ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ Ð¢ÐžÐ›Ð¬ÐšÐž Ñ‚ÐµÐºÑƒÑ‰ÐµÐ³Ð¾ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
      const userId = user.id;
      const [userBookings, userPayments, clubsData, computersData, foodsData] = await Promise.all([
        apiService.getUserBookings(userId).catch(err => {
          console.error('âŒ Error loading bookings:', err);
          return [];
        }),
        apiService.getUserPayments(userId).catch(err => {
          console.error('âŒ Error loading payments:', err);
          return [];
        }),
        apiService.getClubs().catch(err => {
          console.error('âŒ Error loading clubs:', err);
          return [];
        }),
        apiService.getComputers().catch(err => {
          console.error('âŒ Error loading computers:', err);
          return [];
        }),
        apiService.getFoods().catch(err => {
          console.error('âŒ Error loading foods:', err);
          return [];
        })
      ]);
      
      let additionalMenuData = [];
      try {
        // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð²ÑÐµ Ð·Ð°ÐºÐ°Ð·Ñ‹ ÐµÐ´Ñ‹, Ð¿Ð¾Ñ‚Ð¾Ð¼ Ð¾Ñ‚Ñ„Ð¸Ð»ÑŒÑ‚Ñ€ÑƒÐµÐ¼ Ð¿Ð¾ booking_id
        const allMenu = await apiService.getAdditionalMenu().catch(() => []);
        // Ð¤Ð¸Ð»ÑŒÑ‚Ñ€ÑƒÐµÐ¼ Ñ‚Ð¾Ð»ÑŒÐºÐ¾ Ñ‚Ðµ, Ñ‡Ñ‚Ð¾ Ð¾Ñ‚Ð½Ð¾ÑÑÑ‚ÑÑ Ðº Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸ÑÐ¼ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
        const bookingIds = userBookings.map(b => b.id);
        additionalMenuData = allMenu.filter(item => 
          bookingIds.includes(item.booking_id)
        );
      } catch (menuError) {
        console.log('âš ï¸ Cannot load additional menu');
      }
      
      setBookings(userBookings);
      setPayments(userPayments);
      setClubs(clubsData);
      setComputers(computersData);
      setFoods(foodsData);
      setAdditionalMenu(additionalMenuData);
      
    } catch (err) {
      console.error('âŒ Error loading user data:', err);
      setError('ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ Ð·Ð°Ð³Ñ€ÑƒÐ·Ð¸Ñ‚ÑŒ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¾ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸ÑÑ…');
    } finally {
      setLoading(false);
    }
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ð¸ Ð¾ ÐºÐ»ÑƒÐ±Ðµ
  const getClubInfo = (clubId) => {
    const club = clubs.find(c => c.id === clubId);
    return club || { address: 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½', name: 'ÐÐµÐ¸Ð·Ð²ÐµÑÑ‚Ð½Ñ‹Ð¹ ÐºÐ»ÑƒÐ±' };
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ð¸ Ð¾ ÐºÐ¾Ð¼Ð¿ÑŒÑŽÑ‚ÐµÑ€Ðµ
  const getComputerInfo = (computerId) => {
    if (!computerId) return null;
    
    const computer = computers.find(c => c.id == computerId);
    if (!computer) {
      return null;
    }

    return {
      id: computer.id,
      name: computer.name || `ÐšÐ¾Ð¼Ð¿ÑŒÑŽÑ‚ÐµÑ€ ${computer.id}`,
      position: computer.position_id ? `ÐœÐµÑÑ‚Ð¾ ${computer.position_id}` : 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½Ð¾'
    };
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð»Ð¾ÐºÐ°Ð»ÑŒÐ½Ð¾ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð½Ñ‹Ñ… Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð¾ ÐµÐ´Ðµ
  const getLocalFoodData = (bookingId) => {
    try {
      const localFoodKey = `local_food_${bookingId}`;
      const foodOrderKey = `food_order_${bookingId}`;
      
      const localFoodData = localStorage.getItem(localFoodKey);
      const foodOrderData = localStorage.getItem(foodOrderKey);
      
      let localItems = [];
      
      if (localFoodData) {
        const parsed = JSON.parse(localFoodData);
        if (parsed.items && Array.isArray(parsed.items)) {
          localItems = parsed.items.map(item => ({
            ...item,
            isLocal: true,
            quantity: item.count || item.quantity || 1
          }));
        }
      }
      
      if (foodOrderData) {
        const parsed = JSON.parse(foodOrderData);
        if (parsed.items && Array.isArray(parsed.items)) {
          parsed.items.forEach(orderItem => {
            if (!localItems.find(localItem => localItem.food_id === orderItem.food_id)) {
              localItems.push({
                ...orderItem,
                isLocal: true,
                quantity: orderItem.quantity || 1
              });
            }
          });
        }
      }
      
      return localItems;
      
    } catch (error) {
      console.error(`âŒ Error loading local food data for booking ${bookingId}:`, error);
      return [];
    }
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð·Ð°ÐºÐ°Ð·Ð°Ð½Ð½Ñ‹Ñ… Ð±Ð»ÑŽÐ´
  const getBookingFoods = (bookingId) => {
    const apiMenuItems = additionalMenu.filter(item => {
      const itemBookingId = item.booking_id || item.bookingId || item.booking;
      return itemBookingId == bookingId;
    });
    
    const localFoodItems = getLocalFoodData(bookingId);
    
    const allFoodItems = [
      ...apiMenuItems.map(item => {
        const foodId = item.food_id || item.foodId || item.food;
        const food = foods.find(f => f.id == foodId);
        
        return {
          ...item,
          food: food || { 
            name: `ÐŸÑ€Ð¾Ð´ÑƒÐºÑ‚ #${foodId}`, 
            price: item.price || 0,
            id: foodId
          },
          quantity: item.count || item.quantity || 1,
          source: 'api'
        };
      }),
      ...localFoodItems.map(item => {
        const foodId = item.food_id || item.id;
        const food = foods.find(f => f.id == foodId);
        
        return {
          ...item,
          food: food || { 
            name: item.name || `ÐŸÑ€Ð¾Ð´ÑƒÐºÑ‚ #${foodId}`, 
            price: item.price || 0,
            id: foodId
          },
          quantity: item.quantity || item.count || 1,
          source: 'local',
          isLocal: true
        };
      })
    ];
    
    return allFoodItems;
  };

  const getEnhancedBooking = (booking) => {
    const payment = payments.find(p => p.booking_id === booking.id);
    const clubInfo = getClubInfo(booking.club_id);
    const computerInfo = getComputerInfo(booking.computer_id);
    const bookingFoods = getBookingFoods(booking.id);

    return {
      ...booking,
      payment: payment || null,
      clubInfo,
      computerInfo,
      foods: bookingFoods
    };
  };

  const getStatusInfo = (status, paymentStatus) => {
    if (paymentStatus) {
      const paymentStatusMap = {
        'pending': { text: 'ÐžÐ¶Ð¸Ð´Ð°ÐµÑ‚ Ð¾Ð¿Ð»Ð°Ñ‚Ñ‹', color: '#ffc107', icon: 'ðŸ’°' },
        'completed': { text: 'ÐžÐ¿Ð»Ð°Ñ‡ÐµÐ½Ð¾', color: '#28a745', icon: 'ðŸ’³' },
        'failed': { text: 'ÐžÑˆÐ¸Ð±ÐºÐ° Ð¾Ð¿Ð»Ð°Ñ‚Ñ‹', color: '#dc3545', icon: 'âŒ' },
        'refunded': { text: 'Ð’Ð¾Ð·Ð²Ñ€Ð°Ñ‚', color: '#6c757d', icon: 'â†©ï¸' }
      };
      
      return paymentStatusMap[paymentStatus] || paymentStatusMap.pending;
    }

    const statusMap = {
      'draft': { text: 'Ð§ÐµÑ€Ð½Ð¾Ð²Ð¸Ðº', color: '#6c757d', icon: 'ðŸ“' },
      'pending': { text: 'ÐžÐ¶Ð¸Ð´Ð°ÐµÑ‚ Ð¿Ð¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð¸Ñ', color: '#17a2b8', icon: 'â³' },
      'confirmed': { text: 'ÐŸÐ¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð¾', color: '#28a745', icon: 'âœ…' },
      'active': { text: 'ÐÐºÑ‚Ð¸Ð²Ð½Ð¾', color: '#28a745', icon: 'ðŸŸ¢' },
      'completed': { text: 'Ð—Ð°Ð²ÐµÑ€ÑˆÐµÐ½Ð¾', color: '#6c757d', icon: 'ðŸ' },
      'cancelled': { text: 'ÐžÑ‚Ð¼ÐµÐ½ÐµÐ½Ð¾', color: '#dc3545', icon: 'âŒ' }
    };
    
    return statusMap[status] || { text: status, color: '#6c757d', icon: 'â“' };
  };

  const formatDateTime = (dateTimeString) => {
    if (!dateTimeString) return 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½Ð¾';
    
    try {
      const date = new Date(dateTimeString);
      return date.toLocaleString('ru-RU', {
        day: 'numeric',
        month: 'long',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (e) {
      return dateTimeString;
    }
  };

  const formatDateShort = (dateTimeString) => {
    if (!dateTimeString) return '';
    
    try {
      const date = new Date(dateTimeString);
      return date.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'short'
      });
    } catch (e) {
      return '';
    }
  };

  const formatTime = (dateTimeString) => {
    if (!dateTimeString) return '';
    
    try {
      const date = new Date(dateTimeString);
      return date.toLocaleTimeString('ru-RU', {
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (e) {
      return '';
    }
  };

  const formatPrice = (price) => {
    if (!price) return '0 â‚½';
    const numericPrice = typeof price === 'string' ? parseFloat(price) : price;
    return new Intl.NumberFormat('ru-RU').format(numericPrice) + ' â‚½';
  };

  const formatTimeDuration = (minutes) => {
    if (!minutes) return 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½Ð¾';
    const numericMinutes = typeof minutes === 'string' ? parseInt(minutes) : minutes;
    const hours = Math.floor(numericMinutes / 60);
    const mins = numericMinutes % 60;
    
    if (hours === 0) return `${mins} Ð¼Ð¸Ð½`;
    if (mins === 0) return `${hours} Ñ‡`;
    return `${hours} Ñ‡ ${mins} Ð¼Ð¸Ð½`;
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð°Ñ†Ð¸Ð¸ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¹ Ð¿Ð¾ ÑÑ‚Ð°Ñ‚ÑƒÑÑƒ
  const getFilteredBookings = () => {
    if (filterStatus === 'all') {
      return bookings;
    }
    return bookings.filter(booking => booking.status === filterStatus);
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ð¸ Ð´Ð»Ñ ÑƒÐ¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð¸Ñ Ñ€Ð°Ð·Ð²ÐµÑ€Ñ‚Ñ‹Ð²Ð°Ð½Ð¸ÐµÐ¼/ÑÐ²ÐµÑ€Ñ‚Ñ‹Ð²Ð°Ð½Ð¸ÐµÐ¼
  const toggleBookingExpansion = (bookingId) => {
    const newExpanded = new Set(expandedBookings);
    if (newExpanded.has(bookingId)) {
      newExpanded.delete(bookingId);
    } else {
      newExpanded.add(bookingId);
    }
    setExpandedBookings(newExpanded);
  };

  const expandAllBookings = () => {
    const allIds = new Set(filteredBookings.map(booking => booking.id));
    setExpandedBookings(allIds);
  };

  const collapseAllBookings = () => {
    setExpandedBookings(new Set());
  };

  const handlePayNow = async (paymentId) => {
    try {
      alert(`Ð˜Ð½Ð¸Ñ†Ð¸Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð° Ð¾Ð¿Ð»Ð°Ñ‚Ð° Ð¿Ð»Ð°Ñ‚ÐµÐ¶Ð° #${paymentId}`);
    } catch (err) {
      console.error('Error initiating payment:', err);
      alert('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¸Ð½Ð¸Ñ†Ð¸Ð°Ñ†Ð¸Ð¸ Ð¾Ð¿Ð»Ð°Ñ‚Ñ‹');
    }
  };

  const handleCancelBooking = async (bookingId) => {
    if (window.confirm('Ð’Ñ‹ ÑƒÐ²ÐµÑ€ÐµÐ½Ñ‹, Ñ‡Ñ‚Ð¾ Ñ…Ð¾Ñ‚Ð¸Ñ‚Ðµ Ð¾Ñ‚Ð¼ÐµÐ½Ð¸Ñ‚ÑŒ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ?')) {
      try {
        const updatedBookings = bookings.map(booking => 
          booking.id === bookingId 
            ? { ...booking, status: 'cancelled' }
            : booking
        );
        
        setBookings(updatedBookings);
        alert('Ð‘Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ Ð¾Ñ‚Ð¼ÐµÐ½ÐµÐ½Ð¾');
      } catch (err) {
        console.error('Error cancelling booking:', err);
        alert('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¾Ñ‚Ð¼ÐµÐ½Ðµ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ');
      }
    }
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ ÑƒÐ´Ð°Ð»ÐµÐ½Ð¸Ñ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ
  const handleDeleteBooking = async (bookingId) => {
    if (!window.confirm('Ð’Ñ‹ ÑƒÐ²ÐµÑ€ÐµÐ½Ñ‹, Ñ‡Ñ‚Ð¾ Ñ…Ð¾Ñ‚Ð¸Ñ‚Ðµ ÑƒÐ´Ð°Ð»Ð¸Ñ‚ÑŒ ÑÑ‚Ð¾ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ? Ð­Ñ‚Ð¾ Ð´ÐµÐ¹ÑÑ‚Ð²Ð¸Ðµ Ð½ÐµÐ»ÑŒÐ·Ñ Ð¾Ñ‚Ð¼ÐµÐ½Ð¸Ñ‚ÑŒ.')) {
      return;
    }

    try {
      setDeletingBookingId(bookingId);
      
      try {
        await apiService.deleteBooking(bookingId);
      } catch (apiError) {
        console.log(`âš ï¸ API deletion failed for booking ${bookingId}, using local deletion`);
      }
      
      const updatedBookings = bookings.filter(booking => booking.id !== bookingId);
      setBookings(updatedBookings);
      
      localStorage.removeItem(`local_food_${bookingId}`);
      localStorage.removeItem(`food_order_${bookingId}`);
      
      alert('Ð‘Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ ÑƒÐ´Ð°Ð»ÐµÐ½Ð¾');
      
    } catch (err) {
      console.error('Error deleting booking:', err);
      alert('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ ÑƒÐ´Ð°Ð»ÐµÐ½Ð¸Ð¸ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ');
    } finally {
      setDeletingBookingId(null);
    }
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ ÑÐ¸Ð½Ñ…Ñ€Ð¾Ð½Ð¸Ð·Ð°Ñ†Ð¸Ð¸ Ð»Ð¾ÐºÐ°Ð»ÑŒÐ½Ñ‹Ñ… Ð´Ð°Ð½Ð½Ñ‹Ñ… Ñ ÑÐµÑ€Ð²ÐµÑ€Ð¾Ð¼
  const handleSyncLocalFood = async (bookingId) => {
    try {
      const localFoodItems = getLocalFoodData(bookingId);
      if (localFoodItems.length === 0) {
        alert('ÐÐµÑ‚ Ð»Ð¾ÐºÐ°Ð»ÑŒÐ½Ñ‹Ñ… Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð´Ð»Ñ ÑÐ¸Ð½Ñ…Ñ€Ð¾Ð½Ð¸Ð·Ð°Ñ†Ð¸Ð¸');
        return;
      }

      let successCount = 0;
      let failedCount = 0;

      for (const item of localFoodItems) {
        try {
          await apiService.addFoodToBooking(bookingId, {
            food_id: item.food_id || item.id,
            count: item.quantity || item.count
          });
          successCount++;
        } catch (error) {
          console.error(`âŒ Failed to sync item ${item.food_id}:`, error);
          failedCount++;
        }
      }

      if (failedCount === 0) {
        localStorage.removeItem(`local_food_${bookingId}`);
        localStorage.removeItem(`food_order_${bookingId}`);
        alert(`âœ… Ð’ÑÐµ ${successCount} Ð¿Ð¾Ð·Ð¸Ñ†Ð¸Ð¹ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ ÑÐ¸Ð½Ñ…Ñ€Ð¾Ð½Ð¸Ð·Ð¸Ñ€Ð¾Ð²Ð°Ð½Ñ‹ Ñ ÑÐµÑ€Ð²ÐµÑ€Ð¾Ð¼!`);
        loadUserData();
      } else {
        alert(`âš ï¸ Ð¡Ð¸Ð½Ñ…Ñ€Ð¾Ð½Ð¸Ð·Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¾ ${successCount} Ð¸Ð· ${localFoodItems.length} Ð¿Ð¾Ð·Ð¸Ñ†Ð¸Ð¹. ÐÐµÑƒÐ´Ð°Ñ‡Ð½Ñ‹Ðµ: ${failedCount}`);
      }

    } catch (error) {
      console.error('Error syncing local food data:', error);
      alert('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ ÑÐ¸Ð½Ñ…Ñ€Ð¾Ð½Ð¸Ð·Ð°Ñ†Ð¸Ð¸ Ð´Ð°Ð½Ð½Ñ‹Ñ…');
    }
  };

  const handleBackToHome = () => {
  localStorage.removeItem('bookingStarted');
  window.location.href = '/';
};

  const handleRefresh = () => {
    loadUserData();
  };

  // Ð¡Ñ‚Ð°Ñ‚ÑƒÑÑ‹ Ð´Ð»Ñ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð°
  const statusFilters = [
    { value: 'all', label: 'Ð’ÑÐµ Ð±Ñ€Ð¾Ð½Ð¸', count: bookings.length },
    { value: 'pending', label: 'ÐžÐ¶Ð¸Ð´Ð°ÑŽÑ‚ Ð¿Ð¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð¸Ñ', count: bookings.filter(b => b.status === 'pending').length },
    { value: 'confirmed', label: 'ÐŸÐ¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð½Ñ‹Ðµ', count: bookings.filter(b => b.status === 'confirmed').length },
    { value: 'active', label: 'ÐÐºÑ‚Ð¸Ð²Ð½Ñ‹Ðµ', count: bookings.filter(b => b.status === 'active').length },
    { value: 'completed', label: 'Ð—Ð°Ð²ÐµÑ€ÑˆÐµÐ½Ð½Ñ‹Ðµ', count: bookings.filter(b => b.status === 'completed').length },
    { value: 'cancelled', label: 'ÐžÑ‚Ð¼ÐµÐ½ÐµÐ½Ð½Ñ‹Ðµ', count: bookings.filter(b => b.status === 'cancelled').length }
  ];

  if (loading) {
    return (
      <div className="my-bookings-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ð²Ð°ÑˆÐ¸Ñ… Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¹...
        </div>
      </div>
    );
  }

  const filteredBookings = getFilteredBookings();
  const enhancedBookings = filteredBookings.map(booking => getEnhancedBooking(booking));

  return (
