// PlaceDetails.js - –ò–°–ü–†–ê–í–õ–ï–ù–ù–ê–Ø –í–ï–†–°–ò–Ø
import React, { useState, useEffect } from 'react';
import '../styles/PlaceDetails.css';

const PlaceDetails = ({ place, onBack, onSelect, positionInfo }) => {
  const [computerDetails, setComputerDetails] = useState(null);
  const [loading, setLoading] = useState(false);

  // –ò—Å–ø–æ–ª—å–∑—É–µ–º –ø–µ—Ä–µ–¥–∞–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –≤–º–µ—Å—Ç–æ –Ω–æ–≤—ã—Ö –∑–∞–ø—Ä–æ—Å–æ–≤
  useEffect(() => {
    if (positionInfo && positionInfo.computer) {
      console.log('üíª Using preloaded computer data:', positionInfo.computer);
      setComputerDetails(positionInfo.computer);
      
      // –ï—Å–ª–∏ –Ω—É–∂–Ω–æ –±–æ–ª—å—à–µ –¥–µ—Ç–∞–ª–µ–π, –∏—Å–ø–æ–ª—å–∑—É–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ –¥–∞–Ω–Ω—ã–µ
      const computerWithDetails = {
        ...positionInfo.computer,
        position: positionInfo.position,
        // –î–æ–±–∞–≤–ª—è–µ–º —Ä–∞—Å—á–µ—Ç —Å—Ç–æ–∏–º–æ—Å—Ç–∏
        hourlyRate: calculateHourlyRate(positionInfo.computer.price, positionInfo.position.coefficient)
      };
      
      setComputerDetails(computerWithDetails);
    }
  }, [positionInfo]);

  const calculateHourlyRate = (price, coefficient = 1) => {
    const basePrice = parseFloat(price) || 100;
    const calculatedCoefficient = parseFloat(coefficient) || 1;
    return Math.round(basePrice * calculatedCoefficient);
  };

  const calculatedRate = computerDetails?.hourlyRate || 100;

  if (!positionInfo) {
    return (
      <div className="place-details-overlay">
        <div className="place-details">
          <h2>–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö</h2>
          <p>–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ –º–µ—Å—Ç–µ.</p>
          <button onClick={onBack} className="btn secondary">–ù–∞–∑–∞–¥</button>
        </div>
      </div>
    );
  }

  return (
    <div className="place-details-overlay">
      <div className="place-details">
        <div className="place-details-header">
          <h2>–ú–µ—Å—Ç–æ ‚Ññ{place}</h2>
          <button onClick={onBack} className="close-btn">√ó</button>
        </div>

        <div className="place-info">
          <div className="info-section">
            <h3>–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–æ–∑–∏—Ü–∏–∏</h3>
            <div className="info-grid">
              <div className="info-item">
                <span className="label">–ö–æ–º–Ω–∞—Ç–∞:</span>
                <span className="value">{positionInfo.room}</span>
              </div>
              <div className="info-item">
                <span className="label">–ù–æ–º–µ—Ä –º–µ—Å—Ç–∞:</span>
                <span className="value">{positionInfo.number}</span>
              </div>
              <div className="info-item">
                <span className="label">–ö–æ—ç—Ñ—Ñ–∏—Ü–∏–µ–Ω—Ç:</span>
                <span className="value">{positionInfo.position.coefficient || 1}</span>
              </div>
            </div>
          </div>

          {computerDetails && (
            <div className="info-section">
              <h3>–•–∞—Ä–∞–∫—Ç–µ—Ä–∏—Å—Ç–∏–∫–∏ –∫–æ–º–ø—å—é—Ç–µ—Ä–∞</h3>
              <div className="info-grid">
                <div className="info-item">
                  <span className="label">–ë–∞–∑–æ–≤–∞—è —Ü–µ–Ω–∞:</span>
                  <span className="value">{computerDetails.price} ‚ÇΩ/—á–∞—Å</span>
                </div>
                <div className="info-item">
                  <span className="label">–ò—Ç–æ–≥–æ–≤–∞—è —Ü–µ–Ω–∞:</span>
                  <span className="value">{calculatedRate} ‚ÇΩ/—á–∞—Å</span>
                </div>
                <div className="info-item">
                  <span className="label">Computer ID:</span>
                  <span className="value">{computerDetails.id}</span>
                </div>
                <div className="info-item">
                  <span className="label">Position ID:</span>
                  <span className="value">{computerDetails.position_id}</span>
                </div>
              </div>
            </div>
          )}
        </div>

        <div className="place-actions">
          <button 
            onClick={() => onSelect(place, calculatedRate)}
            className="btn primary"
          >
            –í—ã–±—Ä–∞—Ç—å —ç—Ç–æ –º–µ—Å—Ç–æ –∑–∞ {calculatedRate} ‚ÇΩ/—á–∞—Å
          </button>
          <button onClick={onBack} className="btn secondary">
            –ù–∞–∑–∞–¥ –∫ –≤—ã–±–æ—Ä—É
          </button>
        </div>
      </div>
    </div>
  );
};

export default PlaceDetails;
