// components/AdminPanel.js
import React, { useState, useEffect } from 'react';
import { apiService } from '../services/Api';
import '../styles/AdminPanel.css';

const AdminPanel = () => {
  const [activeTab, setActiveTab] = useState('foods');
  const [foods, setFoods] = useState([]);
  const [computers, setComputers] = useState([]);
  const [clubs, setClubs] = useState([]);
  const [specs, setSpecs] = useState([]);
  const [positions, setPositions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [formErrors, setFormErrors] = useState({});

  // Формы для добавления данных
  const [foodForm, setFoodForm] = useState({
    name: '',
    type: 'food',
    price: '',
    count: '',
    path_to_img: '/images/default-food.jpg' 
  });

  const [computerForm, setComputerForm] = useState({
    price: '',
    spec_id: '',
    position_id: ''
  });

  const [specForm, setSpecForm] = useState({
    ram: '',
    processor: '',
    gpu: '',
    monitor: '',
    headphones: '',
    mouse: '',
    keyboard: ''
  });

  const [positionForm, setPositionForm] = useState({
    room: '',
    number: '',
    coefficient: '1.0',
    club_id: ''
  });

  // Загрузка данных при монтировании
  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    setLoading(true);
    setError(null);
    try {
      console.log('Starting to load data...');
      
      // Загружаем данные параллельно для скорости
      const [foodsData, computersData, clubsData, specsData, positionsData] = await Promise.all([
        apiService.getFoods().catch(e => { console.warn('Foods load failed:', e); return []; }),
        
        apiService.getComputers().catch(e => { console.warn('Computers load failed:', e); return []; }),
        apiService.getClubs().catch(e => { console.warn('Clubs load failed:', e); return []; }),
        apiService.getComputerSpecs().catch(e => { console.warn('Specs load failed:', e); return []; }),
        apiService.getComputerPositions().catch(e => { console.warn('Positions load failed:', e); return []; })
      ]);

      console.log('All data loaded:', { foodsData, computersData, clubsData, specsData, positionsData });
      
      setFoods(foodsData);
      setComputers(computersData);
      setClubs(clubsData);
      setSpecs(specsData);
      setPositions(positionsData);
      
    } catch (error) {
      console.error('Error loading data:', error);
      setError(`Ошибка загрузки данных: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Обработчики для форм с улучшенной валидацией
  const handleFoodSubmit = async (e) => {
    e.preventDefault();
    setFormErrors(prev => ({ ...prev, food: null }));
    
    // Валидация на клиенте
    if (!foodForm.name.trim()) {
      setFormErrors(prev => ({ ...prev, food: 'Название товара обязательно' }));
      return;
    }
    if (!foodForm.price || parseFloat(foodForm.price) <= 0) {
      setFormErrors(prev => ({ ...prev, food: 'Цена должна быть больше 0' }));
      return;
    }
    if (!foodForm.count || parseInt(foodForm.count) < 0) {
      setFormErrors(prev => ({ ...prev, food: 'Количество не может быть отрицательным' }));
      return;
    }

    try {
      const foodData = {
        name: foodForm.name.trim(),
        type: foodForm.type,
        price: parseFloat(foodForm.price),
        count: parseInt(foodForm.count),
        path_to_img: foodForm.path_to_img.trim() || '/images/default-food.jpg' 
      };

      console.log('Sending food data:', foodData);
      console.log('Food type being sent:', foodForm.type);
      console.log('Full payload:', JSON.stringify(foodData, null, 2));
      // ИСПРАВЛЕНО: используем apiService.request вместо несуществующего createFood
      const response = await apiService.request('/foods', {
        method: 'POST',
        body: foodData
      });
      
      setFoods(prev => [...prev, response]);
      setFoodForm({ name: '', type: 'food', price: '', count: '', path_to_img: '/images/default-food.jpg' });
      alert('Еда успешно добавлена!');
    } catch (error) {
      console.error('Error adding food:', error);
      console.error('Error details:', error.details);
      setFormErrors(prev => ({ ...prev, food: `Ошибка при добавлении еды: ${error.message}` }));
    }
  };

  const handleSpecSubmit = async (e) => {
    e.preventDefault();
    setFormErrors(prev => ({ ...prev, spec: null }));

    try {
      const response = await apiService.request('/computer-specs', {
        method: 'POST',
        body: specForm
      });
      
      setSpecs(prev => [...prev, response]);
      setSpecForm({ ram: '', processor: '', gpu: '', monitor: '', headphones: '', mouse: '', keyboard: '' });
      alert('Характеристики успешно добавлены!');
    } catch (error) {
      console.error('Error adding spec:', error);
      setFormErrors(prev => ({ ...prev, spec: `Ошибка при добавлении характеристик: ${error.message}` }));
    }
  };
const handlePositionSubmit = async (e) => {
  e.preventDefault();
  setFormErrors(prev => ({ ...prev, position: null }));

  // Валидация на клиенте
  if (!positionForm.room.trim()) {
    setFormErrors(prev => ({ ...prev, position: 'Название комнаты обязательно' }));
    return;
  }
  if (!positionForm.number || parseInt(positionForm.number) <= 0) {
    setFormErrors(prev => ({ ...prev, position: 'Номер места должен быть больше 0' }));
    return;
  }

  try {
    // ПРОБЛЕМА: бэкенд игнорирует coefficient и club_id, но БД требует их
    // ВРЕМЕННОЕ РЕШЕНИЕ: используем значения по умолчанию которые могут быть в БД
    const positionData = {
      room: positionForm.room.trim(),
      number: parseInt(positionForm.number),
      coefficient: parseFloat(positionForm.coefficient) || 1.0, // значение по умолчанию
      club_id: parseInt(positionForm.club_id) || 1 // предположим, что клуб с ID=1 существует
    };

    console.log('Sending position data with defaults:', positionData);
    
    const response = await apiService.request('/computer-positions', {
      method: 'POST',
      body: positionData
    });
    
    // После успешного добавления, перезагрузим данные чтобы получить актуальный список
    await loadData();
    
    setPositionForm({ room: '', number: '', coefficient: '1.0', club_id: '' });
    alert('Позиция успешно добавлена!');
  } catch (error) {
    console.error('Error adding position:', error);
    
    // Если ошибка связана с coefficient/club_id, предложим альтернативное решение
    if (error.message.includes('coefficient') || error.message.includes('club_id')) {
      setFormErrors(prev => ({ 
        ...prev, 
        position: `Ошибка: ${error.message}. Необходимо обновить бэкенд для поддержки этих полей.` 
      }));
    } else {
      setFormErrors(prev => ({ ...prev, position: `Ошибка при добавлении позиции: ${error.message}` }));
    }
  }
};

  const handleComputerSubmit = async (e) => {
    e.preventDefault();
    setFormErrors(prev => ({ ...prev, computer: null }));

    // Валидация на клиенте
    if (!computerForm.price || parseFloat(computerForm.price) <= 0) {
      setFormErrors(prev => ({ ...prev, computer: 'Цена должна быть больше 0' }));
      return;
    }
    if (!computerForm.spec_id) {
      setFormErrors(prev => ({ ...prev, computer: 'Выберите характеристики' }));
      return;
    }
    if (!computerForm.position_id) {
      setFormErrors(prev => ({ ...prev, computer: 'Выберите позицию' }));
      return;
    }

    try {
      const computerData = {
        price: parseFloat(computerForm.price),
        spec_id: parseInt(computerForm.spec_id),
        position_id: parseInt(computerForm.position_id)
      };

      console.log('Sending computer data:', computerData);
      
      const response = await apiService.request('/computers', {
        method: 'POST',
        body: computerData
      });
      
      setComputers(prev => [...prev, response]);
      setComputerForm({ price: '', spec_id: '', position_id: '' });
      alert('Компьютер успешно добавлен!');
    } catch (error) {
      console.error('Error adding computer:', error);
      setFormErrors(prev => ({ ...prev, computer: `Ошибка при добавлении компьютера: ${error.message}` }));
    }
  };

  // Удаление элементов
  const handleDeleteFood = async (id) => {
    if (window.confirm('Вы уверены, что хотите удалить этот товар?')) {
      try {
        await apiService.request(`/foods/${id}`, { method: 'DELETE' });
        setFoods(prev => prev.filter(food => food.id !== id));
        alert('Товар удален!');
      } catch (error) {
        console.error('Error deleting food:', error);
        alert(`Ошибка при удалении товара: ${error.message}`);
      }
    }
  };

  const handleDeleteComputer = async (id) => {
    if (window.confirm('Вы уверены, что хотите удалить этот компьютер?')) {
      try {
        await apiService.request(`/computers/${id}`, { method: 'DELETE' });
        setComputers(prev => prev.filter(computer => computer.id !== id));
        alert('Компьютер удален!');
      } catch (error) {
        console.error('Error deleting computer:', error);
        alert(`Ошибка при удалении компьютера: ${error.message}`);
      }
    }
  };

  const handleDeleteSpec = async (id) => {
    if (window.confirm('Вы уверены, что хотите удалить эти характеристики?')) {
      try {
        await apiService.request(`/computer-specs/${id}`, { method: 'DELETE' });
        setSpecs(prev => prev.filter(spec => spec.id !== id));
        alert('Характеристики удалены!');
      } catch (error) {
        console.error('Error deleting spec:', error);
        alert(`Ошибка при удалении характеристик: ${error.message}`);
      }
    }
  };

  const handleDeletePosition = async (id) => {
    if (window.confirm('Вы уверены, что хотите удалить эту позицию?')) {
      try {
        await apiService.request(`/computer-positions/${id}`, { method: 'DELETE' });
        setPositions(prev => prev.filter(position => position.id !== id));
        alert('Позиция удалена!');
      } catch (error) {
        console.error('Error deleting position:', error);
        alert(`Ошибка при удалении позиции: ${error.message}`);
      }
    }
  };

  // Очистка ошибок при смене таба
  useEffect(() => {
    setFormErrors({});
  }, [activeTab]);

  if (loading) {
    return (
      <div className="admin-loading">
        <h2>Панель администратора</h2>
        <div>Загрузка данных...</div>
      </div>
    );
  }

  return (
    <div className="admin-panel">
      <div className="admin-header">
        <h1>Панель администратора</h1>
        <div className="header-actions">
          <button onClick={loadData} className="btn refresh-btn">Обновить данные</button>
          {error && (
            <div className="error-banner">
              {error}
              <button onClick={() => setError(null)} className="close-btn">×</button>
            </div>
          )}
        </div>
      </div>

      <div className="admin-tabs">
        <button 
          className={`tab-btn ${activeTab === 'foods' ? 'active' : ''}`}
          onClick={() => setActiveTab('foods')}
        >
          Управление едой ({foods.length})
        </button>
        <button 
          className={`tab-btn ${activeTab === 'computers' ? 'active' : ''}`}
          onClick={() => setActiveTab('computers')}
        >
          Управление компьютерами ({computers.length})
        </button>
        <button 
          className={`tab-btn ${activeTab === 'specs' ? 'active' : ''}`}
          onClick={() => setActiveTab('specs')}
        >
          Характеристики ({specs.length})
        </button>
        <button 
          className={`tab-btn ${activeTab === 'positions' ? 'active' : ''}`}
          onClick={() => setActiveTab('positions')}
        >
          Позиции ({positions.length})
        </button>
      </div>

      <div className="admin-content">
        {/* Вкладка управления едой */}
        {activeTab === 'foods' && (
          <div className="tab-content">
            <div className="form-section">
              <h3>Добавить новый товар</h3>
              {formErrors.food && (
                <div className="form-error">{formErrors.food}</div>
              )}
              <form onSubmit={handleFoodSubmit} className="admin-form">
                <div className="form-row">
                  <input
                    type="text"
                    placeholder="Название товара *"
                    value={foodForm.name}
                    onChange={(e) => setFoodForm(prev => ({ ...prev, name: e.target.value }))}
                    required
                  />
                  <select
                    value={foodForm.type}
                    onChange={(e) => setFoodForm(prev => ({ ...prev, type: e.target.value }))}
                  >
                    <option value="food">Еда</option>
                    <option value="drink">Напиток</option>
                    <option value="snack">Закуска</option>
                  </select>
                </div>
                <div className="form-row">
                  <input
                    type="number"
                    step="0.01"
                    min="0.01"
                    placeholder="Цена *"
                    value={foodForm.price}
                    onChange={(e) => setFoodForm(prev => ({ ...prev, price: e.target.value }))}
                    required
                  />
                  <input
                    type="number"
                    min="0"
                    placeholder="Количество *"
                    value={foodForm.count}
                    onChange={(e) => setFoodForm(prev => ({ ...prev, count: e.target.value }))}
                    required
                  />
                </div>
                <input
                  type="text"
                  placeholder="Путь к изображению (опционально)"
                  value={foodForm.path_to_img}
                  onChange={(e) => setFoodForm(prev => ({ ...prev, path_to_img: e.target.value }))}
                />
                <button type="submit" className="btn primary">Добавить товар</button>
              </form>
            </div>

            <div className="list-section">
              <h3>Список товаров</h3>
              <div className="items-grid">
                {foods.map(food => (
                  <div key={food.id} className="item-card">
                    <div className="item-info">
                      <h4>{food.name}</h4>
                      <p>Тип: {food.type}</p>
                      <p>Цена: {food.price} ₽</p>
                      <p>В наличии: {food.count} шт.</p>
                      {food.path_to_img && (
                        <p>Изображение: {food.path_to_img}</p>
                      )}
                    </div>
                    <button 
                      onClick={() => handleDeleteFood(food.id)}
                      className="btn danger"
                    >
                      Удалить
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Вкладка управления компьютерами */}
        {activeTab === 'computers' && (
          <div className="tab-content">
            <div className="form-section">
              <h3>Добавить новый компьютер</h3>
              <form onSubmit={handleComputerSubmit} className="admin-form">
                <div className="form-row">
                  <input
                    type="number"
                    step="0.01"
                    placeholder="Цена за час"
                    value={computerForm.price}
                    onChange={(e) => setComputerForm(prev => ({ ...prev, price: e.target.value }))}
                    required
                  />
                  <select
                    value={computerForm.spec_id}
                    onChange={(e) => setComputerForm(prev => ({ ...prev, spec_id: e.target.value }))}
                    required
                  >
                    <option value="">Выберите характеристики</option>
                    {specs.map(spec => (
                      <option key={spec.id} value={spec.id}>
                        {spec.processor} + {spec.gpu}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="form-row">
                  <select
                    value={computerForm.position_id}
                    onChange={(e) => setComputerForm(prev => ({ ...prev, position_id: e.target.value }))}
                    required
                  >
                    <option value="">Выберите позицию</option>
                    {positions.map(position => (
                      <option key={position.id} value={position.id}>
                        Комната {position.room}, место {position.number}
                      </option>
                    ))}
                  </select>
                </div>
                <button type="submit" className="btn primary">Добавить компьютер</button>
              </form>
            </div>

            <div className="list-section">
              <h3>Список компьютеров</h3>
              <div className="items-grid">
                {computers.map(computer => (
                  <div key={computer.id} className="item-card">
                    <div className="item-info">
                      <h4>Компьютер #{computer.id}</h4>
                      <p>Цена: {computer.price} ₽/час</p>
                      <p>ID характеристик: {computer.spec_id}</p>
                      <p>ID позиции: {computer.position_id}</p>
                    </div>
                    <button 
                      onClick={() => handleDeleteComputer(computer.id)}
                      className="btn danger"
                    >
                      Удалить
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Вкладка характеристик компьютеров */}
        {activeTab === 'specs' && (
          <div className="tab-content">
            <div className="form-section">
              <h3>Добавить новые характеристики</h3>
              <form onSubmit={handleSpecSubmit} className="admin-form">
                <div className="form-row">
                  <input
                    type="text"
                    placeholder="Оперативная память"
                    value={specForm.ram}
                    onChange={(e) => setSpecForm(prev => ({ ...prev, ram: e.target.value }))}
                  />
                  <input
                    type="text"
                    placeholder="Процессор"
                    value={specForm.processor}
                    onChange={(e) => setSpecForm(prev => ({ ...prev, processor: e.target.value }))}
                  />
                </div>
                <div className="form-row">
                  <input
                    type="text"
                    placeholder="Видеокарта"
                    value={specForm.gpu}
                    onChange={(e) => setSpecForm(prev => ({ ...prev, gpu: e.target.value }))}
                  />
                  <input
                    type="text"
                    placeholder="Монитор"
                    value={specForm.monitor}
                    onChange={(e) => setSpecForm(prev => ({ ...prev, monitor: e.target.value }))}
                  />
                </div>
                <div className="form-row">
                  <input
                    type="text"
                    placeholder="Наушники"
                    value={specForm.headphones}
                    onChange={(e) => setSpecForm(prev => ({ ...prev, headphones: e.target.value }))}
                  />
                  <input
                    type="text"
                    placeholder="Мышь"
                    value={specForm.mouse}
                    onChange={(e) => setSpecForm(prev => ({ ...prev, mouse: e.target.value }))}
                  />
                </div>
                <input
                  type="text"
                  placeholder="Клавиатура"
                  value={specForm.keyboard}
                  onChange={(e) => setSpecForm(prev => ({ ...prev, keyboard: e.target.value }))}
                />
                <button type="submit" className="btn primary">Добавить характеристики</button>
              </form>
            </div>

            <div className="list-section">
              <h3>Список характеристик</h3>
              <div className="items-grid">
                {specs.map(spec => (
                  <div key={spec.id} className="item-card">
                    <div className="item-info">
                      <h4>Характеристики #{spec.id}</h4>
                      <p>Процессор: {spec.processor}</p>
                      <p>Видеокарта: {spec.gpu}</p>
                      <p>Память: {spec.ram}</p>
                      <p>Монитор: {spec.monitor}</p>
                      <p>Наушники: {spec.headphones}</p>
                      <p>Мышь: {spec.mouse}</p>
                      <p>Клавиатура: {spec.keyboard}</p>
                    </div>
                    <button 
                      onClick={() => handleDeleteSpec(spec.id)}
                      className="btn danger"
                    >
                      Удалить
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Вкладка позиций компьютеров */}
        {activeTab === 'positions' && (
          <div className="tab-content">
            <div className="form-section">
              <h3>Добавить новую позицию</h3>
              <form onSubmit={handlePositionSubmit} className="admin-form">
                <div className="form-row">
                  <input
                    type="text"
                    placeholder="Комната"
                    value={positionForm.room}
                    onChange={(e) => setPositionForm(prev => ({ ...prev, room: e.target.value }))}
                    required
                  />
                  <input
                    type="number"
                    placeholder="Номер места"
                    value={positionForm.number}
                    onChange={(e) => setPositionForm(prev => ({ ...prev, number: e.target.value }))}
                    required
                  />
                </div>
                <div className="form-row">
                  <input
                    type="number"
                    step="0.1"
                    min="0.1"
                    max="5.0"
                    placeholder="Коэффициент цены"
                    value={positionForm.coefficient}
                    onChange={(e) => setPositionForm(prev => ({ ...prev, coefficient: e.target.value }))}
                    required
                  />
                  <select
                    value={positionForm.club_id}
                    onChange={(e) => setPositionForm(prev => ({ ...prev, club_id: e.target.value }))}
                    required
                  >
                    <option value="">Выберите клуб</option>
                    {clubs.map(club => (
                      <option key={club.id} value={club.id}>
                        {club.address}
                      </option>
                    ))}
                  </select>
                </div>
                <button type="submit" className="btn primary">Добавить позицию</button>
              </form>
            </div>

            <div className="list-section">
              <h3>Список позиций</h3>
              <div className="items-grid">
                {positions.map(position => (
                  <div key={position.id} className="item-card">
                    <div className="item-info">
                      <h4>Позиция #{position.id}</h4>
                      <p>Комната: {position.room}</p>
                      <p>Место: {position.number}</p>
                      <p>Коэффициент: {position.coefficient}</p>
                      <p>ID клуба: {position.club_id}</p>
                    </div>
                    <button 
                      onClick={() => handleDeletePosition(position.id)}
                      className="btn danger"
                    >
                      Удалить
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
