// src/components/Rooms.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { apiService } from '../services/Api';
import '../styles/Rooms.css'; // создайте файл стилей

const Rooms = () => {
  const { clubAddress } = useParams();
  const navigate = useNavigate();
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [clubId, setClubId] = useState(null);

  useEffect(() => {
    const loadRooms = async () => {
      try {
        const clubs = await apiService.getClubs();
        const club = clubs.find(c => c.address === clubAddress);
        if (!club) {
          navigate('/clubs');
          return;
        }
        setClubId(club.id);

        const positions = await apiService.getComputerPositions();
        const clubPositions = positions.filter(p => p.club_id == club.id);
        const uniqueRooms = [...new Set(clubPositions.map(p => p.room))];
        setRooms(uniqueRooms.sort());
      } catch (error) {
        console.error('Ошибка загрузки комнат:', error);
        navigate('/clubs');
      } finally {
        setLoading(false);
      }
    };

    loadRooms();
  }, [clubAddress, navigate]);

  const handleRoomSelect = (room) => {
    navigate(`/booking/${encodeURIComponent(clubAddress)}/${encodeURIComponent(room)}`);
  };

  if (loading) {
    return <div className="rooms-section"><h2>Загрузка комнат...</h2></div>;
  }

  return (
    <section className="rooms-section">
      <div className="container">
        <h2 className="section-title">Выберите зал в клубе {clubAddress}</h2>
        <div className="rooms-grid">
          {rooms.map((room, index) => (
            <button
              key={index}
              className="room-card"
              onClick={() => handleRoomSelect(room)}
            >
              <h3>{room}</h3>
            </button>
          ))}
        </div>
        <button 
          className="btn secondary"
          onClick={() => navigate('/clubs')}
        >
          ← Назад к выбору клуба
        </button>
      </div>
    </section>
  );
};

export default Rooms;
