import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import '../styles/CombinedLayout.css';

const CombinedLayoutBooking = () => {
  const { 
    cartItems, 
    updateCartItemQuantity, 
    clearCart, 
    getTotalPrice, 
    getTotalItems,
    getCartSummary 
  } = useCart();
  
  const [formData, setFormData] = useState({
    place: '',
    dateFrom: '',
    dateTo: '',
    timeFrom: '',
    timeTo: '',
    address: '',
    cart: ''
  });

  const [selectedPlace, setSelectedPlace] = useState(null);
  const [showDateTimePicker, setShowDateTimePicker] = useState(false);
  const [currentField, setCurrentField] = useState(null); // 'from' или 'to'
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState('12:00');

  // Часы и минуты для выбора времени
  const hours = Array.from({ length: 24 }, (_, i) => i.toString().padStart(2, '0'));
  const minutes = Array.from({ length: 60 }, (_, i) => i.toString().padStart(2, '0'));

  // Генерация дат для календаря (текущий месяц + следующий)
  const generateCalendarDates = () => {
    const dates = [];
    const today = new Date();
    const currentMonth = today.getMonth();
    const currentYear = today.getFullYear();
    
    // Текущий месяц
    const daysInCurrentMonth = new Date(currentYear, currentMonth + 1, 0).getDate();
    for (let day = today.getDate(); day <= daysInCurrentMonth; day++) {
      const date = new Date(currentYear, currentMonth, day);
      dates.push(date);
    }
    
    // Следующий месяц
    const nextMonth = currentMonth + 1;
    const nextYear = nextMonth > 11 ? currentYear + 1 : currentYear;
    const daysInNextMonth = new Date(nextYear, nextMonth + 1, 0).getDate();
    const daysToShow = Math.min(30, daysInNextMonth); // Показываем 30 дней следующего месяца
    
    for (let day = 1; day <= daysToShow; day++) {
      const date = new Date(nextYear, nextMonth, day);
      dates.push(date);
    }
    
    return dates;
  };

  const calendarDates = generateCalendarDates();

  // Автоматически обновляем поле корзины при изменении cartItems
  useEffect(() => {
    setFormData(prev => ({
      ...prev,
      cart: getCartSummary()
    }));
  }, [cartItems, getCartSummary]);

  // Функция для установки адреса
  const setAddressFromStorage = () => {
    const savedAddress = localStorage.getItem('selectedClubAddress');
    if (savedAddress) {
      setFormData(prev => ({
        ...prev,
        address: savedAddress
      }));
    }
  };

  // Загружаем адрес при монтировании компонента
  useEffect(() => {
    setAddressFromStorage();
  }, []);

  // Слушаем событие выбора адреса
  useEffect(() => {
    const handleAddressSelected = () => {
      setAddressFromStorage();
    };

    window.addEventListener('clubAddressSelected', handleAddressSelected);
    
    return () => {
      window.removeEventListener('clubAddressSelected', handleAddressSelected);
    };
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    if (name === 'address') {
      localStorage.removeItem('selectedClubAddress');
    }
  };

  const handlePlaceSelect = (placeNumber) => {
    setSelectedPlace(placeNumber);
    setFormData(prev => ({
      ...prev,
      place: placeNumber.toString()
    }));
  };

  // Обработчик клика по полю даты и времени
  const handleDateTimeFieldClick = (field) => {
    setCurrentField(field);
    setShowDateTimePicker(true);
    
    // Если уже есть значение, устанавливаем его
    if (field === 'from' && formData.dateFrom) {
      setSelectedDate(new Date(formData.dateFrom));
      setSelectedTime(formData.timeFrom || '12:00');
    } else if (field === 'to' && formData.dateTo) {
      setSelectedDate(new Date(formData.dateTo));
      setSelectedTime(formData.timeTo || '13:00');
    } else {
      setSelectedDate(new Date());
      setSelectedTime('12:00');
    }
  };

  // Выбор даты
  const handleDateSelect = (date) => {
    setSelectedDate(date);
  };

  // Выбор времени
  const handleTimeSelect = (e) => {
    setSelectedTime(e.target.value);
  };

  // Подтверждение выбора
  const handleDateTimeConfirm = () => {
    if (selectedDate && selectedTime) {
      const dateString = selectedDate.toISOString().split('T')[0];
      
      if (currentField === 'from') {
        setFormData(prev => ({
          ...prev,
          dateFrom: dateString,
          timeFrom: selectedTime
        }));
      } else {
        setFormData(prev => ({
          ...prev,
          dateTo: dateString,
          timeTo: selectedTime
        }));
      }
      
      setShowDateTimePicker(false);
      setCurrentField(null);
    }
  };

  // Отмена выбора
  const handleDateTimeCancel = () => {
    setShowDateTimePicker(false);
    setCurrentField(null);
    setSelectedDate(null);
    setSelectedTime('12:00');
  };

  // Форматирование даты для отображения
  const formatDateDisplay = (dateString) => {
    if (!dateString) return '';
    
    const date = new Date(dateString);
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);
    
    if (date.toDateString() === today.toDateString()) {
      return 'Сегодня';
    } else if (date.toDateString() === tomorrow.toDateString()) {
      return 'Завтра';
    } else {
      return date.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'long',
        weekday: 'short'
      });
    }
  };

  // Форматирование даты для календаря
  const formatCalendarDate = (date) => {
    return date.toLocaleDateString('ru-RU', {
      day: 'numeric',
      month: 'short'
    });
  };

  // Получение названия дня недели
  const getDayName = (date) => {
    return date.toLocaleDateString('ru-RU', { weekday: 'short' });
  };

  // Проверка, является ли дата сегодняшней
  const isToday = (date) => {
    const today = new Date();
    return date.toDateString() === today.toDateString();
  };

  // Проверка, является ли дата прошедшей
  const isPastDate = (date) => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    return date < today;
  };

  // Закрытие пикера при клике вне его
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (showDateTimePicker && !e.target.closest('.datetime-picker')) {
        handleDateTimeCancel();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showDateTimePicker]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const bookingData = {
      ...formData,
      selectedPlace,
      cartItems,
      totalPrice: getTotalPrice()
    };
    console.log('Booking data:', bookingData);
    localStorage.removeItem('selectedClubAddress');
  };

  const handleClearCart = () => {
    clearCart();
  };

  return (
    <section id="combined-booking" className="combined-booking-section">
      <div className="background-container">
        <img src="/images/67f504fdfc00ad2f7d384258d27391b08ef7aabd.png" alt="Abstract background" className="bg-image" />
        <div className="bg-overlay"></div>
      </div>
      
      <div className="combined-container">
        {/* Левая часть - схема мест */}
        <div className="layout-side">
          <h2 className="section-title">Выберите место</h2>
          <div className="layout-content-box">
            <div className="layout-scaler">
              <div className="decorative-vectors">
                <img src="/images/30_51.svg" alt="decorative shape" className="decorative-shape shape-1" />
              </div>
              
              {/* Кликабельные места */}
              {[1, 2, 3, 4, 5, 6].map((num) => (
                <div 
                  key={`pc-${num}`} 
                  className={`pc-setup setup-${num} ${selectedPlace === num ? 'selected' : ''}`}
                  onClick={() => handlePlaceSelect(num)}
                >
                  <img src="/images/6356f02b474a41d638cf709af15fe1f7c6dd92c0.png" alt={`PC setup ${num}`} />
                  <span className="layout-number">{num}</span>
                </div>
              ))}
              
              {[8, 9, 12, 13].map((num) => (
                <div 
                  key={`console-${num}`} 
                  className={`console-setup setup-${num} ${selectedPlace === num ? 'selected' : ''}`}
                  onClick={() => handlePlaceSelect(num)}
                >
                  <img src="/images/1b9fb18a794f8543e1b7ff770153e91c8879c831.png" alt={`Console setup ${num}`} />
                  <span className="layout-number">{num}</span>
                </div>
              ))}
              
              {[10, 11, 14].map((num) => (
                <div 
                  key={`headphones-${num}`} 
                  className={`headphones-setup setup-${num} ${selectedPlace === num ? 'selected' : ''}`}
                  onClick={() => handlePlaceSelect(num)}
                >
                  <img src="/images/2fc05fccb4c07d9e1bb638c4487609fd22b2f1ec.png" alt={`Headphones ${num}`} />
                  <span className="layout-number">{num}</span>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Правая часть - форма бронирования */}
        <div className="booking-side">
          <h2 className="section-title">Бронь места {selectedPlace || ''}</h2>
          
          {/* Информация о корзине */}
          {getTotalItems() > 0 && (
            <div className="cart-info">
              <h3>Корзина ({getTotalItems()} товаров)</h3>
              <div className="cart-items">
                {cartItems.map(item => (
                  <div key={item.id} className="cart-item">
                    <span>{item.name} x{item.quantity}</span>
                    <div className="cart-item-controls">
                      <button 
                        onClick={() => updateCartItemQuantity(item.id, -1)}
                        className="cart-btn"
                      >-</button>
                      <span>{item.quantity}</span>
                      <button 
                        onClick={() => updateCartItemQuantity(item.id, 1)}
                        className="cart-btn"
                      >+</button>
                      <span className="cart-item-price">{item.price * item.quantity} ₽</span>
                    </div>
                  </div>
                ))}
              </div>
              <div className="cart-total">
                Итого: {getTotalPrice()} ₽
              </div>
            </div>
          )}

          <form className="booking-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="place">Выбранное место</label>
              <input 
                type="text" 
                id="place" 
                name="place"
                value={formData.place}
                onChange={handleChange}
                placeholder="Нажмите на место слева"
                readOnly
                className={selectedPlace ? 'selected-place' : ''}
              />
            </div>

            <div className="datetime-fields">
              <div className="form-group">
                <label>Дата и время начала</label>
                <div className="datetime-input-wrapper">
                  <input 
                    type="text"
                    value={formData.dateFrom && formData.timeFrom ? 
                      `${formatDateDisplay(formData.dateFrom)} в ${formData.timeFrom}` : 
                      'Выберите дату и время'}
                    placeholder="Дата и время начала"
                    readOnly
                    onClick={() => handleDateTimeFieldClick('from')}
                    className="datetime-input"
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Дата и время окончания</label>
                <div className="datetime-input-wrapper">
                  <input 
                    type="text"
                    value={formData.dateTo && formData.timeTo ? 
                      `${formatDateDisplay(formData.dateTo)} в ${formData.timeTo}` : 
                      'Выберите дату и время'}
                    placeholder="Дата и время окончания"
                    readOnly
                    onClick={() => handleDateTimeFieldClick('to')}
                    className="datetime-input"
                  />
                </div>
              </div>
            </div>
            
            {/* Комбинированный пикер даты и времени */}
            {showDateTimePicker && (
              <div className="datetime-picker-overlay">
                <div className="datetime-picker">
                  <div className="datetime-picker-header">
                    <h3>
                      {currentField === 'from' ? 'Выберите начало брони' : 'Выберите окончание брони'}
                    </h3>
                    <button 
                      type="button" 
                      className="datetime-picker-close"
                      onClick={handleDateTimeCancel}
                    >
                      ×
                    </button>
                  </div>
                  
                  <div className="datetime-picker-body">
                    {/* Календарь */}
                    <div className="calendar-section">
                      <h4>Выберите дату</h4>
                      <div className="dates-scroll">
                        {calendarDates.map((date, index) => (
                          <button
                            key={index}
                            type="button"
                            className={`date-option ${isToday(date) ? 'today' : ''} ${
                              selectedDate && date.toDateString() === selectedDate.toDateString() ? 'selected' : ''
                            } ${isPastDate(date) ? 'past' : ''}`}
                            onClick={() => !isPastDate(date) && handleDateSelect(date)}
                            disabled={isPastDate(date)}
                          >
                            <div className="date-day">{getDayName(date)}</div>
                            <div className="date-number">{formatCalendarDate(date)}</div>
                            {isToday(date) && <div className="today-badge">Сегодня</div>}
                          </button>
                        ))}
                      </div>
                    </div>
                    
                    {/* Выбор времени */}
                    <div className="time-section">
                      <h4>Выберите время</h4>
                      <div className="time-slider-container">
                        <select 
                          value={selectedTime}
                          onChange={handleTimeSelect}
                          className="time-select"
                        >
                          {hours.map(hour => 
                            minutes.map(minute => {
                              const timeValue = `${hour}:${minute}`;
                              return (
                                <option key={timeValue} value={timeValue}>
                                  {timeValue}
                                </option>
                              );
                            })
                          )}
                        </select>
                        <div className="time-slider-labels">
                          <span>00:00</span>
                          <span>12:00</span>
                          <span>23:59</span>
                        </div>
                        <input
                          type="range"
                          min="0"
                          max="1439" // 24 часа * 60 минут - 1
                          value={(() => {
                            const [hours, minutes] = selectedTime.split(':');
                            return parseInt(hours) * 60 + parseInt(minutes);
                          })()}
                          onChange={(e) => {
                            const totalMinutes = parseInt(e.target.value);
                            const hours = Math.floor(totalMinutes / 60).toString().padStart(2, '0');
                            const minutes = (totalMinutes % 60).toString().padStart(2, '0');
                            setSelectedTime(`${hours}:${minutes}`);
                          }}
                          className="time-slider"
                        />
                      </div>
                    </div>
                  </div>
                  
                  <div className="datetime-picker-footer">
                    <div className="selected-datetime-preview">
                      {selectedDate ? (
                        `Выбрано: ${formatDateDisplay(selectedDate.toISOString().split('T')[0])} в ${selectedTime}`
                      ) : (
                        'Выберите дату и время'
                      )}
                    </div>
                    <div className="datetime-picker-actions">
                      <button 
                        type="button" 
                        className="btn secondary"
                        onClick={handleDateTimeCancel}
                      >
                        Отмена
                      </button>
                      <button 
                        type="button" 
                        className="btn primary"
                        onClick={handleDateTimeConfirm}
                        disabled={!selectedDate}
                      >
                        Выбрать
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            )}
            
            <div className="form-group">
              <label htmlFor="address">Адрес клуба</label>
              <select 
                id="address" 
                name="address"
                value={formData.address}
                onChange={handleChange}
                required
              >
                <option value="">Выберите клуб</option>
                <option value="ул. Рахова, 53">ул. Рахова, 53</option>
                <option value="ул. Астраханская, 15/8">ул. Астраханская, 15/8</option>
                <option value="ул. Московская, 11">ул. Московская, 11</option>
              </select>
              {formData.address && (
                <div className="auto-address-indicator">
                  ✓ Адрес автоматически выбран из карточки клуба
                </div>
              )}
            </div>
            
            <div className="booking-actions">
              <button 
                type="submit" 
                className="btn primary"
                disabled={!selectedPlace || !formData.dateFrom || !formData.timeFrom || !formData.dateTo || !formData.timeTo}
              >
                Забронировать место {selectedPlace || ''}
              </button>
              <a href="#cafe" className="btn secondary">Добавить из кафе</a>
              {getTotalItems() > 0 && (
                <button 
                  type="button" 
                  className="btn secondary" 
                  onClick={handleClearCart}
                >
                  Очистить корзину
                </button>
              )}
            </div>
          </form>
        </div>
      </div>
    </section>
  );
};

export default CombinedLayoutBooking;
