import React, { useState, useEffect } from 'react';
import { apiService } from '../services/Api';
import '../styles/BookingConfirmation.css';

const BookingConfirmation = () => {
  const [bookingData, setBookingData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [processingPayment, setProcessingPayment] = useState(false);

  // Загружаем данные из localStorage при монтировании
  useEffect(() => {
    const loadBookingData = () => {
      try {
        const savedData = localStorage.getItem('lastBooking');
        console.log('Loading booking data from localStorage:', savedData);
        
        if (savedData) {
          const parsedData = JSON.parse(savedData);
          setBookingData(parsedData);
        } else {
          console.warn('No booking data found in localStorage');
        }
      } catch (error) {
        console.error('Error loading booking data:', error);
      } finally {
        setLoading(false);
      }
    };

    loadBookingData();
  }, []);

  // Используем данные из состояния
  const data = bookingData || {};

  console.log('=== BOOKING DATA DEBUG ===');
  console.log('Full booking data:', data);
  console.log('=== END DEBUG ===');

  const {
    formData = {},
    selectedPlace,
    placeRate,
    cartItems = [],
    totalPrice = 0,
    id: bookingId,
    calculatedData = {}
  } = data;

  // Деструктурируем formData с значениями по умолчанию
  const {
    place = '',
    dateFrom = '',
    timeFrom = '',
    dateTo = '',
    timeTo = '',
    address = '',
    computer_id = '',
    club_id = ''
  } = formData;

  // Функция для получения информации о месте
  const getPlaceInfo = (placeNumber) => {
    const placeInfo = {
      1: { type: "Gaming PC", rate: 100 },
      2: { type: "Gaming PC", rate: 90 },
      3: { type: "Gaming PC", rate: 95 },
      4: { type: "Gaming PC", rate: 80 },
      5: { type: "Streaming PC", rate: 150 },
      6: { type: "Competitive PC", rate: 120 },
      8: { type: "PlayStation 5", rate: 70 },
      9: { type: "PlayStation 5 Pro", rate: 90 },
      10: { type: "VR Station", rate: 120 },
      11: { type: "Audio Station", rate: 60 },
      12: { type: "PlayStation 4 Pro", rate: 50 },
      13: { type: "Nintendo Switch", rate: 40 },
      14: { type: "Premium Audio", rate: 100 }
    };
    return placeInfo[placeNumber] || { type: "Стандартное место", rate: 80 };
  };

  const placeInfo = getPlaceInfo(selectedPlace);

  // Используем переданную стоимость или получаем из данных места
  const actualPlaceRate = placeRate || placeInfo.rate;

  // Показываем загрузку пока данные не готовы
  if (loading) {
    return (
      <div className="booking-confirmation">
        <div className="container">
          <div className="loading-state">
            <h2>Загрузка данных бронирования...</h2>
          </div>
        </div>
      </div>
    );
  }

  // Если данных нет, показываем сообщение об ошибке
  if (!bookingData || !selectedPlace) {
    return (
      <div className="booking-confirmation">
        <div className="container">
          <div className="no-booking-data">
            <h2>Данные бронирования не найдены</h2>
            <p>Пожалуйста, вернитесь и создайте бронирование заново.</p>
            <button 
              className="btn primary"
              onClick={() => window.location.href = '/booking'}
            >
              Вернуться к бронированию
            </button>
            <button 
              className="btn secondary"
              onClick={() => window.location.href = '/'}
              style={{marginLeft: '10px'}}
            >
              На главную
            </button>
          </div>
        </div>
      </div>
    );
  }

  console.log('=== RATE CALCULATION DEBUG ===');
  console.log('selectedPlace:', selectedPlace);
  console.log('placeRate from booking data:', placeRate);
  console.log('placeInfo.rate for this place:', placeInfo.rate);
  console.log('actualPlaceRate used:', actualPlaceRate);
  console.log('=== END RATE DEBUG ===');

  // Форматирование даты для отображения
  const formatDateDisplay = (dateString) => {
    if (!dateString) return '';
    const [year, month, day] = dateString.split('-').map(Number);
    const date = new Date(year, month - 1, day);
    
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);
    
    if (date.toDateString() === today.toDateString()) {
      return 'Сегодня';
    } else if (date.toDateString() === tomorrow.toDateString()) {
      return 'Завтра';
    } else {
      return date.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'long',
        weekday: 'short'
      });
    }
  };

  // Расчет количества часов
  const getBookingHours = () => {
    if (!dateFrom || !timeFrom || !dateTo || !timeTo) return 0;
    
    try {
      const startDateTime = new Date(`${dateFrom}T${timeFrom}`);
      const endDateTime = new Date(`${dateTo}T${timeTo}`);
      const hoursDiff = (endDateTime - startDateTime) / (1000 * 60 * 60);
      
      return Math.round(hoursDiff * 10) / 10;
    } catch (error) {
      console.error('Error calculating booking hours:', error);
      return 0;
    }
  };

  // Расчет общей стоимости бронирования
  const calculateBookingCost = () => {
    if (calculatedData && calculatedData.totalCost) {
      return calculatedData.totalCost;
    }
    
    if (!dateFrom || !timeFrom || !dateTo || !timeTo) return 0;
    
    try {
      const startDateTime = new Date(`${dateFrom}T${timeFrom}`);
      const endDateTime = new Date(`${dateTo}T${timeTo}`);
      const hours = (endDateTime - startDateTime) / (1000 * 60 * 60);
      
      console.log('=== FINAL RATE CALCULATION ===');
      console.log('Hours:', hours);
      console.log('Place rate:', actualPlaceRate);
      console.log('Cart total:', totalPrice);
      console.log('=== END FINAL CALCULATION ===');

      if (!actualPlaceRate) {
        console.error('ERROR: placeRate is not defined!');
        return totalPrice;
      }
      
      const placeCost = Math.round(hours * actualPlaceRate);
      return placeCost + totalPrice;
    } catch (error) {
      console.error('Error calculating booking cost:', error);
      return totalPrice;
    }
  };

  const bookingHours = getBookingHours();
  const bookingCost = calculateBookingCost();
  const placeCost = calculatedData?.placeCost || Math.round(bookingHours * actualPlaceRate);

  const handleEditBooking = () => {
    window.location.href = '/booking';
  };

  const handleBackToHome = () => {
    window.location.href = '/';
  };

  const handlePayment = async () => {
    try {
      setProcessingPayment(true);
      
      // Сначала создаем бронирование в базе данных
      console.log('🔄 Creating booking in database...');
      
      const bookingMinutes = getBookingHours() * 60;
      const placeCostValue = Math.round((bookingMinutes / 60) * actualPlaceRate);
      const totalCostValue = placeCostValue + totalPrice;

      const bookingDataToSend = {
        computer_id: parseInt(computer_id),
        user_id: 1,
        club_id: parseInt(club_id),
        tariff_id: 1,
        start_time: `${dateFrom} ${timeFrom}:00`,
        end_time: `${dateTo} ${timeTo}:00`,
        minutes: bookingMinutes,
        price_for_pc: placeCostValue,
        price_for_additions: totalPrice,
        total_price: totalCostValue,
        status: 'pending'
      };

      console.log('📤 Sending booking to API:', bookingDataToSend);
      
      const bookingResponse = await apiService.createBooking(bookingDataToSend);
      console.log('✅ Booking created:', bookingResponse);

      // Затем создаем платеж
      console.log('🔄 Creating payment...');
      
      const paymentData = {
        user_id: 1,
        booking_id: bookingResponse.id,
        price: totalCostValue,
        payment_type: 'card',
        status: 'pending',
        payment_hash: `pay_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        payment_date: new Date().toISOString().split('T')[0] // ДОБАВЛЯЕМ обязательное поле
      };

      console.log('📤 Sending payment to API:', paymentData);

      // Отправка запроса на создание платежа
      const paymentResponse = await apiService.createPayment(paymentData);
      
      console.log('✅ Payment created:', paymentResponse);

      // Если есть товары в корзине, добавляем их в additional_menu
      if (cartItems.length > 0) {
        console.log('🔄 Adding food items to booking...');
        for (const item of cartItems) {
          try {
            await apiService.addFoodToBooking(bookingResponse.id, {
              food_id: item.id,
              count: item.quantity
            });
            console.log(`✅ Added ${item.name} to booking`);
          } catch (foodError) {
            console.error('Error adding food to booking:', foodError);
          }
        }
      }

      // Обновляем данные в localStorage с реальным ID брони
      const updatedBookingData = {
        ...bookingData,
        id: bookingResponse.id,
        payment_id: paymentResponse.id,
        status: 'pending_payment'
      };

      localStorage.setItem('lastBooking', JSON.stringify(updatedBookingData));

      // Здесь должна быть интеграция с платежной системой
      // Пока используем заглушку
      alert('Платеж обработан успешно! Бронирование подтверждено.');
      localStorage.removeItem('lastBooking');
      
      // Редирект на главную страницу
      window.location.href = '/';

    } catch (error) {
      console.error('Payment error:', error);
      
      if (error.message.includes('payment_date')) {
        alert('Ошибка при обработке платежа: отсутствует дата платежа. Пожалуйста, попробуйте еще раз.');
      } else if (error.message.includes('tariff_id')) {
        alert('Ошибка при создании бронирования: отсутствует тариф. Пожалуйста, попробуйте еще раз.');
      } else {
        alert('Ошибка при обработке платежа. Пожалуйста, попробуйте еще раз.');
      }
    } finally {
      setProcessingPayment(false);
    }
  };

  // Альтернативная функция оплаты - только создание бронирования
  const handleSimplePayment = async () => {
    try {
      setProcessingPayment(true);
      
      console.log('🔄 Creating booking without payment...');
      
      const bookingMinutes = getBookingHours() * 60;
      const placeCostValue = Math.round((bookingMinutes / 60) * actualPlaceRate);
      const totalCostValue = placeCostValue + totalPrice;

      const bookingDataToSend = {
        computer_id: parseInt(computer_id),
        user_id: 1,
        club_id: parseInt(club_id),
        tariff_id: 1,
        start_time: `${dateFrom} ${timeFrom}:00`,
        end_time: `${dateTo} ${timeTo}:00`,
        minutes: bookingMinutes,
        price_for_pc: placeCostValue,
        price_for_additions: totalPrice,
        total_price: totalCostValue,
        status: 'confirmed' // Сразу подтверждаем без оплаты
      };

      console.log('📤 Sending booking to API:', bookingDataToSend);
      
      const bookingResponse = await apiService.createBooking(bookingDataToSend);
      console.log('✅ Booking created:', bookingResponse);

      // Если есть товары в корзине, добавляем их в additional_menu
      if (cartItems.length > 0) {
        console.log('🔄 Adding food items to booking...');
        for (const item of cartItems) {
          try {
            await apiService.addFoodToBooking(bookingResponse.id, {
              food_id: item.id,
              count: item.quantity
            });
            console.log(`✅ Added ${item.name} to booking`);
          } catch (foodError) {
            console.error('Error adding food to booking:', foodError);
          }
        }
      }

      alert('Бронирование успешно создано! Номер брони: ' + bookingResponse.id);
      localStorage.removeItem('lastBooking');
      
      // Редирект на главную страницу
      window.location.href = '/';

    } catch (error) {
      console.error('Booking creation error:', error);
      alert('Ошибка при создании бронирования. Пожалуйста, попробуйте еще раз.');
    } finally {
      setProcessingPayment(false);
    }
  };

  // Если данных нет или нет стоимости места, показываем сообщение
  if (!selectedPlace || !actualPlaceRate) {
    return (
      <div className="booking-confirmation">
        <div className="container">
          <div className="no-booking-data">
            <h2>Ошибка данных бронирования</h2>
            <p>Не удалось загрузить информацию о стоимости места. Пожалуйста, вернитесь и выберите место заново.</p>
            <button 
              className="btn primary"
              onClick={handleEditBooking}
            >
              Вернуться к бронированию
            </button>
            <button 
              className="btn secondary"
              onClick={handleBackToHome}
              style={{marginLeft: '10px'}}
            >
              На главную
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="booking-confirmation">
      <div className="background-container">
        <img src="/images/67f504fdfc00ad2f7d384258d27391b08ef7aabd.png" alt="Abstract background" className="bg-image" />
        <div className="bg-overlay"></div>
      </div>

      <div className="container">
        <div className="confirmation-header">
          <h1>Подтверждение бронирования</h1>
          <p className="confirmation-subtitle">Проверьте данные перед оплатой</p>
        </div>

        <div className="confirmation-content">
          <div className="booking-info">
            <div className="info-section">
              <h2>Информация о брони</h2>
              
              <div className="info-grid">
                <div className="info-item">
                  <span className="info-label">Место:</span>
                  <span className="info-value">Место №{place} ({placeInfo.type})</span>
                </div>
                
                <div className="info-item">
                  <span className="info-label">Тариф:</span>
                  <span className="info-value">{actualPlaceRate} ₽/час</span>
                </div>
                
                <div className="info-item">
                  <span className="info-label">Клуб:</span>
                  <span className="info-value">{address}</span>
                </div>
                
                <div className="info-item">
                  <span className="info-label">Computer ID:</span>
                  <span className="info-value">{computer_id}</span>
                </div>
                
                <div className="info-item">
                  <span className="info-label">Начало:</span>
                  <span className="info-value">
                    {formatDateDisplay(dateFrom)} {timeFrom}
                  </span>
                </div>
                
                <div className="info-item">
                  <span className="info-label">Окончание:</span>
                  <span className="info-value">
                    {formatDateDisplay(dateTo)} {timeTo}
                  </span>
                </div>
                
                <div className="info-item">
                  <span className="info-label">Продолжительность:</span>
                  <span className="info-value">
                    {bookingHours > 0 ? `${bookingHours} часов` : 'Не указано'}
                  </span>
                </div>
              </div>
            </div>

            {cartItems.length > 0 && (
              <div className="info-section">
                <h2>Заказ из кафе</h2>
                <div className="cart-items-list">
                  {cartItems.map(item => (
                    <div key={item.id} className="cart-item-confirm">
                      <span className="item-name">{item.name}</span>
                      <span className="item-quantity">x{item.quantity}</span>
                      <span className="item-price">{item.price * item.quantity} ₽</span>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          <div className="payment-section">
            <div className="payment-card">
              <h3>Стоимость бронирования</h3>
              
              <div className="cost-breakdown">
                <div className="cost-item">
                  <span>Аренда места ({bookingHours} часов × {actualPlaceRate} ₽/час):</span>
                  <span>{placeCost} ₽</span>
                </div>
                
                {cartItems.length > 0 && (
                  <div className="cost-item">
                    <span>Заказ из кафе:</span>
                    <span>{totalPrice} ₽</span>
                  </div>
                )}
                
                <div className="cost-total">
                  <span>Итого к оплате:</span>
                  <span className="total-amount">{bookingCost} ₽</span>
                </div>
              </div>

              <div className="payment-actions">
                <button 
                  className="btn payment-btn primary"
                  onClick={handlePayment}
                  disabled={processingPayment}
                >
                  {processingPayment ? 'Обработка...' : `Перейти к оплате ${bookingCost} ₽`}
                </button>
                
                <button 
                  className="btn secondary"
                  onClick={handleSimplePayment}
                  disabled={processingPayment}
                  style={{marginTop: '10px'}}
                >
                  {processingPayment ? 'Создание...' : 'Создать бронь без оплаты (тест)'}
                </button>
                
                <button 
                  className="btn secondary"
                  onClick={handleEditBooking}
                  disabled={processingPayment}
                >
                  Изменить бронь
                </button>
                <button 
                  className="btn secondary"
                  onClick={handleBackToHome}
                  disabled={processingPayment}
                >
                  На главную
                </button>
              </div>

              <div className="payment-security">
                <div className="security-info">
                  <span className="security-icon">🔒</span>
                  <span>Безопасная оплата через CloudPayments</span>
                </div>
                <p className="security-note">
                  После оплаты вы получите подтверждение на email и смс
                </p>
              </div>
            </div>

            <div className="support-info">
              <h4>Нужна помощь?</h4>
              <p>Телефон поддержки: +7 (999) 123-45-67</p>
              <p>Email: support@cyberclub.ru</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookingConfirmation;
