import React from 'react';
import Button from './Button';
import '../styles/Clubs.css';

const Clubs = () => {
  const clubsData = [
    {
      title: "Рахова 53",
      info: ["18 ПК с Game Room и VIP-зоной", "Кресла DXRacer"]
    },
    {
      title: "Астраханская 15/8",
      info: ["18 ПК с Game Room и VIP-зоной", "Кресла DXRacer", "Зоны PS5 и PS4 PRO"]
    },
    {
      title: "Московская 11",
      info: ["18 ПК с Game Room и VIP-зоной", "Кресла DXRacer", "Две отдельные зоны Stream Room"]
    }
  ];

  const handleClubSelect = (clubName) => {
    console.log(`Выбран клуб: ${clubName}`);
    // Здесь можно добавить логику выбора клуба
  };

  return (
    <section id="clubs" className="clubs-section">
      <div className="container">
        <h2 className="section-title">Наши клубы</h2>
        <div className="clubs-grid">
          {clubsData.map((club, index) => (
            <div key={index} className="club-card">
              <h3 className="club-card-title">{club.title}</h3>
              <div className="club-card-info">
                {club.info.map((item, itemIndex) => (
                  <p key={itemIndex}>{item}</p>
                ))}
              </div>
              <Button onClick={() => handleClubSelect(club.title)}>
                Выбрать клуб
              </Button>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Clubs;
