import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/BookingConfirmation.css';

const BookingConfirmation = () => {
  const location = useLocation();
  const navigate = useNavigate();
  
  // Получаем данные из состояния навигации или из localStorage
  const bookingData = location.state || JSON.parse(localStorage.getItem('lastBooking')) || {};

  const {
    formData = {},
    selectedPlace,
    cartItems = [],
    totalPrice = 0
  } = bookingData;

  const {
    place = '',
    dateFrom = '',
    timeFrom = '',
    dateTo = '',
    timeTo = '',
    address = ''
  } = formData;

  // Форматирование даты для отображения
  const formatDateDisplay = (dateString) => {
    if (!dateString) return '';
    const [year, month, day] = dateString.split('-').map(Number);
    const date = new Date(year, month - 1, day);
    
    return date.toLocaleDateString('ru-RU', {
      day: 'numeric',
      month: 'long',
      weekday: 'short'
    });
  };

  // Расчет общей стоимости бронирования
  const calculateBookingCost = () => {
    if (!dateFrom || !timeFrom || !dateTo || !timeTo) return 0;
    
    const startDateTime = new Date(`${dateFrom}T${timeFrom}`);
    const endDateTime = new Date(`${dateTo}T${timeTo}`);
    const hours = (endDateTime - startDateTime) / (1000 * 60 * 60);
    
    // Базовая стоимость места (можно добавить разные тарифы для разных мест)
    const placeRate = 100; // рублей в час
    
    return Math.round(hours * placeRate) + totalPrice;
  };

  const bookingCost = calculateBookingCost();

  const handlePayment = () => {
    // Здесь будет интеграция с платежной системой
    console.log('Переход к оплате', bookingCost);
    
    // Временная заглушка
    alert(`Переход к оплате ${bookingCost} руб.`);
    
    // После успешной оплаты можно очистить данные
    localStorage.removeItem('lastBooking');
  };

  const handleEditBooking = () => {
    navigate('/booking');
  };

  // Если данных нет, показываем сообщение
  if (!selectedPlace) {
    return (
      <div className="booking-confirmation">
        <div className="container">
          <div className="no-booking-data">
            <h2>Данные бронирования не найдены</h2>
            <p>Пожалуйста, вернитесь на страницу бронирования и выберите место.</p>
            <button 
              className="btn primary"
              onClick={() => navigate('/booking')}
            >
              Вернуться к бронированию
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="booking-confirmation">
      <div className="background-container">
        <img src="/images/67f504fdfc00ad2f7d384258d27391b08ef7aabd.png" alt="Abstract background" className="bg-image" />
        <div className="bg-overlay"></div>
      </div>

      <div className="container">
        <div className="confirmation-header">
          <h1>Подтверждение бронирования</h1>
          <p className="confirmation-subtitle">Проверьте данные перед оплатой</p>
        </div>

        <div className="confirmation-content">
          {/* Левая колонка - информация о брони */}
          <div className="booking-info">
            <div className="info-section">
              <h2>Информация о брони</h2>
              
              <div className="info-grid">
                <div className="info-item">
                  <span className="info-label">Место:</span>
                  <span className="info-value">Место №{place}</span>
                </div>
                
                <div className="info-item">
                  <span className="info-label">Клуб:</span>
                  <span className="info-value">{address}</span>
                </div>
                
                <div className="info-item">
                  <span className="info-label">Начало:</span>
                  <span className="info-value">
                    {formatDateDisplay(dateFrom)} {timeFrom}
                  </span>
                </div>
                
                <div className="info-item">
                  <span className="info-label">Окончание:</span>
                  <span className="info-value">
                    {formatDateDisplay(dateTo)} {timeTo}
                  </span>
                </div>
                
                <div className="info-item">
                  <span className="info-label">Продолжительность:</span>
                  <span className="info-value">
                    {(() => {
                      if (!dateFrom || !timeFrom || !dateTo || !timeTo) return '';
                      const start = new Date(`${dateFrom}T${timeFrom}`);
                      const end = new Date(`${dateTo}T${timeTo}`);
                      const hours = Math.round((end - start) / (1000 * 60 * 60) * 10) / 10;
                      return `${hours} часов`;
                    })()}
                  </span>
                </div>
              </div>
            </div>

            {/* Информация о заказе из кафе */}
            {cartItems.length > 0 && (
              <div className="info-section">
                <h2>Заказ из кафе</h2>
                <div className="cart-items-list">
                  {cartItems.map(item => (
                    <div key={item.id} className="cart-item-confirm">
                      <span className="item-name">{item.name}</span>
                      <span className="item-quantity">x{item.quantity}</span>
                      <span className="item-price">{item.price * item.quantity} ₽</span>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Правая колонка - итоги и оплата */}
          <div className="payment-section">
            <div className="payment-card">
              <h3>Стоимость бронирования</h3>
              
              <div className="cost-breakdown">
                <div className="cost-item">
                  <span>Аренда места:</span>
                  <span>{bookingCost - totalPrice} ₽</span>
                </div>
                
                {cartItems.length > 0 && (
                  <div className="cost-item">
                    <span>Заказ из кафе:</span>
                    <span>{totalPrice} ₽</span>
                  </div>
                )}
                
                <div className="cost-total">
                  <span>Итого к оплате:</span>
                  <span className="total-amount">{bookingCost} ₽</span>
                </div>
              </div>

              <div className="payment-actions">
                <button 
                  className="btn payment-btn primary"
                  onClick={handlePayment}
                >
                  Перейти к оплате {bookingCost} ₽
                </button>
                
                <button 
                  className="btn secondary"
                  onClick={handleEditBooking}
                >
                  Изменить бронь
                </button>
              </div>

              <div className="payment-security">
                <div className="security-info">
                  <span className="security-icon">🔒</span>
                  <span>Безопасная оплата через CloudPayments</span>
                </div>
                <p className="security-note">
                  После оплаты вы получите подтверждение на email и смс
                </p>
              </div>
            </div>

            <div className="support-info">
              <h4>Нужна помощь?</h4>
              <p>Телефон поддержки: +7 (999) 123-45-67</p>
              <p>Email: support@cyberclub.ru</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookingConfirmation;
