import React, { useState, useEffect } from 'react';
import { apiService } from '../services/Api';
import '../styles/MyBookings.css';

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadUserData();
  }, []);

  const loadUserData = async () => {
    try {
      setLoading(true);
      
      // Загружаем бронирования пользователя через API
      const userBookings = await apiService.getBookings();
      console.log('📋 Bookings loaded:', userBookings);
      
      // Загружаем платежи пользователя через API
      const userPayments = await apiService.getPayments();
      console.log('💰 Payments loaded:', userPayments);
      
      setBookings(userBookings);
      setPayments(userPayments);
      
    } catch (err) {
      console.error('Error loading user data:', err);
      setError('Не удалось загрузить данные о бронированиях');
      
      // Fallback: проверяем localStorage
      const lastBooking = localStorage.getItem('lastBooking');
      if (lastBooking) {
        try {
          const booking = JSON.parse(lastBooking);
          if (booking.status && booking.status !== 'draft') {
            setBookings([booking]);
          }
        } catch (e) {
          console.error('Error parsing localStorage booking:', e);
        }
      }
    } finally {
      setLoading(false);
    }
  };

  // Функция для объединения бронирований и платежей
  const getBookingWithPayment = (booking) => {
    const payment = payments.find(p => p.booking_id === booking.id);
    return {
      ...booking,
      payment: payment || null
    };
  };

  const getStatusInfo = (status, paymentStatus) => {
    // Если есть информация о платеже, используем её для определения статуса
    if (paymentStatus) {
      const paymentStatusMap = {
        'pending': { text: 'Ожидает оплаты', color: '#ffc107', icon: '💰' },
        'completed': { text: 'Оплачено', color: '#28a745', icon: '💳' },
        'failed': { text: 'Ошибка оплаты', color: '#dc3545', icon: '❌' },
        'refunded': { text: 'Возврат', color: '#6c757d', icon: '↩️' }
      };
      
      return paymentStatusMap[paymentStatus] || paymentStatusMap.pending;
    }

    // Статусы бронирований
    const statusMap = {
      'draft': { text: 'Черновик', color: '#6c757d', icon: '📝' },
      'pending': { text: 'Ожидает подтверждения', color: '#17a2b8', icon: '⏳' },
      'confirmed': { text: 'Подтверждено', color: '#28a745', icon: '✅' },
      'active': { text: 'Активно', color: '#28a745', icon: '🟢' },
      'completed': { text: 'Завершено', color: '#6c757d', icon: '🏁' },
      'cancelled': { text: 'Отменено', color: '#dc3545', icon: '❌' }
    };
    
    return statusMap[status] || { text: status, color: '#6c757d', icon: '❓' };
  };

  const formatDateTime = (dateString, timeString) => {
    if (!dateString) return 'Не указано';
    
    try {
      const date = new Date(dateString);
      const formattedDate = date.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'long',
        weekday: 'short'
      });
      
      return timeString ? `${formattedDate} ${timeString}` : formattedDate;
    } catch (e) {
      return dateString;
    }
  };

  const formatPrice = (price) => {
    if (!price) return '0 ₽';
    return new Intl.NumberFormat('ru-RU', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(price) + ' ₽';
  };

  const handlePayNow = async (paymentId) => {
    try {
      // Здесь должна быть логика для инициации оплаты
      alert(`Инициирована оплата платежа #${paymentId}`);
      // В реальном приложении здесь был бы переход на страницу платежной системы
    } catch (err) {
      console.error('Error initiating payment:', err);
      alert('Ошибка при инициации оплаты');
    }
  };

  const handleCancelBooking = async (bookingId) => {
    if (window.confirm('Вы уверены, что хотите отменить бронирование?')) {
      try {
        // В реальном приложении здесь был бы API запрос на отмену брони
        // await apiService.cancelBooking(bookingId);
        
        // Пока просто обновляем локальное состояние
        const updatedBookings = bookings.map(booking => 
          booking.id === bookingId 
            ? { ...booking, status: 'cancelled' }
            : booking
        );
        
        setBookings(updatedBookings);
        alert('Бронирование успешно отменено');
      } catch (err) {
        console.error('Error cancelling booking:', err);
        alert('Ошибка при отмене бронирования');
      }
    }
  };

  const handleBackToHome = () => {
    window.location.href = '/';
  };

  const handleRefresh = () => {
    loadUserData();
  };

  if (loading) {
    return (
      <div className="my-bookings-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          Загрузка ваших бронирований...
        </div>
      </div>
    );
  }

  // Объединяем бронирования с платежами
  const bookingsWithPayments = bookings.map(booking => getBookingWithPayment(booking));

  return (
    <section id="my-bookings" className="my-bookings-section">
      <div className="background-container">
        <img src="/images/67f504fdfc00ad2f7d384258d27391b08ef7aabd.png" alt="Abstract background" className="bg-image" />
        <div className="bg-overlay"></div>
      </div>
      
      <div className="my-bookings-container">
        <div className="my-bookings-header">
          <div>
            <h1>📋 Мои бронирования</h1>
            <p className="bookings-count">
              Найдено бронирований: {bookings.length} | Платежей: {payments.length}
            </p>
          </div>
          <div className="header-actions">
            <button onClick={handleRefresh} className="btn secondary">
              🔄 Обновить
            </button>
            <button onClick={handleBackToHome} className="btn secondary">
              На главную
            </button>
          </div>
        </div>

        {error && (
          <div className="error-message">
            ⚠️ {error}
          </div>
        )}

        {bookings.length === 0 ? (
          <div className="no-bookings">
            <div className="no-bookings-icon">📭</div>
            <h2>У вас нет активных бронирований</h2>
            <p>Забронируйте место в компьютерном клубе, чтобы увидеть его здесь</p>
            <button 
              onClick={() => window.location.href = '/booking'} 
              className="btn primary"
            >
              Забронировать место
            </button>
          </div>
        ) : (
          <div className="bookings-list">
            {bookingsWithPayments.map((booking) => {
              const statusInfo = getStatusInfo(
                booking.status, 
                booking.payment?.status
              );
              
              const isPaymentPending = booking.payment?.status === 'pending';
              const canCancel = ['pending', 'confirmed'].includes(booking.status);
              
              return (
                <div key={booking.id} className="booking-card">
                  <div className="booking-header">
                    <div className="booking-info">
                      <h3>Бронь #{booking.id}</h3>
                      <span 
                        className="booking-status"
                        style={{ backgroundColor: statusInfo.color }}
                      >
                        {statusInfo.icon} {statusInfo.text}
                      </span>
                    </div>
                    <div className="booking-date">
                      Создано: {new Date(booking.created_at).toLocaleDateString('ru-RU')}
                    </div>
                  </div>

                  <div className="booking-details">
                    {/* Основная информация о брони */}
                    <div className="detail-row">
                      <div className="detail-item">
                        <span className="detail-label">📍 Место:</span>
                        <span className="detail-value">
                          {booking.computer_id ? `Компьютер #${booking.computer_id}` : 'Не указано'}
                        </span>
                      </div>
                      <div className="detail-item">
                        <span className="detail-label">🏢 Клуб:</span>
                        <span className="detail-value">
                          {booking.club_id ? `Клуб #${booking.club_id}` : 'Не указано'}
                        </span>
                      </div>
                    </div>

                    <div className="detail-row">
                      <div className="detail-item">
                        <span className="detail-label">🕐 Дата начала:</span>
                        <span className="detail-value">
                          {formatDateTime(booking.date_from)}
                        </span>
                      </div>
                      <div className="detail-item">
                        <span className="detail-label">🕐 Дата окончания:</span>
                        <span className="detail-value">
                          {formatDateTime(booking.date_to)}
                        </span>
                      </div>
                    </div>

                    {/* Информация о платеже */}
                    {booking.payment && (
                      <div className="payment-section">
                        <h4>💳 Информация о платеже</h4>
                        <div className="detail-row">
                          <div className="detail-item">
                            <span className="detail-label">ID платежа:</span>
                            <span className="detail-value">#{booking.payment.id}</span>
                          </div>
                          <div className="detail-item">
                            <span className="detail-label">Тип оплаты:</span>
                            <span className="detail-value">
                              {booking.payment.payment_type === 'card' ? '💳 Карта' : '💵 Наличные'}
                            </span>
                          </div>
                        </div>
                        <div className="detail-row">
                          <div className="detail-item">
                            <span className="detail-label">Дата платежа:</span>
                            <span className="detail-value">
                              {formatDateTime(booking.payment.payment_date)}
                            </span>
                          </div>
                          <div className="detail-item">
                            <span className="detail-label">Хеш платежа:</span>
                            <span className="detail-value small">
                              {booking.payment.payment_hash}
                            </span>
                          </div>
                        </div>
                      </div>
                    )}

                    <div className="booking-total">
                      <span className="total-label">💰 Сумма:</span>
                      <span className="total-amount">
                        {formatPrice(booking.payment?.price || booking.total_price)}
                      </span>
                    </div>
                  </div>

                  <div className="booking-actions">
                    {isPaymentPending && (
                      <button 
                        onClick={() => handlePayNow(booking.payment.id)}
                        className="btn primary"
                      >
                        💳 Оплатить сейчас
                      </button>
                    )}
                    
                    {canCancel && (
                      <button 
                        onClick={() => handleCancelBooking(booking.id)}
                        className="btn secondary"
                      >
                        ❌ Отменить бронь
                      </button>
                    )}
                    
                    <button className="btn outline">
                      📞 Связаться с поддержкой
                    </button>

                    <button className="btn outline">
                      📧 Получить квитанцию
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}

        {/* Статистика */}
        {bookings.length > 0 && (
          <div className="bookings-stats">
            <div className="stat-card">
              <span className="stat-number">{bookings.length}</span>
              <span className="stat-label">Всего броней</span>
            </div>
            <div className="stat-card">
              <span className="stat-number">
                {bookings.filter(b => b.status === 'confirmed' || b.status === 'active').length}
              </span>
              <span className="stat-label">Активные</span>
            </div>
            <div className="stat-card">
              <span className="stat-number">
                {payments.filter(p => p.status === 'pending').length}
              </span>
              <span className="stat-label">Ожидают оплаты</span>
            </div>
            <div className="stat-card">
              <span className="stat-number">
                {payments.filter(p => p.status === 'completed').length}
              </span>
              <span className="stat-label">Оплачено</span>
            </div>
          </div>
        )}

        {/* Отладочная информация */}
        <div className="debug-info" style={{
          background: 'rgba(255,255,255,0.1)', 
          padding: '15px', 
          borderRadius: '8px', 
          marginTop: '30px',
          color: 'white',
          fontSize: '12px'
        }}>
          <strong>Отладочная информация:</strong><br/>
          - Загружено бронирований: {bookings.length}<br/>
          - Загружено платежей: {payments.length}<br/>
          - Первое бронирование: {bookings[0]?.id}<br/>
          - Первый платеж: {payments[0]?.id}
        </div>
      </div>
    </section>
  );
};

export default MyBookings;
