import React, { useState, useEffect } from 'react';
import { apiService } from '../services/Api';
import '../styles/MyBookings.css';

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadUserData();
  }, []);

  const loadUserData = async () => {
    try {
      setLoading(true);
      
      // –ó–∞–≥—Ä—É–∂–∞–µ–º –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è —á–µ—Ä–µ–∑ API
      const userBookings = await apiService.getBookings();
      console.log('üìã Bookings loaded:', userBookings);
      
      // –ó–∞–≥—Ä—É–∂–∞–µ–º –ø–ª–∞—Ç–µ–∂–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è —á–µ—Ä–µ–∑ API
      const userPayments = await apiService.getPayments();
      console.log('üí∞ Payments loaded:', userPayments);
      
      setBookings(userBookings);
      setPayments(userPayments);
      
    } catch (err) {
      console.error('Error loading user data:', err);
      setError('–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –¥–∞–Ω–Ω—ã–µ –æ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è—Ö');
      
      // Fallback: –ø—Ä–æ–≤–µ—Ä—è–µ–º localStorage
      const lastBooking = localStorage.getItem('lastBooking');
      if (lastBooking) {
        try {
          const booking = JSON.parse(lastBooking);
          if (booking.status && booking.status !== 'draft') {
            setBookings([booking]);
          }
        } catch (e) {
          console.error('Error parsing localStorage booking:', e);
        }
      }
    } finally {
      setLoading(false);
    }
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–±—ä–µ–¥–∏–Ω–µ–Ω–∏—è –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–π –∏ –ø–ª–∞—Ç–µ–∂–µ–π
  const getBookingWithPayment = (booking) => {
    const payment = payments.find(p => p.booking_id === booking.id);
    return {
      ...booking,
      payment: payment || null
    };
  };

  const getStatusInfo = (status, paymentStatus) => {
    // –ï—Å–ª–∏ –µ—Å—Ç—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–ª–∞—Ç–µ–∂–µ, –∏—Å–ø–æ–ª—å–∑—É–µ–º –µ—ë –¥–ª—è –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏—è —Å—Ç–∞—Ç—É—Å–∞
    if (paymentStatus) {
      const paymentStatusMap = {
        'pending': { text: '–û–∂–∏–¥–∞–µ—Ç –æ–ø–ª–∞—Ç—ã', color: '#ffc107', icon: 'üí∞' },
        'completed': { text: '–û–ø–ª–∞—á–µ–Ω–æ', color: '#28a745', icon: 'üí≥' },
        'failed': { text: '–û—à–∏–±–∫–∞ –æ–ø–ª–∞—Ç—ã', color: '#dc3545', icon: '‚ùå' },
        'refunded': { text: '–í–æ–∑–≤—Ä–∞—Ç', color: '#6c757d', icon: '‚Ü©Ô∏è' }
      };
      
      return paymentStatusMap[paymentStatus] || paymentStatusMap.pending;
    }

    // –°—Ç–∞—Ç—É—Å—ã –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–π
    const statusMap = {
      'draft': { text: '–ß–µ—Ä–Ω–æ–≤–∏–∫', color: '#6c757d', icon: 'üìù' },
      'pending': { text: '–û–∂–∏–¥–∞–µ—Ç –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è', color: '#17a2b8', icon: '‚è≥' },
      'confirmed': { text: '–ü–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–æ', color: '#28a745', icon: '‚úÖ' },
      'active': { text: '–ê–∫—Ç–∏–≤–Ω–æ', color: '#28a745', icon: 'üü¢' },
      'completed': { text: '–ó–∞–≤–µ—Ä—à–µ–Ω–æ', color: '#6c757d', icon: 'üèÅ' },
      'cancelled': { text: '–û—Ç–º–µ–Ω–µ–Ω–æ', color: '#dc3545', icon: '‚ùå' }
    };
    
    return statusMap[status] || { text: status, color: '#6c757d', icon: '‚ùì' };
  };

  const formatDateTime = (dateString, timeString) => {
    if (!dateString) return '–ù–µ —É–∫–∞–∑–∞–Ω–æ';
    
    try {
      const date = new Date(dateString);
      const formattedDate = date.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'long',
        weekday: 'short'
      });
      
      return timeString ? `${formattedDate} ${timeString}` : formattedDate;
    } catch (e) {
      return dateString;
    }
  };

  const formatPrice = (price) => {
    if (!price) return '0 ‚ÇΩ';
    return new Intl.NumberFormat('ru-RU', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(price) + ' ‚ÇΩ';
  };

  const handlePayNow = async (paymentId) => {
    try {
      // –ó–¥–µ—Å—å –¥–æ–ª–∂–Ω–∞ –±—ã—Ç—å –ª–æ–≥–∏–∫–∞ –¥–ª—è –∏–Ω–∏—Ü–∏–∞—Ü–∏–∏ –æ–ø–ª–∞—Ç—ã
      alert(`–ò–Ω–∏—Ü–∏–∏—Ä–æ–≤–∞–Ω–∞ –æ–ø–ª–∞—Ç–∞ –ø–ª–∞—Ç–µ–∂–∞ #${paymentId}`);
      // –í —Ä–µ–∞–ª—å–Ω–æ–º –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–∏ –∑–¥–µ—Å—å –±—ã–ª –±—ã –ø–µ—Ä–µ—Ö–æ–¥ –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É –ø–ª–∞—Ç–µ–∂–Ω–æ–π —Å–∏—Å—Ç–µ–º—ã
    } catch (err) {
      console.error('Error initiating payment:', err);
      alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∏–Ω–∏—Ü–∏–∞—Ü–∏–∏ –æ–ø–ª–∞—Ç—ã');
    }
  };

  const handleCancelBooking = async (bookingId) => {
    if (window.confirm('–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ –æ—Ç–º–µ–Ω–∏—Ç—å –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ?')) {
      try {
        // –í —Ä–µ–∞–ª—å–Ω–æ–º –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–∏ –∑–¥–µ—Å—å –±—ã–ª –±—ã API –∑–∞–ø—Ä–æ—Å –Ω–∞ –æ—Ç–º–µ–Ω—É –±—Ä–æ–Ω–∏
        // await apiService.cancelBooking(bookingId);
        
        // –ü–æ–∫–∞ –ø—Ä–æ—Å—Ç–æ –æ–±–Ω–æ–≤–ª—è–µ–º –ª–æ–∫–∞–ª—å–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ
        const updatedBookings = bookings.map(booking => 
          booking.id === bookingId 
            ? { ...booking, status: 'cancelled' }
            : booking
        );
        
        setBookings(updatedBookings);
        alert('–ë—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ —É—Å–ø–µ—à–Ω–æ –æ—Ç–º–µ–Ω–µ–Ω–æ');
      } catch (err) {
        console.error('Error cancelling booking:', err);
        alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ç–º–µ–Ω–µ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è');
      }
    }
  };

  const handleBackToHome = () => {
    window.location.href = '/';
  };

  const handleRefresh = () => {
    loadUserData();
  };

  if (loading) {
    return (
      <div className="my-bookings-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          –ó–∞–≥—Ä—É–∑–∫–∞ –≤–∞—à–∏—Ö –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–π...
        </div>
      </div>
    );
  }

  // –û–±—ä–µ–¥–∏–Ω—è–µ–º –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è —Å –ø–ª–∞—Ç–µ–∂–∞–º–∏
  const bookingsWithPayments = bookings.map(booking => getBookingWithPayment(booking));

  return (
    <section id="my-bookings" className="my-bookings-section">
      <div className="background-container">
        <img src="/images/67f504fdfc00ad2f7d384258d27391b08ef7aabd.png" alt="Abstract background" className="bg-image" />
        <div className="bg-overlay"></div>
      </div>
      
      <div className="my-bookings-container">
        <div className="my-bookings-header">
          <div>
            <h1>üìã –ú–æ–∏ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è</h1>
            <p className="bookings-count">
              –ù–∞–π–¥–µ–Ω–æ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–π: {bookings.length} | –ü–ª–∞—Ç–µ–∂–µ–π: {payments.length}
            </p>
          </div>
          <div className="header-actions">
            <button onClick={handleRefresh} className="btn secondary">
              üîÑ –û–±–Ω–æ–≤–∏—Ç—å
            </button>
            <button onClick={handleBackToHome} className="btn secondary">
              –ù–∞ –≥–ª–∞–≤–Ω—É—é
            </button>
          </div>
        </div>

        {error && (
          <div className="error-message">
            ‚ö†Ô∏è {error}
          </div>
        )}

        {bookings.length === 0 ? (
          <div className="no-bookings">
            <div className="no-bookings-icon">üì≠</div>
            <h2>–£ –≤–∞—Å –Ω–µ—Ç –∞–∫—Ç–∏–≤–Ω—ã—Ö –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–π</h2>
            <p>–ó–∞–±—Ä–æ–Ω–∏—Ä—É–π—Ç–µ –º–µ—Å—Ç–æ –≤ –∫–æ–º–ø—å—é—Ç–µ—Ä–Ω–æ–º –∫–ª—É–±–µ, —á—Ç–æ–±—ã —É–≤–∏–¥–µ—Ç—å –µ–≥–æ –∑–¥–µ—Å—å</p>
            <button 
              onClick={() => window.location.href = '/booking'} 
              className="btn primary"
            >
              –ó–∞–±—Ä–æ–Ω–∏—Ä–æ–≤–∞—Ç—å –º–µ—Å—Ç–æ
            </button>
          </div>
        ) : (
          <div className="bookings-list">
            {bookingsWithPayments.map((booking) => {
              const statusInfo = getStatusInfo(
                booking.status, 
                booking.payment?.status
              );
              
              const isPaymentPending = booking.payment?.status === 'pending';
              const canCancel = ['pending', 'confirmed'].includes(booking.status);
              
              return (
                <div key={booking.id} className="booking-card">
                  <div className="booking-header">
                    <div className="booking-info">
                      <h3>–ë—Ä–æ–Ω—å #{booking.id}</h3>
                      <span 
                        className="booking-status"
                        style={{ backgroundColor: statusInfo.color }}
                      >
                        {statusInfo.icon} {statusInfo.text}
                      </span>
                    </div>
                    <div className="booking-date">
                      –°–æ–∑–¥–∞–Ω–æ: {new Date(booking.created_at).toLocaleDateString('ru-RU')}
                    </div>
                  </div>

                  <div className="booking-details">
                    {/* –û—Å–Ω–æ–≤–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –±—Ä–æ–Ω–∏ */}
                    <div className="detail-row">
                      <div className="detail-item">
                        <span className="detail-label">üìç –ú–µ—Å—Ç–æ:</span>
                        <span className="detail-value">
                          {booking.computer_id ? `–ö–æ–º–ø—å—é—Ç–µ—Ä #${booking.computer_id}` : '–ù–µ —É–∫–∞–∑–∞–Ω–æ'}
                        </span>
                      </div>
                      <div className="detail-item">
                        <span className="detail-label">üè¢ –ö–ª—É–±:</span>
                        <span className="detail-value">
                          {booking.club_id ? `–ö–ª—É–± #${booking.club_id}` : '–ù–µ —É–∫–∞–∑–∞–Ω–æ'}
                        </span>
                      </div>
                    </div>

                    <div className="detail-row">
                      <div className="detail-item">
                        <span className="detail-label">üïê –î–∞—Ç–∞ –Ω–∞—á–∞–ª–∞:</span>
                        <span className="detail-value">
                          {formatDateTime(booking.date_from)}
                        </span>
                      </div>
                      <div className="detail-item">
                        <span className="detail-label">üïê –î–∞—Ç–∞ –æ–∫–æ–Ω—á–∞–Ω–∏—è:</span>
                        <span className="detail-value">
                          {formatDateTime(booking.date_to)}
                        </span>
                      </div>
                    </div>

                    {/* –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–ª–∞—Ç–µ–∂–µ */}
                    {booking.payment && (
                      <div className="payment-section">
                        <h4>üí≥ –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–ª–∞—Ç–µ–∂–µ</h4>
                        <div className="detail-row">
                          <div className="detail-item">
                            <span className="detail-label">ID –ø–ª–∞—Ç–µ–∂–∞:</span>
                            <span className="detail-value">#{booking.payment.id}</span>
                          </div>
                          <div className="detail-item">
                            <span className="detail-label">–¢–∏–ø –æ–ø–ª–∞—Ç—ã:</span>
                            <span className="detail-value">
                              {booking.payment.payment_type === 'card' ? 'üí≥ –ö–∞—Ä—Ç–∞' : 'üíµ –ù–∞–ª–∏—á–Ω—ã–µ'}
                            </span>
                          </div>
                        </div>
                        <div className="detail-row">
                          <div className="detail-item">
                            <span className="detail-label">–î–∞—Ç–∞ –ø–ª–∞—Ç–µ–∂–∞:</span>
                            <span className="detail-value">
                              {formatDateTime(booking.payment.payment_date)}
                            </span>
                          </div>
                          <div className="detail-item">
                            <span className="detail-label">–•–µ—à –ø–ª–∞—Ç–µ–∂–∞:</span>
                            <span className="detail-value small">
                              {booking.payment.payment_hash}
                            </span>
                          </div>
                        </div>
                      </div>
                    )}

                    <div className="booking-total">
                      <span className="total-label">üí∞ –°—É–º–º–∞:</span>
                      <span className="total-amount">
                        {formatPrice(booking.payment?.price || booking.total_price)}
                      </span>
                    </div>
                  </div>

                  <div className="booking-actions">
                    {isPaymentPending && (
                      <button 
                        onClick={() => handlePayNow(booking.payment.id)}
                        className="btn primary"
                      >
                        üí≥ –û–ø–ª–∞—Ç–∏—Ç—å —Å–µ–π—á–∞—Å
                      </button>
                    )}
                    
                    {canCancel && (
                      <button 
                        onClick={() => handleCancelBooking(booking.id)}
                        className="btn secondary"
                      >
                        ‚ùå –û—Ç–º–µ–Ω–∏—Ç—å –±—Ä–æ–Ω—å
                      </button>
                    )}
                    
                    <button className="btn outline">
                      üìû –°–≤—è–∑–∞—Ç—å—Å—è —Å –ø–æ–¥–¥–µ—Ä–∂–∫–æ–π
                    </button>

                    <button className="btn outline">
                      üìß –ü–æ–ª—É—á–∏—Ç—å –∫–≤–∏—Ç–∞–Ω—Ü–∏—é
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}

        {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ */}
        {bookings.length > 0 && (
          <div className="bookings-stats">
            <div className="stat-card">
              <span className="stat-number">{bookings.length}</span>
              <span className="stat-label">–í—Å–µ–≥–æ –±—Ä–æ–Ω–µ–π</span>
            </div>
            <div className="stat-card">
              <span className="stat-number">
                {bookings.filter(b => b.status === 'confirmed' || b.status === 'active').length}
              </span>
              <span className="stat-label">–ê–∫—Ç–∏–≤–Ω—ã–µ</span>
            </div>
            <div className="stat-card">
              <span className="stat-number">
                {payments.filter(p => p.status === 'pending').length}
              </span>
              <span className="stat-label">–û–∂–∏–¥–∞—é—Ç –æ–ø–ª–∞—Ç—ã</span>
            </div>
            <div className="stat-card">
              <span className="stat-number">
                {payments.filter(p => p.status === 'completed').length}
              </span>
              <span className="stat-label">–û–ø–ª–∞—á–µ–Ω–æ</span>
            </div>
          </div>
        )}

        {/* –û—Ç–ª–∞–¥–æ—á–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è */}
        <div className="debug-info" style={{
          background: 'rgba(255,255,255,0.1)', 
          padding: '15px', 
          borderRadius: '8px', 
          marginTop: '30px',
          color: 'white',
          fontSize: '12px'
        }}>
          <strong>–û—Ç–ª–∞–¥–æ—á–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è:</strong><br/>
          - –ó–∞–≥—Ä—É–∂–µ–Ω–æ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–π: {bookings.length}<br/>
          - –ó–∞–≥—Ä—É–∂–µ–Ω–æ –ø–ª–∞—Ç–µ–∂–µ–π: {payments.length}<br/>
          - –ü–µ—Ä–≤–æ–µ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ: {bookings[0]?.id}<br/>
          - –ü–µ—Ä–≤—ã–π –ø–ª–∞—Ç–µ–∂: {payments[0]?.id}
        </div>
      </div>
    </section>
  );
};

export default MyBookings;
