              <button type="button" className="btn secondary" onClick={scrollToCafe}>ÐŸÐµÑ€ÐµÐ¹Ñ‚Ð¸ Ð² ÐºÐ°Ñ„Ðµ</button>
              {getTotalItems() > 0 && (
