import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import { apiService } from '../services/Api';
import PlaceDetails from './PlaceDetails';
import '../styles/CombinedLayout.css';

const CombinedLayoutBooking = () => {
  const { 
    cartItems, 
    updateCartItemQuantity, 
    clearCart, 
    getTotalPrice, 
    getTotalItems,
    getCartSummary 
  } = useCart();

  const [formData, setFormData] = useState({
    place: '',
    dateFrom: '',
    dateTo: '',
    timeFrom: '',
    timeTo: '',
    address: '',
    cart: '',
    club_id: '',
    computer_id: ''
  });

  const [selectedPlace, setSelectedPlace] = useState(null);
  const [selectedRoom, setSelectedRoom] = useState(null); // ← новое состояние
  const [showPlaceDetails, setShowPlaceDetails] = useState(false);
  const [selectedPlaceRate, setSelectedPlaceRate] = useState(0);
  const [showDatePicker, setShowDatePicker] = useState(false);
  const [showTimePicker, setShowTimePicker] = useState(false);
  const [currentField, setCurrentField] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedHour, setSelectedHour] = useState('12');
  const [selectedMinute, setSelectedMinute] = useState('00');
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [timeError, setTimeError] = useState('');
  const [computers, setComputers] = useState([]);
  const [positions, setPositions] = useState([]);
  const [clubs, setClubs] = useState([]);
  const [loading, setLoading] = useState(true);

  const hours = Array.from({ length: 24 }, (_, i) => i.toString().padStart(2, '0'));
  const minutes = Array.from({ length: 60 }, (_, i) => i.toString().padStart(2, '0'));

  useEffect(() => {
    loadInitialData();
  }, []);

  const loadInitialData = async () => {
    try {
      setLoading(true);
      const [computersData, positionsData, clubsData] = await Promise.all([
        apiService.getComputers(),
        apiService.getComputerPositions(),
        apiService.getClubs()
      ]);
      setComputers(computersData);
      setPositions(positionsData);
      setClubs(clubsData);
      console.log('✅ All data loaded:', {
        computers: computersData.length,
        positions: positionsData.length,
        clubs: clubsData.length
      });
    } catch (error) {
      console.error('Error loading initial ', error);
    } finally {
      setLoading(false);
    }
  };

  // Получить список комнат для выбранного клуба
  const getAvailableRooms = () => {
    if (!formData.club_id) return [];
    const rooms = new Set();
    positions.forEach(pos => {
      if (pos.club_id == formData.club_id) {
        rooms.add(pos.room);
      }
    });
    return Array.from(rooms).sort();
  };

  const getPositionInfo = (positionNumber) => {
    const position = positions.find(pos => 
      pos.number == positionNumber &&
      pos.club_id == formData.club_id &&
      pos.room === selectedRoom
    );
    if (!position) return null;

    const computer = computers.find(comp => comp.position_id == position.id);
    if (!computer) return null;

    return {
      position,
      computer,
      room: position.room,
      number: position.number,
      computerId: computer.id,
      price: parseFloat(computer.price) || 100
    };
  };

  const handlePlaceClick = (positionNumber) => {
    const positionInfo = getPositionInfo(positionNumber);
    if (positionInfo && positionInfo.computer) {
      setSelectedPlace(positionNumber);
      setShowPlaceDetails(true);
    } else {
      alert('Это место временно недоступно для бронирования.');
    }
  };

  const handlePlaceSelectFromDetails = (positionNumber, placeRate) => {
    const positionInfo = getPositionInfo(positionNumber);
    if (positionInfo && positionInfo.computer) {
      const newComputerId = positionInfo.computerId.toString();
      setSelectedPlace(positionNumber);
      setSelectedPlaceRate(placeRate);
      setFormData(prev => ({
        ...prev,
        place: positionNumber.toString(),
        computer_id: newComputerId
      }));
      setShowPlaceDetails(false);
    } else {
      alert('Ошибка: не найден компьютер для этого места. Пожалуйста, выберите другое место.');
    }
  };

  const handleBackFromDetails = () => {
    setShowPlaceDetails(false);
  };

  const isPlaceAvailable = (positionNumber) => {
    const positionInfo = getPositionInfo(positionNumber);
    return positionInfo && positionInfo.computer !== undefined;
  };

  // Получаем номера мест только для выбранной комнаты и клуба
  const getDisplayPositions = () => {
    if (!formData.club_id || !selectedRoom) return [];

    const roomPositions = positions.filter(pos => 
      pos.club_id == formData.club_id && 
      pos.room === selectedRoom
    ).map(pos => pos.number);

    // Фильтруем по жёсткому списку, как в оригинале
    return [1, 2, 3, 4, 5, 6, 8, 9, 10, 11, 12, 13, 14]
      .filter(num => roomPositions.includes(num))
      .sort((a, b) => a - b);
  };

  // === Календарь и время (без изменений) ===
  const generateCalendarDays = () => {
    const year = currentMonth.getFullYear();
    const month = currentMonth.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startDay = firstDay.getDay();
    const days = [];
    for (let i = 0; i < startDay; i++) days.push(null);
    for (let day = 1; day <= daysInMonth; day++) days.push(new Date(year, month, day));
    return days;
  };

  const calendarDays = generateCalendarDays();
  const monthNames = ['Январь', 'Февраль', 'Март', 'Апрель', 'Май', 'Июнь', 'Июль', 'Август', 'Сентябрь', 'Октябрь', 'Ноябрь', 'Декабрь'];
  const dayNames = ['Вс', 'Пн', 'Вт', 'Ср', 'Чт', 'Пт', 'Сб'];

  const prevMonth = () => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1, 1));
  const nextMonth = () => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 1));

  const isToday = (date) => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const checkDate = new Date(date.getFullYear(), date.getMonth(), date.getDate());
    return checkDate.toDateString() === today.toDateString();
  };

  const isPastDate = (date) => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const checkDate = new Date(date.getFullYear(), date.getMonth(), date.getDate());
    return checkDate < today;
  };

  const validateTimeRange = (dateFrom, timeFrom, dateTo, timeTo) => {
    if (!dateFrom || !timeFrom || !dateTo || !timeTo) return true;
    const start = new Date(`${dateFrom}T${timeFrom}`);
    const end = new Date(`${dateTo}T${timeTo}`);
    if (end <= start) {
      setTimeError('Время окончания должно быть позже времени начала');
      return false;
    }
    const diffMin = (end - start) / (1000 * 60);
    if (diffMin < 30) {
      setTimeError('Минимальное время бронирования - 30 минут');
      return false;
    }
    if (diffMin > 24 * 60) {
      setTimeError('Максимальное время бронирования - 24 часа');
      return false;
    }
    setTimeError('');
    return true;
  };

  useEffect(() => {
    if (formData.dateFrom && formData.timeFrom && formData.dateTo && formData.timeTo) {
      validateTimeRange(formData.dateFrom, formData.timeFrom, formData.dateTo, formData.timeTo);
    } else {
      setTimeError('');
    }
  }, [formData.dateFrom, formData.timeFrom, formData.dateTo, formData.timeTo]);

  useEffect(() => {
    setFormData(prev => ({ ...prev, cart: getCartSummary() }));
  }, [cartItems, getCartSummary]);

  useEffect(() => {
    if (selectedPlace && !formData.computer_id) {
      const info = getPositionInfo(selectedPlace);
      if (info && info.computer) {
        setFormData(prev => ({
          ...prev,
          computer_id: info.computerId.toString(),
          place: selectedPlace.toString()
        }));
      }
    }
  }, [selectedPlace, formData.computer_id]);

  const setAddressFromStorage = () => {
    const saved = localStorage.getItem('selectedClubAddress');
    if (saved) {
      const club = clubs.find(c => c.address === saved);
      setFormData(prev => ({
        ...prev,
        address: saved,
        club_id: club ? club.id : ''
      }));
      setSelectedRoom(null); // ← сброс комнаты при смене клуба
    }
  };

  useEffect(() => {
    if (clubs.length > 0) setAddressFromStorage();
  }, [clubs]);

  useEffect(() => {
    const handler = () => setAddressFromStorage();
    window.addEventListener('clubAddressSelected', handler);
    return () => window.removeEventListener('clubAddressSelected', handler);
  }, [clubs]);

  const handleClubChange = (e) => {
    const { value } = e.target;
    const club = clubs.find(c => c.address === value);
    setFormData(prev => ({
      ...prev,
      address: value,
      club_id: club ? club.id : ''
    }));
    setSelectedRoom(null);
    setSelectedPlace(null);
    localStorage.removeItem('selectedClubAddress');
  };

  // === Обработчики даты/времени (без изменений) ===
  const handleDateFieldClick = (field) => {
    setCurrentField(field);
    setShowDatePicker(true);
    const dateStr = field === 'from' ? formData.dateFrom : formData.dateTo;
    if (dateStr) {
      const [y, m, d] = dateStr.split('-').map(Number);
      setSelectedDate(new Date(y, m - 1, d));
    } else {
      setSelectedDate(new Date());
    }
  };

  const handleTimeFieldClick = (field) => {
    setCurrentField(field);
    setShowTimePicker(true);
    const timeStr = field === 'from' ? formData.timeFrom : formData.timeTo;
    if (timeStr && timeStr.includes(':')) {
      const [h, m] = timeStr.split(':');
      setSelectedHour(h);
      setSelectedMinute(m);
    } else {
      setSelectedHour('12');
      setSelectedMinute('00');
    }
  };

  const handleDateSelect = (date) => !isPastDate(date) && setSelectedDate(date);
  const handleDateConfirm = () => {
    if (!selectedDate) return;
    const y = selectedDate.getFullYear();
    const m = (selectedDate.getMonth() + 1).toString().padStart(2, '0');
    const d = selectedDate.getDate().toString().padStart(2, '0');
    const str = `${y}-${m}-${d}`;
    setFormData(prev => ({
      ...prev,
      [currentField === 'from' ? 'dateFrom' : 'dateTo']: str
    }));
    setShowDatePicker(false);
    setCurrentField(null);
  };
  const handleDateCancel = () => { setShowDatePicker(false); setCurrentField(null); setSelectedDate(null); };

  const handleTimeConfirm = () => {
    const time = `${selectedHour}:${selectedMinute}`;
    setFormData(prev => ({
      ...prev,
      [currentField === 'from' ? 'timeFrom' : 'timeTo']: time
    }));
    setShowTimePicker(false);
    setCurrentField(null);
  };
  const handleTimeCancel = () => { setShowTimePicker(false); setCurrentField(null); };

  const formatDateDisplay = (dateString) => {
    if (!dateString) return '';
    const [y, m, d] = dateString.split('-').map(Number);
    const date = new Date(y, m - 1, d);
    const today = new Date(); today.setHours(0,0,0,0);
    const tomorrow = new Date(today); tomorrow.setDate(tomorrow.getDate() + 1);
    if (date.toDateString() === today.toDateString()) return 'Сегодня';
    if (date.toDateString() === tomorrow.toDateString()) return 'Завтра';
    return date.toLocaleDateString('ru-RU', { day: 'numeric', month: 'long', weekday: 'short' });
  };

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (showDatePicker && !e.target.closest('.date-picker')) handleDateCancel();
      if (showTimePicker && !e.target.closest('.time-picker')) handleTimeCancel();
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [showDatePicker, showTimePicker]);

  const getBookingMinutes = () => {
    if (!formData.dateFrom || !formData.timeFrom || !formData.dateTo || !formData.timeTo) return 0;
    const start = new Date(`${formData.dateFrom}T${formData.timeFrom}`);
    const end = new Date(`${formData.dateTo}T${formData.timeTo}`);
    return Math.round((end - start) / (1000 * 60));
  };

  const getBookingHours = () => getBookingMinutes() / 60;

  const isBookingDisabled = !selectedPlace || 
    !formData.dateFrom || !formData.timeFrom || 
    !formData.dateTo || !formData.timeTo ||
    !formData.address ||
    !formData.computer_id ||
    timeError;

  const getDisabledReason = () => {
    const reasons = [];
    if (!selectedPlace) reasons.push("Не выбрано место");
    if (!formData.dateFrom) reasons.push("Не выбрана дата начала");
    if (!formData.timeFrom) reasons.push("Не выбрано время начала");
    if (!formData.dateTo) reasons.push("Не выбрана дата окончания");
    if (!formData.timeTo) reasons.push("Не выбрано время окончания");
    if (!formData.address) reasons.push("Не выбран адрес клуба");
    if (!formData.computer_id) reasons.push("Не установлен computer_id");
    if (timeError) reasons.push(timeError);
    return reasons.join(", ");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateTimeRange(formData.dateFrom, formData.timeFrom, formData.dateTo, formData.timeTo)) return;

    const computerId = formData.computer_id || (selectedPlace ? getPositionInfo(selectedPlace)?.computerId?.toString() : null);
    if (!computerId) {
      alert('Не удалось определить компьютер для бронирования.');
      return;
    }

    const bookingMinutes = getBookingMinutes();
    const placeCost = Math.round((bookingMinutes / 60) * selectedPlaceRate);
    const totalCost = placeCost + getTotalPrice();

    const bookingData = {
      id: `temp_${Date.now()}`,
      formData: { ...formData, computer_id: computerId },
      selectedPlace,
      placeRate: selectedPlaceRate,
      cartItems: [...cartItems],
      totalPrice: getTotalPrice(),
      bookingMinutes,
      calculatedData: { placeCost, totalCost, bookingHours: getBookingHours() },
      status: 'draft',
      created_at: new Date().toISOString()
    };

    localStorage.setItem('lastBooking', JSON.stringify(bookingData));
    window.location.href = '/confirmation';
  };

  const handleClearCart = () => clearCart();
  const handleBackToHome = () => window.location.href = '/';
  const scrollToCafe = () => {
    const cafe = document.getElementById('cafe');
    if (cafe) cafe.scrollIntoView({ behavior: 'smooth' });
  };

  const displayPositions = getDisplayPositions();

  if (loading) {
    return (
      <div className="combined-booking-section">
        <div className="loading-container">
          <h2>Загрузка данных...</h2>
        </div>
      </div>
    );
  }

  return (
    <section id="combined-booking" className="combined-booking-section">
      <div className="background-container">
        <img src="/images/67f504fdfc00ad2f7d384258d27391b08ef7aabd.png" alt="Abstract background" className="bg-image" />
        <div className="bg-overlay"></div>
      </div>

      {/* Debug panel (без изменений) */}
      <div className="debug-panel" style={{
        position: 'fixed',
        top: '10px',
        right: '10px',
        background: 'rgba(0,0,0,0.9)',
        color: 'white',
        padding: '15px',
        borderRadius: '8px',
        fontSize: '12px',
        zIndex: 1000,
        maxWidth: '300px',
        border: '2px solid #667eea',
        fontFamily: 'monospace'
      }}>
        <div style={{ marginBottom: '10px', fontWeight: 'bold', color: '#667eea' }}>🔧 ДЕБАГ БРОНИ</div>
        <div>📍 Место: <span style={{color: selectedPlace ? '#4CAF50' : '#f44336'}}>{selectedPlace || 'НЕТ'}</span></div>
        <div>🏠 Комната: <span style={{color: selectedRoom ? '#4CAF50' : '#f44336'}}>{selectedRoom || 'НЕ ВЫБРАНА'}</span></div>
        <div>💻 Computer ID: <span style={{color: formData.computer_id ? '#4CAF50' : '#f44336'}}>{formData.computer_id || 'НЕТ'}</span></div>
        <div>📅 Дата/время: <span style={{color: formData.dateFrom && formData.timeFrom && formData.dateTo && formData.timeTo ? '#4CAF50' : '#f44336'}}>
          {formData.dateFrom && formData.timeFrom && formData.dateTo && formData.timeTo ? '✓' : '✗'}
        </span></div>
        <div>🏢 Адрес: <span style={{color: formData.address ? '#4CAF50' : '#f44336'}}>{formData.address ? '✓' : '✗'}</span></div>
        <div>🚫 Ошибка: <span style={{color: timeError ? '#f44336' : '#4CAF50'}}>{timeError || 'НЕТ'}</span></div>
        <div>🛒 Корзина: <span style={{color: cartItems.length > 0 ? '#FF9800' : '#4CAF50'}}>{cartItems.length} товаров</span></div>
        <div>🔄 Кнопка: <span style={{color: isBookingDisabled ? '#f44336' : '#4CAF50'}}>{isBookingDisabled ? '🔴 ВЫКЛ' : '🟢 ВКЛ'}</span></div>
        {isBookingDisabled && (
          <div style={{ marginTop: '8px', padding: '5px', background: '#f44336', borderRadius: '3px', fontSize: '10px' }}>
            Причина: {getDisabledReason()}
          </div>
        )}
      </div>

      <div className="combined-container">
        {/* Левая часть - схема мест или выбор комнаты */}
        <div className="layout-side">
          <h2 className="section-title">
            {selectedRoom 
              ? `Места в ${selectedRoom}` 
              : formData.club_id 
                ? 'Выберите комнату' 
                : 'Выберите место'}
          </h2>

          <div className="layout-content-box">
            <div className="layout-scaler">
              {/* Выбор комнаты */}
              {formData.club_id && !selectedRoom ? (
                <div className="room-selection">
                  <div className="rooms-grid">
                    {getAvailableRooms().map(room => (
                      <button
                        key={room}
                        className="room-btn"
                        onClick={() => setSelectedRoom(room)}
                      >
                        {room}
                      </button>
                    ))}
                  </div>
                  <button 
                    className="btn secondary"
                    onClick={() => setFormData(prev => ({ ...prev, address: '', club_id: '' }))}
                  >
                    ← Назад к выбору клуба
                  </button>
                </div>
              ) : selectedRoom ? (
                <>
                  {/* Схема мест */}
                  {displayPositions.map((positionNumber) => {
                    const isAvailable = isPlaceAvailable(positionNumber);
                    return (
                      <div 
                        key={`position-${positionNumber}`} 
                        className={`
                          ${positionNumber <= 6 ? 'pc-setup' : 
                            [8, 9, 12, 13].includes(positionNumber) ? 'console-setup' : 
                            'headphones-setup'
                          } 
                          setup-${positionNumber} 
                          ${selectedPlace === positionNumber ? 'selected' : ''}
                          ${!isAvailable ? 'unavailable' : ''}
                        `}
                        onClick={() => isAvailable && handlePlaceClick(positionNumber)}
                        style={{
                          cursor: isAvailable ? 'pointer' : 'not-allowed',
                          opacity: isAvailable ? 1 : 0.5
                        }}
                      >
                        <img 
                          src={
                            positionNumber <= 6 ? "/images/6356f02b474a41d638cf709af15fe1f7c6dd92c0.png" :
                            [8, 9, 12, 13].includes(positionNumber) ? "/images/1b9fb18a794f8543e1b7ff770153e91c8879c831.png" :
                            "/images/2fc05fccb4c07d9e1bb638c4487609fd22b2f1ec.png"
                          } 
                          alt={`Setup ${positionNumber}`} 
                        />
                        <span className="layout-number">
                          {positionNumber}
                          {!isAvailable && <span className="unavailable-badge">✗</span>}
                        </span>
                      </div>
                    );
                  })}
                  <button 
                    className="btn secondary back-to-rooms"
                    onClick={() => setSelectedRoom(null)}
                    style={{ marginTop: '20px', width: '100%' }}
                  >
                    ← Назад к выбору комнаты
                  </button>
                </>
              ) : (
                <div className="info-text">
                  Выберите клуб на странице "Наши клубы"
                </div>
              )}
            </div>
          </div>

          {/* Отладочная информация */}
          <div className="debug-info" style={{
            background: '#f5f5f5',
            padding: '10px',
            margin: '10px 0',
            borderRadius: '5px',
            fontSize: '12px'
          }}>
            <strong>Информация о данных:</strong><br/>
            - Позиций: {positions.length}<br/>
            - Компьютеров: {computers.length}<br/>
            - Клубов: {clubs.length}<br/>
            - Выбран клуб: {formData.club_id || 'нет'}<br/>
            - Выбрана комната: {selectedRoom || 'нет'}<br/>
            - Мест в комнате: {displayPositions.length}<br/>
            - Выбрано место: {selectedPlace || 'нет'}
          </div>
        </div>

        {/* Правая часть - форма бронирования */}
        <div className="booking-side">
          <h2 className="section-title">Бронь места {selectedPlace || ''}</h2>

          {getTotalItems() > 0 && (
            <div className="cart-info">
              <h3>Корзина ({getTotalItems()} товаров)</h3>
              <div className="cart-items">
                {cartItems.map(item => (
                  <div key={item.id} className="cart-item">
                    <span>{item.name} x{item.quantity}</span>
                    <div className="cart-item-controls">
                      <button onClick={() => updateCartItemQuantity(item.id, -1)} className="cart-btn">-</button>
                      <span>{item.quantity}</span>
                      <button onClick={() => updateCartItemQuantity(item.id, 1)} className="cart-btn">+</button>
                      <span className="cart-item-price">{item.price * item.quantity} ₽</span>
                    </div>
                  </div>
                ))}
              </div>
              <div className="cart-total">Итого: {getTotalPrice()} ₽</div>
            </div>
          )}

          <form className="booking-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="place">Выбранное место</label>
              <input 
                type="text" 
                id="place" 
                name="place"
                value={formData.place}
                readOnly
                placeholder="Нажмите на место слева"
                className={selectedPlace ? 'selected-place' : ''}
              />
              {selectedPlace && (
                <div className="place-selected-info">
                  ✓ Место {selectedPlace} выбрано.<br/>
                  <small style={{color: 'blue', fontWeight: 'bold'}}>
                    Computer ID: {formData.computer_id || 'не установлен'}
                    {!formData.computer_id && <span style={{color: 'red'}}> - ТРЕБУЕТСЯ ВЫБРАТЬ МЕСТО!</span>}
                  </small>
                  <button type="button" className="view-details-btn" onClick={() => setShowPlaceDetails(true)}>
                    Посмотреть детали
                  </button>
                </div>
              )}
            </div>

            <div className="datetime-fields">
              <div className="form-group">
                <label>Дата и время начала</label>
                <div className="datetime-inputs-row">
                  <input type="text" value={formData.dateFrom ? formatDateDisplay(formData.dateFrom) : 'Выберите дату'} readOnly onClick={() => handleDateFieldClick('from')} className="date-input" />
                  <input type="text" value={formData.timeFrom || 'Выберите время'} readOnly onClick={() => handleTimeFieldClick('from')} className="time-input" />
                </div>
              </div>
              <div className="form-group">
                <label>Дата и время окончания</label>
                <div className="datetime-inputs-row">
                  <input type="text" value={formData.dateTo ? formatDateDisplay(formData.dateTo) : 'Выберите дату'} readOnly onClick={() => handleDateFieldClick('to')} className="date-input" />
                  <input type="text" value={formData.timeTo || 'Выберите время'} readOnly onClick={() => handleTimeFieldClick('to')} className="time-input" />
                </div>
              </div>
              {timeError && <div className="time-error-message">⚠️ {timeError}</div>}
              {formData.dateFrom && formData.timeFrom && formData.dateTo && formData.timeTo && !timeError && (
                <div className="time-range-info">
                  ✅ Выбран промежуток: {formatDateDisplay(formData.dateFrom)} {formData.timeFrom} – {formatDateDisplay(formData.dateTo)} {formData.timeTo}
                </div>
              )}
            </div>

            {/* Пикеры (без изменений) */}
            {showDatePicker && (
              <div className="date-picker-overlay">
                <div className="date-picker">
                  <div className="date-picker-header">
                    <h3>{currentField === 'from' ? 'Выберите дату начала' : 'Выберите дату окончания'}</h3>
                    <button type="button" className="date-picker-close" onClick={handleDateCancel}>×</button>
                  </div>
                  <div className="date-picker-body">
                    <div className="calendar-section">
                      <div className="calendar-header">
                        <button type="button" className="calendar-nav prev" onClick={prevMonth}>‹</button>
                        <div className="calendar-month">{monthNames[currentMonth.getMonth()]} {currentMonth.getFullYear()}</div>
                        <button type="button" className="calendar-nav next" onClick={nextMonth}>›</button>
                      </div>
                      <div className="calendar-grid">
                        {dayNames.map(day => <div key={day} className="calendar-day-header">{day}</div>)}
                        {calendarDays.map((date, i) => (
                          <button
                            key={i}
                            type="button"
                            className={`calendar-day ${date ? (isPastDate(date) ? 'past' : selectedDate && date.toDateString() === selectedDate.toDateString() ? 'selected' : isToday(date) ? 'today' : '') : 'empty'}`}
                            onClick={() => date && handleDateSelect(date)}
                            disabled={!date || isPastDate(date)}
                          >
                            {date ? date.getDate() : ''}
                            {date && isToday(date) && <div className="today-dot"></div>}
                          </button>
                        ))}
                      </div>
                    </div>
                  </div>
                  <div className="date-picker-footer">
                    <div className="selected-date-preview">
                      {selectedDate ? `Выбрано: ${formatDateDisplay(`${selectedDate.getFullYear()}-${(selectedDate.getMonth()+1).toString().padStart(2,'0')}-${selectedDate.getDate().toString().padStart(2,'0')}`)}` : 'Выберите дату'}
                    </div>
                    <div className="date-picker-actions">
                      <button type="button" className="btn secondary" onClick={handleDateCancel}>Отмена</button>
                      <button type="button" className="btn primary" onClick={handleDateConfirm} disabled={!selectedDate}>Выбрать дату</button>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {showTimePicker && (
              <div className="time-picker-overlay">
                <div className="time-picker">
                  <div className="time-picker-header">
                    <h3>{currentField === 'from' ? 'Выберите время начала' : 'Выберите время окончания'}</h3>
                    <button type="button" className="time-picker-close" onClick={handleTimeCancel}>×</button>
                  </div>
                  <div className="time-picker-body">
                    <div className="time-selectors">
                      <div className="time-selector">
                        <label>Часы</label>
                        <select value={selectedHour} onChange={(e) => setSelectedHour(e.target.value)} className="time-select">
                          {hours.map(h => <option key={h} value={h}>{h}</option>)}
                        </select>
                      </div>
                      <div className="time-selector">
                        <label>Минуты</label>
                        <select value={selectedMinute} onChange={(e) => setSelectedMinute(e.target.value)} className="time-select">
                          {minutes.map(m => <option key={m} value={m}>{m}</option>)}
                        </select>
                      </div>
                    </div>
                  </div>
                  <div className="time-picker-footer">
                    <div className="selected-time-preview">
                      {selectedHour && selectedMinute ? `Выбрано: ${selectedHour}:${selectedMinute}` : 'Выберите время'}
                    </div>
                    <div className="time-picker-actions">
                      <button type="button" className="btn secondary" onClick={handleTimeCancel}>Отмена</button>
                      <button type="button" className="btn primary" onClick={handleTimeConfirm} disabled={!selectedHour || !selectedMinute}>Выбрать время</button>
                    </div>
                  </div>
                </div>
              </div>
            )}

            <div className="form-group">
              <label htmlFor="address">Адрес клуба</label>
              <select 
                id="address" 
                name="address"
                value={formData.address}
                onChange={handleClubChange}
                required
              >
                <option value="">Выберите клуб</option>
                {clubs.map(club => (
                  <option key={club.id} value={club.address}>{club.address}</option>
                ))}
              </select>
              {formData.address && <div className="auto-address-indicator">✓ Адрес автоматически выбран</div>}
            </div>

            <div className="booking-actions">
              <button 
                type="submit" 
                className="btn primary"
                disabled={isBookingDisabled}
                title={isBookingDisabled ? getDisabledReason() : "Перейти к подтверждению брони"}
              >
                Перейти к подтверждению
                {isBookingDisabled && (
                  <span style={{fontSize: '12px', display: 'block', marginTop: '5px', color: '#ff6b6b'}}>
                    ({getDisabledReason()})
                  </span>
                )}
              </button>
              <button type="button" className="btn secondary" onClick={handleBackToHome}>На главную</button>
              <button type="button" className="btn secondary" onClick={scrollToCafe}>Перейти в кафе</button>
              {getTotalItems() > 0 && (
                <button type="button" className="btn secondary" onClick={handleClearCart}>Очистить корзину</button>
              )}
            </div>
          </form>
        </div>
      </div>

      {showPlaceDetails && selectedPlace && (
        <PlaceDetails 
          place={selectedPlace}
          onBack={handleBackFromDetails}
          onSelect={handlePlaceSelectFromDetails}
          positionInfo={getPositionInfo(selectedPlace)}
        />
      )}
    </section>
  );
};

export default CombinedLayoutBooking;
