import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import '../styles/CombinedLayout.css';

const CombinedLayoutBooking = () => {
  const { 
    cartItems, 
    updateCartItemQuantity, 
    clearCart, 
    getTotalPrice, 
    getTotalItems,
    getCartSummary 
  } = useCart();
  
  const [formData, setFormData] = useState({
    place: '',
    date: '',
    timeFrom: '',
    timeTo: '',
    address: '',
    cart: ''
  });

  const [selectedPlace, setSelectedPlace] = useState(null);
  const [showTimePicker, setShowTimePicker] = useState(false);
  const [showDatePicker, setShowDatePicker] = useState(false);
  const [currentTimeField, setCurrentTimeField] = useState(null);
  const [selectedHour, setSelectedHour] = useState(null);
  const [selectedMinute, setSelectedMinute] = useState(null);
  const [selectedDate, setSelectedDate] = useState('');

  // Часы и минуты для выбора
  const hours = Array.from({ length: 24 }, (_, i) => i.toString().padStart(2, '0'));
  const minutes = ['00', '15', '30', '45'];

  // Генерация дат для календаря (текущий месяц + следующий)
  const generateCalendarDates = () => {
    const dates = [];
    const today = new Date();
    const currentMonth = today.getMonth();
    const currentYear = today.getFullYear();
    
    // Текущий месяц
    const daysInCurrentMonth = new Date(currentYear, currentMonth + 1, 0).getDate();
    for (let day = today.getDate(); day <= daysInCurrentMonth; day++) {
      const date = new Date(currentYear, currentMonth, day);
      dates.push(date);
    }
    
    // Следующий месяц
    const nextMonth = currentMonth + 1;
    const nextYear = nextMonth > 11 ? currentYear + 1 : currentYear;
    const daysInNextMonth = new Date(nextYear, nextMonth + 1, 0).getDate();
    const daysToShow = Math.min(14, daysInNextMonth); // Показываем только 2 недели следующего месяца
    
    for (let day = 1; day <= daysToShow; day++) {
      const date = new Date(nextYear, nextMonth, day);
      dates.push(date);
    }
    
    return dates;
  };

  const calendarDates = generateCalendarDates();

  // Автоматически обновляем поле корзины при изменении cartItems
  useEffect(() => {
    setFormData(prev => ({
      ...prev,
      cart: getCartSummary()
    }));
  }, [cartItems, getCartSummary]);

  // Функция для установки адреса
  const setAddressFromStorage = () => {
    const savedAddress = localStorage.getItem('selectedClubAddress');
    if (savedAddress) {
      setFormData(prev => ({
        ...prev,
        address: savedAddress
      }));
    }
  };

  // Загружаем адрес при монтировании компонента
  useEffect(() => {
    setAddressFromStorage();
  }, []);

  // Слушаем событие выбора адреса
  useEffect(() => {
    const handleAddressSelected = () => {
      setAddressFromStorage();
    };

    window.addEventListener('clubAddressSelected', handleAddressSelected);
    
    return () => {
      window.removeEventListener('clubAddressSelected', handleAddressSelected);
    };
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    if (name === 'address') {
      localStorage.removeItem('selectedClubAddress');
    }
  };

  const handlePlaceSelect = (placeNumber) => {
    setSelectedPlace(placeNumber);
    setFormData(prev => ({
      ...prev,
      place: placeNumber.toString()
    }));
  };

  // Обработчик клика по полю даты
  const handleDateFieldClick = () => {
    setShowDatePicker(true);
  };

  // Выбор даты
  const handleDateSelect = (date) => {
    const dateString = date.toISOString().split('T')[0]; // Формат YYYY-MM-DD
    const formattedDate = formatDateDisplay(date); // Красивый формат для отображения
    
    setFormData(prev => ({
      ...prev,
      date: dateString
    }));
    setSelectedDate(formattedDate);
    setShowDatePicker(false);
  };

  // Форматирование даты для отображения
  const formatDateDisplay = (date) => {
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);
    
    if (date.toDateString() === today.toDateString()) {
      return 'Сегодня';
    } else if (date.toDateString() === tomorrow.toDateString()) {
      return 'Завтра';
    } else {
      return date.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'long',
        weekday: 'short'
      });
    }
  };

  // Форматирование даты для календаря
  const formatCalendarDate = (date) => {
    return date.toLocaleDateString('ru-RU', {
      day: 'numeric',
      month: 'short'
    });
  };

  // Получение названия дня недели
  const getDayName = (date) => {
    return date.toLocaleDateString('ru-RU', { weekday: 'short' });
  };

  // Проверка, является ли дата сегодняшней
  const isToday = (date) => {
    const today = new Date();
    return date.toDateString() === today.toDateString();
  };

  // Обработчик клика по полю времени
  const handleTimeFieldClick = (fieldName) => {
    setCurrentTimeField(fieldName);
    setShowTimePicker(true);
    
    const currentTime = formData[fieldName];
    if (currentTime && currentTime.includes(':')) {
      const [hour, minute] = currentTime.split(':');
      setSelectedHour(hour);
      setSelectedMinute(minute);
    } else {
      setSelectedHour(null);
      setSelectedMinute(null);
    }
  };

  // Выбор часа
  const handleHourSelect = (hour) => {
    setSelectedHour(hour);
  };

  // Выбор минуты
  const handleMinuteSelect = (minute) => {
    setSelectedMinute(minute);
  };

  // Подтверждение выбора времени
  const handleTimeConfirm = () => {
    if (selectedHour && selectedMinute) {
      const timeString = `${selectedHour}:${selectedMinute}`;
      setFormData(prev => ({
        ...prev,
        [currentTimeField]: timeString
      }));
      setShowTimePicker(false);
      setCurrentTimeField(null);
    }
  };

  // Отмена выбора времени
  const handleTimeCancel = () => {
    setShowTimePicker(false);
    setCurrentTimeField(null);
    setSelectedHour(null);
    setSelectedMinute(null);
  };

  // Отмена выбора даты
  const handleDateCancel = () => {
    setShowDatePicker(false);
  };

  // Закрытие пикеров при клике вне их
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (showTimePicker && !e.target.closest('.time-picker')) {
        handleTimeCancel();
      }
      if (showDatePicker && !e.target.closest('.date-picker')) {
        handleDateCancel();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showTimePicker, showDatePicker]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const bookingData = {
      ...formData,
      selectedPlace,
      cartItems,
      totalPrice: getTotalPrice()
    };
    console.log('Booking data:', bookingData);
    localStorage.removeItem('selectedClubAddress');
  };

  const handleClearCart = () => {
    clearCart();
  };

  return (
    <section id="combined-booking" className="combined-booking-section">
      <div className="background-container">
        <img src="/images/67f504fdfc00ad2f7d384258d27391b08ef7aabd.png" alt="Abstract background" className="bg-image" />
        <div className="bg-overlay"></div>
      </div>
      
      <div className="combined-container">
        {/* Левая часть - схема мест */}
        <div className="layout-side">
          <h2 className="section-title">Выберите место</h2>
          <div className="layout-content-box">
            <div className="layout-scaler">
              <div className="decorative-vectors">
                <img src="/images/30_51.svg" alt="decorative shape" className="decorative-shape shape-1" />
              </div>
              
              {/* Кликабельные места */}
              {[1, 2, 3, 4, 5, 6].map((num) => (
                <div 
                  key={`pc-${num}`} 
                  className={`pc-setup setup-${num} ${selectedPlace === num ? 'selected' : ''}`}
                  onClick={() => handlePlaceSelect(num)}
                >
                  <img src="/images/6356f02b474a41d638cf709af15fe1f7c6dd92c0.png" alt={`PC setup ${num}`} />
                  <span className="layout-number">{num}</span>
                </div>
              ))}
              
              {[8, 9, 12, 13].map((num) => (
                <div 
                  key={`console-${num}`} 
                  className={`console-setup setup-${num} ${selectedPlace === num ? 'selected' : ''}`}
                  onClick={() => handlePlaceSelect(num)}
                >
                  <img src="/images/1b9fb18a794f8543e1b7ff770153e91c8879c831.png" alt={`Console setup ${num}`} />
                  <span className="layout-number">{num}</span>
                </div>
              ))}
              
              {[10, 11, 14].map((num) => (
                <div 
                  key={`headphones-${num}`} 
                  className={`headphones-setup setup-${num} ${selectedPlace === num ? 'selected' : ''}`}
                  onClick={() => handlePlaceSelect(num)}
                >
                  <img src="/images/2fc05fccb4c07d9e1bb638c4487609fd22b2f1ec.png" alt={`Headphones ${num}`} />
                  <span className="layout-number">{num}</span>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Правая часть - форма бронирования */}
        <div className="booking-side">
          <h2 className="section-title">Бронь места {selectedPlace || ''}</h2>
          
          {/* Информация о корзине */}
          {getTotalItems() > 0 && (
            <div className="cart-info">
              <h3>Корзина ({getTotalItems()} товаров)</h3>
              <div className="cart-items">
                {cartItems.map(item => (
                  <div key={item.id} className="cart-item">
                    <span>{item.name} x{item.quantity}</span>
                    <div className="cart-item-controls">
                      <button 
                        onClick={() => updateCartItemQuantity(item.id, -1)}
                        className="cart-btn"
                      >-</button>
                      <span>{item.quantity}</span>
                      <button 
                        onClick={() => updateCartItemQuantity(item.id, 1)}
                        className="cart-btn"
                      >+</button>
                      <span className="cart-item-price">{item.price * item.quantity} ₽</span>
                    </div>
                  </div>
                ))}
              </div>
              <div className="cart-total">
                Итого: {getTotalPrice()} ₽
              </div>
            </div>
          )}

          <form className="booking-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="place">Выбранное место</label>
              <input 
                type="text" 
                id="place" 
                name="place"
                value={formData.place}
                onChange={handleChange}
                placeholder="Нажмите на место слева"
                readOnly
                className={selectedPlace ? 'selected-place' : ''}
              />
            </div>

            <div className="form-group">
              <label htmlFor="date">Дата бронирования</label>
              <div className="date-input-wrapper">
                <input 
                  type="text" 
                  id="date" 
                  name="date"
                  value={selectedDate}
                  onChange={handleChange}
                  placeholder="Выберите дату"
                  required 
                  readOnly
                  onClick={handleDateFieldClick}
                  className="date-input"
                />
              </div>
            </div>
            
            <div className="form-group time-group">
              <label>Время</label>
              <div className="time-inputs">
                <span>с</span>
                <div className="time-input-wrapper">
                  <input 
                    type="text" 
                    name="timeFrom"
                    value={formData.timeFrom}
                    onChange={handleChange}
                    placeholder="00:00"
                    required 
                    readOnly
                    onClick={() => handleTimeFieldClick('timeFrom')}
                    className="time-input"
                  />
                </div>
                <span>до</span>
                <div className="time-input-wrapper">
                  <input 
                    type="text" 
                    name="timeTo"
                    value={formData.timeTo}
                    onChange={handleChange}
                    placeholder="00:00"
                    required 
                    readOnly
                    onClick={() => handleTimeFieldClick('timeTo')}
                    className="time-input"
                  />
                </div>
              </div>
            </div>
            
            {/* Пicker даты */}
            {showDatePicker && (
              <div className="date-picker-overlay">
                <div className="date-picker">
                  <div className="date-picker-header">
                    <h3>Выберите дату</h3>
                    <button 
                      type="button" 
                      className="date-picker-close"
                      onClick={handleDateCancel}
                    >
                      ×
                    </button>
                  </div>
                  
                  <div className="date-picker-body">
                    <div className="dates-grid">
                      {calendarDates.map((date, index) => (
                        <button
                          key={index}
                          type="button"
                          className={`date-option ${isToday(date) ? 'today' : ''} ${
                            formData.date === date.toISOString().split('T')[0] ? 'selected' : ''
                          }`}
                          onClick={() => handleDateSelect(date)}
                        >
                          <div className="date-day">{getDayName(date)}</div>
                          <div className="date-number">{formatCalendarDate(date)}</div>
                          {isToday(date) && <div className="today-badge">Сегодня</div>}
                        </button>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            )}
            
            {/* Пicker времени */}
            {showTimePicker && (
              <div className="time-picker-overlay">
                <div className="time-picker">
                  <div className="time-picker-header">
                    <h3>Выберите время</h3>
                    <button 
                      type="button" 
                      className="time-picker-close"
                      onClick={handleTimeCancel}
                    >
                      ×
                    </button>
                  </div>
                  
                  <div className="time-picker-body">
                    <div className="time-section">
                      <h4>Часы</h4>
                      <div className="time-grid hours-grid">
                        {hours.map(hour => (
                          <button
                            key={hour}
                            type="button"
                            className={`time-option ${selectedHour === hour ? 'selected' : ''}`}
                            onClick={() => handleHourSelect(hour)}
                          >
                            {hour}
                          </button>
                        ))}
                      </div>
                    </div>
                    
                    <div className="time-section">
                      <h4>Минуты</h4>
                      <div className="time-grid minutes-grid">
                        {minutes.map(minute => (
                          <button
                            key={minute}
                            type="button"
                            className={`time-option ${selectedMinute === minute ? 'selected' : ''}`}
                            onClick={() => handleMinuteSelect(minute)}
                          >
                            {minute}
                          </button>
                        ))}
                      </div>
                    </div>
                  </div>
                  
                  <div className="time-picker-footer">
                    <div className="selected-time-preview">
                      {selectedHour && selectedMinute ? (
                        `Выбрано: ${selectedHour}:${selectedMinute}`
                      ) : (
                        'Выберите час и минуты'
                      )}
                    </div>
                    <div className="time-picker-actions">
                      <button 
                        type="button" 
                        className="btn secondary"
                        onClick={handleTimeCancel}
                      >
                        Отмена
                      </button>
                      <button 
                        type="button" 
                        className="btn primary"
                        onClick={handleTimeConfirm}
                        disabled={!selectedHour || !selectedMinute}
                      >
                        Выбрать
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            )}
            
            <div className="form-group">
              <label htmlFor="address">Адрес клуба</label>
              <select 
                id="address" 
                name="address"
                value={formData.address}
                onChange={handleChange}
                required
              >
                <option value="">Выберите клуб</option>
                <option value="ул. Рахова, 53">ул. Рахова, 53</option>
                <option value="ул. Астраханская, 15/8">ул. Астраханская, 15/8</option>
                <option value="ул. Московская, 11">ул. Московская, 11</option>
              </select>
              {formData.address && (
                <div className="auto-address-indicator">
                  ✓ Адрес автоматически выбран из карточки клуба
                </div>
              )}
            </div>
            
            <div className="booking-actions">
              <button 
                type="submit" 
                className="btn primary"
                disabled={!selectedPlace || !formData.date}
              >
                Забронировать место {selectedPlace || ''}
              </button>
              <a href="#cafe" className="btn secondary">Добавить из кафе</a>
              {getTotalItems() > 0 && (
                <button 
                  type="button" 
                  className="btn secondary" 
                  onClick={handleClearCart}
                >
                  Очистить корзину
                </button>
              )}
            </div>
          </form>
        </div>
      </div>
    </section>
  );
};

export default CombinedLayoutBooking;
