import React, { useState, useEffect } from 'react';
import { apiService } from '../services/Api';
import '../styles/MyBookings.css';

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [payments, setPayments] = useState([]);
  const [clubs, setClubs] = useState([]);
  const [computers, setComputers] = useState([]);
  const [foods, setFoods] = useState([]);
  const [additionalMenu, setAdditionalMenu] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadUserData();
  }, []);

  const loadUserData = async () => {
    try {
      setLoading(true);
      
      // Загружаем все необходимые данные параллельно
      const [
        userBookings,
        userPayments,
        clubsData,
        computersData,
        foodsData,
        additionalMenuData
      ] = await Promise.all([
        apiService.getBookings(),
        apiService.getPayments(),
        apiService.getClubs(),
        apiService.getComputers(),
        apiService.getFoods(),
        // Предполагаем, что есть endpoint для получения дополнительного меню
        apiService.getAdditionalMenu ? apiService.getAdditionalMenu() : Promise.resolve([])
      ]);
      
      console.log('📋 All data loaded:', {
        bookings: userBookings.length,
        payments: userPayments.length,
        clubs: clubsData.length,
        computers: computersData.length,
        foods: foodsData.length,
        additionalMenu: additionalMenuData.length
      });
      
      setBookings(userBookings);
      setPayments(userPayments);
      setClubs(clubsData);
      setComputers(computersData);
      setFoods(foodsData);
      setAdditionalMenu(additionalMenuData);
      
    } catch (err) {
      console.error('Error loading user data:', err);
      setError('Не удалось загрузить данные о бронированиях');
      
      // Fallback: проверяем localStorage
      const lastBooking = localStorage.getItem('lastBooking');
      if (lastBooking) {
        try {
          const booking = JSON.parse(lastBooking);
          if (booking.status && booking.status !== 'draft') {
            setBookings([booking]);
          }
        } catch (e) {
          console.error('Error parsing localStorage booking:', e);
        }
      }
    } finally {
      setLoading(false);
    }
  };

  // Функция для получения информации о клубе
  const getClubInfo = (clubId) => {
    const club = clubs.find(c => c.id === clubId);
    return club || { address: 'Не указан', name: 'Неизвестный клуб' };
  };

  // Функция для получения информации о компьютере
  const getComputerInfo = (computerId) => {
    const computer = computers.find(c => c.id === computerId);
    if (!computer) return null;

    // Находим позицию компьютера
    const position = computer.position_id ? 
      computers.find(c => c.position_id === computer.position_id) : null;

    return {
      id: computer.id,
      name: computer.name || `Компьютер ${computer.id}`,
      price: computer.price,
      processor: computer.processor,
      graphicsCard: computer.graphics_card,
      ram: computer.ram,
      monitor: computer.monitor,
      headphones: computer.headphones,
      keyboard: computer.keyboard,
      mouse: computer.mouse,
      position: position ? `Место ${position.number}` : 'Не указано'
    };
  };

  // Функция для получения заказанных блюд
  const getBookingFoods = (bookingId) => {
    const menuItems = additionalMenu.filter(item => item.booking_id === bookingId);
    return menuItems.map(item => {
      const food = foods.find(f => f.id === item.food_id);
      return {
        ...item,
        food: food || { name: 'Неизвестный продукт', price: 0 }
      };
    });
  };

  // Функция для объединения бронирований с дополнительной информацией
  const getEnhancedBooking = (booking) => {
    const payment = payments.find(p => p.booking_id === booking.id);
    const clubInfo = getClubInfo(booking.club_id);
    const computerInfo = getComputerInfo(booking.computer_id);
    const bookingFoods = getBookingFoods(booking.id);

    return {
      ...booking,
      payment: payment || null,
      clubInfo,
      computerInfo,
      foods: bookingFoods
    };
  };

  const getStatusInfo = (status, paymentStatus) => {
    // Если есть информация о платеже, используем её для определения статуса
    if (paymentStatus) {
      const paymentStatusMap = {
        'pending': { text: 'Ожидает оплаты', color: '#ffc107', icon: '💰' },
        'completed': { text: 'Оплачено', color: '#28a745', icon: '💳' },
        'failed': { text: 'Ошибка оплаты', color: '#dc3545', icon: '❌' },
        'refunded': { text: 'Возврат', color: '#6c757d', icon: '↩️' }
      };
      
      return paymentStatusMap[paymentStatus] || paymentStatusMap.pending;
    }

    // Статусы бронирований
    const statusMap = {
      'draft': { text: 'Черновик', color: '#6c757d', icon: '📝' },
      'pending': { text: 'Ожидает подтверждения', color: '#17a2b8', icon: '⏳' },
      'confirmed': { text: 'Подтверждено', color: '#28a745', icon: '✅' },
      'active': { text: 'Активно', color: '#28a745', icon: '🟢' },
      'completed': { text: 'Завершено', color: '#6c757d', icon: '🏁' },
      'cancelled': { text: 'Отменено', color: '#dc3545', icon: '❌' }
    };
    
    return statusMap[status] || { text: status, color: '#6c757d', icon: '❓' };
  };

  const formatDateTime = (dateTimeString) => {
    if (!dateTimeString) return 'Не указано';
    
    try {
      const date = new Date(dateTimeString);
      return date.toLocaleString('ru-RU', {
        day: 'numeric',
        month: 'long',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        weekday: 'short'
      });
    } catch (e) {
      return dateTimeString;
    }
  };

  const formatPrice = (price) => {
    if (!price) return '0 ₽';
    return new Intl.NumberFormat('ru-RU', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(price) + ' ₽';
  };

  const formatTimeDuration = (minutes) => {
    if (!minutes) return 'Не указано';
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    
    if (hours === 0) return `${mins} мин`;
    if (mins === 0) return `${hours} ч`;
    return `${hours} ч ${mins} мин`;
  };

  const handlePayNow = async (paymentId) => {
    try {
      // Здесь должна быть логика для инициации оплаты
      alert(`Инициирована оплата платежа #${paymentId}`);
      // В реальном приложении здесь был бы переход на страницу платежной системы
    } catch (err) {
      console.error('Error initiating payment:', err);
      alert('Ошибка при инициации оплаты');
    }
  };

  const handleCancelBooking = async (bookingId) => {
    if (window.confirm('Вы уверены, что хотите отменить бронирование?')) {
      try {
        // В реальном приложении здесь был бы API запрос на отмену брони
        // await apiService.cancelBooking(bookingId);
        
        // Пока просто обновляем локальное состояние
        const updatedBookings = bookings.map(booking => 
          booking.id === bookingId 
            ? { ...booking, status: 'cancelled' }
            : booking
        );
        
        setBookings(updatedBookings);
        alert('Бронирование успешно отменено');
      } catch (err) {
        console.error('Error cancelling booking:', err);
        alert('Ошибка при отмене бронирования');
      }
    }
  };

  const handleBackToHome = () => {
    window.location.href = '/';
  };

  const handleRefresh = () => {
    loadUserData();
  };

  if (loading) {
    return (
      <div className="my-bookings-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          Загрузка ваших бронирований...
        </div>
      </div>
    );
  }

  // Объединяем бронирования с дополнительной информацией
  const enhancedBookings = bookings.map(booking => getEnhancedBooking(booking));

  return (
    <section id="my-bookings" className="my-bookings-section">
      <div className="background-container">
        <img src="/images/67f504fdfc00ad2f7d384258d27391b08ef7aabd.png" alt="Abstract background" className="bg-image" />
        <div className="bg-overlay"></div>
      </div>
      
      <div className="my-bookings-container">
        <div className="my-bookings-header">
          <div>
            <h1>📋 Мои бронирования</h1>
            <p className="bookings-count">
              Найдено бронирований: {bookings.length}
            </p>
          </div>
          <div className="header-actions">
            <button onClick={handleRefresh} className="btn secondary">
              🔄 Обновить
            </button>
            <button onClick={handleBackToHome} className="btn secondary">
              На главную
            </button>
          </div>
        </div>

        {error && (
          <div className="error-message">
            ⚠️ {error}
          </div>
        )}

        {bookings.length === 0 ? (
          <div className="no-bookings">
            <div className="no-bookings-icon">📭</div>
            <h2>У вас нет активных бронирований</h2>
            <p>Забронируйте место в компьютерном клубе, чтобы увидеть его здесь</p>
            <button 
              onClick={() => window.location.href = '/booking'} 
              className="btn primary"
            >
              Забронировать место
            </button>
          </div>
        ) : (
          <div className="bookings-list">
            {enhancedBookings.map((booking) => {
              const statusInfo = getStatusInfo(
                booking.status, 
                booking.payment?.status
              );
              
              const isPaymentPending = booking.payment?.status === 'pending';
              const canCancel = ['pending', 'confirmed'].includes(booking.status);
              
              return (
                <div key={booking.id} className="booking-card">
                  <div className="booking-header">
                    <div className="booking-info">
                      <h3>Бронь #{booking.id}</h3>
                      <span 
                        className="booking-status"
                        style={{ backgroundColor: statusInfo.color }}
                      >
                        {statusInfo.icon} {statusInfo.text}
                      </span>
                    </div>
                    <div className="booking-date">
                      Создано: {new Date(booking.created_at).toLocaleDateString('ru-RU')}
                    </div>
                  </div>

                  <div className="booking-details">
                    {/* Основная информация о брони */}
                    <div className="detail-section">
                      <h4>📍 Информация о брони</h4>
                      <div className="detail-grid">
                        <div className="detail-item">
                          <span className="detail-label">🏢 Клуб:</span>
                          <span className="detail-value">
                            {booking.clubInfo.address || `Клуб #${booking.club_id}`}
                          </span>
                        </div>
                        
                        {booking.computerInfo && (
                          <>
                            <div className="detail-item">
                              <span className="detail-label">💻 Компьютер:</span>
                              <span className="detail-value">
                                {booking.computerInfo.name} ({booking.computerInfo.position})
                              </span>
                            </div>
                            
                            <div className="detail-item">
                              <span className="detail-label">⚡ Процессор:</span>
                              <span className="detail-value">
                                {booking.computerInfo.processor || 'Не указан'}
                              </span>
                            </div>
                            
                            <div className="detail-item">
                              <span className="detail-label">🎮 Видеокарта:</span>
                              <span className="detail-value">
                                {booking.computerInfo.graphicsCard || 'Не указана'}
                              </span>
                            </div>
                            
                            <div className="detail-item">
                              <span className="detail-label">💾 Оперативная память:</span>
                              <span className="detail-value">
                                {booking.computerInfo.ram ? `${booking.computerInfo.ram} GB` : 'Не указана'}
                              </span>
                            </div>
                          </>
                        )}
                      </div>
                    </div>

                    {/* Время бронирования */}
                    <div className="detail-section">
                      <h4>🕐 Время бронирования</h4>
                      <div className="detail-grid">
                        <div className="detail-item">
                          <span className="detail-label">Начало:</span>
                          <span className="detail-value">
                            {formatDateTime(booking.start_time)}
                          </span>
                        </div>
                        <div className="detail-item">
                          <span className="detail-label">Окончание:</span>
                          <span className="detail-value">
                            {formatDateTime(booking.end_time)}
                          </span>
                        </div>
                        <div className="detail-item">
                          <span className="detail-label">⏱️ Длительность:</span>
                          <span className="detail-value">
                            {formatTimeDuration(booking.minutes)}
                          </span>
                        </div>
                      </div>
                    </div>

                    {/* Заказ из кафе */}
                    {booking.foods && booking.foods.length > 0 && (
                      <div className="detail-section">
                        <h4>🍽️ Заказ из кафе</h4>
                        <div className="foods-list">
                          {booking.foods.map((item, index) => (
                            <div key={index} className="food-item">
                              <span className="food-name">{item.food.name}</span>
                              <span className="food-quantity">× {item.count}</span>
                              <span className="food-price">
                                {formatPrice(item.food.price * item.count)}
                              </span>
                            </div>
                          ))}
                          <div className="food-total">
                            <span>Итого за еду:</span>
                            <span className="total-amount">
                              {formatPrice(booking.foods.reduce((sum, item) => 
                                sum + (item.food.price * item.count), 0
                              ))}
                            </span>
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Информация о платеже */}
                    {booking.payment && (
                      <div className="detail-section payment-info">
                        <h4>💳 Информация о платеже</h4>
                        <div className="detail-grid">
                          <div className="detail-item">
                            <span className="detail-label">ID платежа:</span>
                            <span className="detail-value">#{booking.payment.id}</span>
                          </div>
                          <div className="detail-item">
                            <span className="detail-label">Тип оплаты:</span>
                            <span className="detail-value">
                              {booking.payment.payment_type === 'card' ? '💳 Карта' : '💵 Наличные'}
                            </span>
                          </div>
                          <div className="detail-item">
                            <span className="detail-label">Дата платежа:</span>
                            <span className="detail-value">
                              {formatDateTime(booking.payment.payment_date)}
                            </span>
                          </div>
                          <div className="detail-item full-width">
                            <span className="detail-label">Хеш платежа:</span>
                            <span className="detail-value small">
                              {booking.payment.payment_hash}
                            </span>
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Итоговая стоимость */}
                    <div className="booking-total">
                      <div className="cost-breakdown">
                        <div className="cost-item">
                          <span>Аренда компьютера:</span>
                          <span>{formatPrice(booking.price_for_pc)}</span>
                        </div>
                        {booking.foods.length > 0 && (
                          <div className="cost-item">
                            <span>Заказ из кафе:</span>
                            <span>{formatPrice(booking.price_for_additions)}</span>
                          </div>
                        )}
                        <div className="cost-total">
                          <span>💰 Итого к оплате:</span>
                          <span className="total-amount">
                            {formatPrice(booking.total_price)}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="booking-actions">
                    {isPaymentPending && (
                      <button 
                        onClick={() => handlePayNow(booking.payment.id)}
                        className="btn primary"
                      >
                        💳 Оплатить сейчас
                      </button>
                    )}
                    
                    {canCancel && (
                      <button 
                        onClick={() => handleCancelBooking(booking.id)}
                        className="btn secondary"
                      >
                        ❌ Отменить бронь
                      </button>
                    )}
                    
                    <button className="btn outline">
                      📞 Связаться с поддержкой
                    </button>

                    <button className="btn outline">
                      📧 Получить квитанцию
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}

        {/* Статистика */}
        {bookings.length > 0 && (
          <div className="bookings-stats">
            <div className="stat-card">
              <span className="stat-number">{bookings.length}</span>
              <span className="stat-label">Всего броней</span>
            </div>
            <div className="stat-card">
              <span className="stat-number">
                {bookings.filter(b => b.status === 'confirmed' || b.status === 'active').length}
              </span>
              <span className="stat-label">Активные</span>
            </div>
            <div className="stat-card">
              <span className="stat-number">
                {payments.filter(p => p.status === 'pending').length}
              </span>
              <span className="stat-label">Ожидают оплаты</span>
            </div>
            <div className="stat-card">
              <span className="stat-number">
                {payments.filter(p => p.status === 'completed').length}
              </span>
              <span className="stat-label">Оплачено</span>
            </div>
          </div>
        )}

        {/* Отладочная информация */}
        <div className="debug-info" style={{
          background: 'rgba(255,255,255,0.1)', 
          padding: '15px', 
          borderRadius: '8px', 
          marginTop: '30px',
          color: 'white',
          fontSize: '12px'
        }}>
          <strong>Отладочная информация:</strong><br/>
          - Загружено бронирований: {bookings.length}<br/>
          - Загружено платежей: {payments.length}<br/>
          - Загружено клубов: {clubs.length}<br/>
          - Загружено компьютеров: {computers.length}<br/>
          - Загружено блюд: {foods.length}<br/>
          - Загружено доп. меню: {additionalMenu.length}
        </div>
      </div>
    </section>
  );
};

export default MyBookings;
