import React, { useState, useEffect } from 'react';
import { apiService } from '../services/Api';
import '../styles/MyBookings.css';

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [payments, setPayments] = useState([]);
  const [clubs, setClubs] = useState([]);
  const [computers, setComputers] = useState([]);
  const [foods, setFoods] = useState([]);
  const [additionalMenu, setAdditionalMenu] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadUserData();
  }, []);

  const loadUserData = async () => {
    try {
      setLoading(true);
      
      // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð²ÑÐµ Ð½ÐµÐ¾Ð±Ñ…Ð¾Ð´Ð¸Ð¼Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¿Ð°Ñ€Ð°Ð»Ð»ÐµÐ»ÑŒÐ½Ð¾
      const [
        userBookings,
        userPayments,
        clubsData,
        computersData,
        foodsData,
        additionalMenuData
      ] = await Promise.all([
        apiService.getBookings(),
        apiService.getPayments(),
        apiService.getClubs(),
        apiService.getComputers(),
        apiService.getFoods(),
        // ÐŸÑ€ÐµÐ´Ð¿Ð¾Ð»Ð°Ð³Ð°ÐµÐ¼, Ñ‡Ñ‚Ð¾ ÐµÑÑ‚ÑŒ endpoint Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð´Ð¾Ð¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð¾Ð³Ð¾ Ð¼ÐµÐ½ÑŽ
        apiService.getAdditionalMenu ? apiService.getAdditionalMenu() : Promise.resolve([])
      ]);
      
      console.log('ðŸ“‹ All data loaded:', {
        bookings: userBookings.length,
        payments: userPayments.length,
        clubs: clubsData.length,
        computers: computersData.length,
        foods: foodsData.length,
        additionalMenu: additionalMenuData.length
      });
      
      setBookings(userBookings);
      setPayments(userPayments);
      setClubs(clubsData);
      setComputers(computersData);
      setFoods(foodsData);
      setAdditionalMenu(additionalMenuData);
      
    } catch (err) {
      console.error('Error loading user data:', err);
      setError('ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ Ð·Ð°Ð³Ñ€ÑƒÐ·Ð¸Ñ‚ÑŒ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¾ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸ÑÑ…');
      
      // Fallback: Ð¿Ñ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ localStorage
      const lastBooking = localStorage.getItem('lastBooking');
      if (lastBooking) {
        try {
          const booking = JSON.parse(lastBooking);
          if (booking.status && booking.status !== 'draft') {
            setBookings([booking]);
          }
        } catch (e) {
          console.error('Error parsing localStorage booking:', e);
        }
      }
    } finally {
      setLoading(false);
    }
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ð¸ Ð¾ ÐºÐ»ÑƒÐ±Ðµ
  const getClubInfo = (clubId) => {
    const club = clubs.find(c => c.id === clubId);
    return club || { address: 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½', name: 'ÐÐµÐ¸Ð·Ð²ÐµÑÑ‚Ð½Ñ‹Ð¹ ÐºÐ»ÑƒÐ±' };
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ð¸ Ð¾ ÐºÐ¾Ð¼Ð¿ÑŒÑŽÑ‚ÐµÑ€Ðµ
  const getComputerInfo = (computerId) => {
    const computer = computers.find(c => c.id === computerId);
    if (!computer) return null;

    // ÐÐ°Ñ…Ð¾Ð´Ð¸Ð¼ Ð¿Ð¾Ð·Ð¸Ñ†Ð¸ÑŽ ÐºÐ¾Ð¼Ð¿ÑŒÑŽÑ‚ÐµÑ€Ð°
    const position = computer.position_id ? 
      computers.find(c => c.position_id === computer.position_id) : null;

    return {
      id: computer.id,
      name: computer.name || `ÐšÐ¾Ð¼Ð¿ÑŒÑŽÑ‚ÐµÑ€ ${computer.id}`,
      price: computer.price,
      processor: computer.processor,
      graphicsCard: computer.graphics_card,
      ram: computer.ram,
      monitor: computer.monitor,
      headphones: computer.headphones,
      keyboard: computer.keyboard,
      mouse: computer.mouse,
      position: position ? `ÐœÐµÑÑ‚Ð¾ ${position.number}` : 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½Ð¾'
    };
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð·Ð°ÐºÐ°Ð·Ð°Ð½Ð½Ñ‹Ñ… Ð±Ð»ÑŽÐ´
  const getBookingFoods = (bookingId) => {
    const menuItems = additionalMenu.filter(item => item.booking_id === bookingId);
    return menuItems.map(item => {
      const food = foods.find(f => f.id === item.food_id);
      return {
        ...item,
        food: food || { name: 'ÐÐµÐ¸Ð·Ð²ÐµÑÑ‚Ð½Ñ‹Ð¹ Ð¿Ñ€Ð¾Ð´ÑƒÐºÑ‚', price: 0 }
      };
    });
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¾Ð±ÑŠÐµÐ´Ð¸Ð½ÐµÐ½Ð¸Ñ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¹ Ñ Ð´Ð¾Ð¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð¾Ð¹ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸ÐµÐ¹
  const getEnhancedBooking = (booking) => {
    const payment = payments.find(p => p.booking_id === booking.id);
    const clubInfo = getClubInfo(booking.club_id);
    const computerInfo = getComputerInfo(booking.computer_id);
    const bookingFoods = getBookingFoods(booking.id);

    return {
      ...booking,
      payment: payment || null,
      clubInfo,
      computerInfo,
      foods: bookingFoods
    };
  };

  const getStatusInfo = (status, paymentStatus) => {
    // Ð•ÑÐ»Ð¸ ÐµÑÑ‚ÑŒ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ Ð¾ Ð¿Ð»Ð°Ñ‚ÐµÐ¶Ðµ, Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ ÐµÑ‘ Ð´Ð»Ñ Ð¾Ð¿Ñ€ÐµÐ´ÐµÐ»ÐµÐ½Ð¸Ñ ÑÑ‚Ð°Ñ‚ÑƒÑÐ°
    if (paymentStatus) {
      const paymentStatusMap = {
        'pending': { text: 'ÐžÐ¶Ð¸Ð´Ð°ÐµÑ‚ Ð¾Ð¿Ð»Ð°Ñ‚Ñ‹', color: '#ffc107', icon: 'ðŸ’°' },
        'completed': { text: 'ÐžÐ¿Ð»Ð°Ñ‡ÐµÐ½Ð¾', color: '#28a745', icon: 'ðŸ’³' },
        'failed': { text: 'ÐžÑˆÐ¸Ð±ÐºÐ° Ð¾Ð¿Ð»Ð°Ñ‚Ñ‹', color: '#dc3545', icon: 'âŒ' },
        'refunded': { text: 'Ð’Ð¾Ð·Ð²Ñ€Ð°Ñ‚', color: '#6c757d', icon: 'â†©ï¸' }
      };
      
      return paymentStatusMap[paymentStatus] || paymentStatusMap.pending;
    }

    // Ð¡Ñ‚Ð°Ñ‚ÑƒÑÑ‹ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¹
    const statusMap = {
      'draft': { text: 'Ð§ÐµÑ€Ð½Ð¾Ð²Ð¸Ðº', color: '#6c757d', icon: 'ðŸ“' },
      'pending': { text: 'ÐžÐ¶Ð¸Ð´Ð°ÐµÑ‚ Ð¿Ð¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð¸Ñ', color: '#17a2b8', icon: 'â³' },
      'confirmed': { text: 'ÐŸÐ¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð¾', color: '#28a745', icon: 'âœ…' },
      'active': { text: 'ÐÐºÑ‚Ð¸Ð²Ð½Ð¾', color: '#28a745', icon: 'ðŸŸ¢' },
      'completed': { text: 'Ð—Ð°Ð²ÐµÑ€ÑˆÐµÐ½Ð¾', color: '#6c757d', icon: 'ðŸ' },
      'cancelled': { text: 'ÐžÑ‚Ð¼ÐµÐ½ÐµÐ½Ð¾', color: '#dc3545', icon: 'âŒ' }
    };
    
    return statusMap[status] || { text: status, color: '#6c757d', icon: 'â“' };
  };

  const formatDateTime = (dateTimeString) => {
    if (!dateTimeString) return 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½Ð¾';
    
    try {
      const date = new Date(dateTimeString);
      return date.toLocaleString('ru-RU', {
        day: 'numeric',
        month: 'long',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        weekday: 'short'
      });
    } catch (e) {
      return dateTimeString;
    }
  };

  const formatPrice = (price) => {
    if (!price) return '0 â‚½';
    return new Intl.NumberFormat('ru-RU', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(price) + ' â‚½';
  };

  const formatTimeDuration = (minutes) => {
    if (!minutes) return 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½Ð¾';
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    
    if (hours === 0) return `${mins} Ð¼Ð¸Ð½`;
    if (mins === 0) return `${hours} Ñ‡`;
    return `${hours} Ñ‡ ${mins} Ð¼Ð¸Ð½`;
  };

  const handlePayNow = async (paymentId) => {
    try {
      // Ð—Ð´ÐµÑÑŒ Ð´Ð¾Ð»Ð¶Ð½Ð° Ð±Ñ‹Ñ‚ÑŒ Ð»Ð¾Ð³Ð¸ÐºÐ° Ð´Ð»Ñ Ð¸Ð½Ð¸Ñ†Ð¸Ð°Ñ†Ð¸Ð¸ Ð¾Ð¿Ð»Ð°Ñ‚Ñ‹
      alert(`Ð˜Ð½Ð¸Ñ†Ð¸Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð° Ð¾Ð¿Ð»Ð°Ñ‚Ð° Ð¿Ð»Ð°Ñ‚ÐµÐ¶Ð° #${paymentId}`);
      // Ð’ Ñ€ÐµÐ°Ð»ÑŒÐ½Ð¾Ð¼ Ð¿Ñ€Ð¸Ð»Ð¾Ð¶ÐµÐ½Ð¸Ð¸ Ð·Ð´ÐµÑÑŒ Ð±Ñ‹Ð» Ð±Ñ‹ Ð¿ÐµÑ€ÐµÑ…Ð¾Ð´ Ð½Ð° ÑÑ‚Ñ€Ð°Ð½Ð¸Ñ†Ñƒ Ð¿Ð»Ð°Ñ‚ÐµÐ¶Ð½Ð¾Ð¹ ÑÐ¸ÑÑ‚ÐµÐ¼Ñ‹
    } catch (err) {
      console.error('Error initiating payment:', err);
      alert('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¸Ð½Ð¸Ñ†Ð¸Ð°Ñ†Ð¸Ð¸ Ð¾Ð¿Ð»Ð°Ñ‚Ñ‹');
    }
  };

  const handleCancelBooking = async (bookingId) => {
    if (window.confirm('Ð’Ñ‹ ÑƒÐ²ÐµÑ€ÐµÐ½Ñ‹, Ñ‡Ñ‚Ð¾ Ñ…Ð¾Ñ‚Ð¸Ñ‚Ðµ Ð¾Ñ‚Ð¼ÐµÐ½Ð¸Ñ‚ÑŒ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ?')) {
      try {
        // Ð’ Ñ€ÐµÐ°Ð»ÑŒÐ½Ð¾Ð¼ Ð¿Ñ€Ð¸Ð»Ð¾Ð¶ÐµÐ½Ð¸Ð¸ Ð·Ð´ÐµÑÑŒ Ð±Ñ‹Ð» Ð±Ñ‹ API Ð·Ð°Ð¿Ñ€Ð¾Ñ Ð½Ð° Ð¾Ñ‚Ð¼ÐµÐ½Ñƒ Ð±Ñ€Ð¾Ð½Ð¸
        // await apiService.cancelBooking(bookingId);
        
        // ÐŸÐ¾ÐºÐ° Ð¿Ñ€Ð¾ÑÑ‚Ð¾ Ð¾Ð±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ Ð»Ð¾ÐºÐ°Ð»ÑŒÐ½Ð¾Ðµ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ
        const updatedBookings = bookings.map(booking => 
          booking.id === bookingId 
            ? { ...booking, status: 'cancelled' }
            : booking
        );
        
        setBookings(updatedBookings);
        alert('Ð‘Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ Ð¾Ñ‚Ð¼ÐµÐ½ÐµÐ½Ð¾');
      } catch (err) {
        console.error('Error cancelling booking:', err);
        alert('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¾Ñ‚Ð¼ÐµÐ½Ðµ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ');
      }
    }
  };

  const handleBackToHome = () => {
    window.location.href = '/';
  };

  const handleRefresh = () => {
    loadUserData();
  };

  if (loading) {
    return (
      <div className="my-bookings-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ð²Ð°ÑˆÐ¸Ñ… Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¹...
        </div>
      </div>
    );
  }

  // ÐžÐ±ÑŠÐµÐ´Ð¸Ð½ÑÐµÐ¼ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ Ñ Ð´Ð¾Ð¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð¾Ð¹ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸ÐµÐ¹
  const enhancedBookings = bookings.map(booking => getEnhancedBooking(booking));

  return (
    <section id="my-bookings" className="my-bookings-section">
      <div className="background-container">
        <img src="/images/67f504fdfc00ad2f7d384258d27391b08ef7aabd.png" alt="Abstract background" className="bg-image" />
        <div className="bg-overlay"></div>
      </div>
      
      <div className="my-bookings-container">
        <div className="my-bookings-header">
          <div>
            <h1>ðŸ“‹ ÐœÐ¾Ð¸ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ</h1>
            <p className="bookings-count">
              ÐÐ°Ð¹Ð´ÐµÐ½Ð¾ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¹: {bookings.length}
            </p>
          </div>
          <div className="header-actions">
            <button onClick={handleRefresh} className="btn secondary">
              ðŸ”„ ÐžÐ±Ð½Ð¾Ð²Ð¸Ñ‚ÑŒ
            </button>
            <button onClick={handleBackToHome} className="btn secondary">
              ÐÐ° Ð³Ð»Ð°Ð²Ð½ÑƒÑŽ
            </button>
          </div>
        </div>

        {error && (
          <div className="error-message">
            âš ï¸ {error}
          </div>
        )}

        {bookings.length === 0 ? (
          <div className="no-bookings">
            <div className="no-bookings-icon">ðŸ“­</div>
            <h2>Ð£ Ð²Ð°Ñ Ð½ÐµÑ‚ Ð°ÐºÑ‚Ð¸Ð²Ð½Ñ‹Ñ… Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¹</h2>
            <p>Ð—Ð°Ð±Ñ€Ð¾Ð½Ð¸Ñ€ÑƒÐ¹Ñ‚Ðµ Ð¼ÐµÑÑ‚Ð¾ Ð² ÐºÐ¾Ð¼Ð¿ÑŒÑŽÑ‚ÐµÑ€Ð½Ð¾Ð¼ ÐºÐ»ÑƒÐ±Ðµ, Ñ‡Ñ‚Ð¾Ð±Ñ‹ ÑƒÐ²Ð¸Ð´ÐµÑ‚ÑŒ ÐµÐ³Ð¾ Ð·Ð´ÐµÑÑŒ</p>
            <button 
              onClick={() => window.location.href = '/booking'} 
              className="btn primary"
            >
              Ð—Ð°Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ñ‚ÑŒ Ð¼ÐµÑÑ‚Ð¾
            </button>
          </div>
        ) : (
          <div className="bookings-list">
            {enhancedBookings.map((booking) => {
              const statusInfo = getStatusInfo(
                booking.status, 
                booking.payment?.status
              );
              
              const isPaymentPending = booking.payment?.status === 'pending';
              const canCancel = ['pending', 'confirmed'].includes(booking.status);
              
              return (
                <div key={booking.id} className="booking-card">
                  <div className="booking-header">
                    <div className="booking-info">
                      <h3>Ð‘Ñ€Ð¾Ð½ÑŒ #{booking.id}</h3>
                      <span 
                        className="booking-status"
                        style={{ backgroundColor: statusInfo.color }}
                      >
                        {statusInfo.icon} {statusInfo.text}
                      </span>
                    </div>
                    <div className="booking-date">
                      Ð¡Ð¾Ð·Ð´Ð°Ð½Ð¾: {new Date(booking.created_at).toLocaleDateString('ru-RU')}
                    </div>
                  </div>

                  <div className="booking-details">
                    {/* ÐžÑÐ½Ð¾Ð²Ð½Ð°Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ Ð¾ Ð±Ñ€Ð¾Ð½Ð¸ */}
                    <div className="detail-section">
                      <h4>ðŸ“ Ð˜Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ Ð¾ Ð±Ñ€Ð¾Ð½Ð¸</h4>
                      <div className="detail-grid">
                        <div className="detail-item">
                          <span className="detail-label">ðŸ¢ ÐšÐ»ÑƒÐ±:</span>
                          <span className="detail-value">
                            {booking.clubInfo.address || `ÐšÐ»ÑƒÐ± #${booking.club_id}`}
                          </span>
                        </div>
                        
                        {booking.computerInfo && (
                          <>
                            <div className="detail-item">
                              <span className="detail-label">ðŸ’» ÐšÐ¾Ð¼Ð¿ÑŒÑŽÑ‚ÐµÑ€:</span>
                              <span className="detail-value">
                                {booking.computerInfo.name} ({booking.computerInfo.position})
                              </span>
                            </div>
                            
                            <div className="detail-item">
                              <span className="detail-label">âš¡ ÐŸÑ€Ð¾Ñ†ÐµÑÑÐ¾Ñ€:</span>
                              <span className="detail-value">
                                {booking.computerInfo.processor || 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½'}
                              </span>
                            </div>
                            
                            <div className="detail-item">
                              <span className="detail-label">ðŸŽ® Ð’Ð¸Ð´ÐµÐ¾ÐºÐ°Ñ€Ñ‚Ð°:</span>
                              <span className="detail-value">
                                {booking.computerInfo.graphicsCard || 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½Ð°'}
                              </span>
                            </div>
                            
                            <div className="detail-item">
                              <span className="detail-label">ðŸ’¾ ÐžÐ¿ÐµÑ€Ð°Ñ‚Ð¸Ð²Ð½Ð°Ñ Ð¿Ð°Ð¼ÑÑ‚ÑŒ:</span>
                              <span className="detail-value">
                                {booking.computerInfo.ram ? `${booking.computerInfo.ram} GB` : 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½Ð°'}
                              </span>
                            </div>
                          </>
                        )}
                      </div>
                    </div>

                    {/* Ð’Ñ€ÐµÐ¼Ñ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ */}
                    <div className="detail-section">
                      <h4>ðŸ• Ð’Ñ€ÐµÐ¼Ñ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ</h4>
                      <div className="detail-grid">
                        <div className="detail-item">
                          <span className="detail-label">ÐÐ°Ñ‡Ð°Ð»Ð¾:</span>
                          <span className="detail-value">
                            {formatDateTime(booking.start_time)}
                          </span>
                        </div>
                        <div className="detail-item">
                          <span className="detail-label">ÐžÐºÐ¾Ð½Ñ‡Ð°Ð½Ð¸Ðµ:</span>
                          <span className="detail-value">
                            {formatDateTime(booking.end_time)}
                          </span>
                        </div>
                        <div className="detail-item">
                          <span className="detail-label">â±ï¸ Ð”Ð»Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð¾ÑÑ‚ÑŒ:</span>
                          <span className="detail-value">
                            {formatTimeDuration(booking.minutes)}
                          </span>
                        </div>
                      </div>
                    </div>

                    {/* Ð—Ð°ÐºÐ°Ð· Ð¸Ð· ÐºÐ°Ñ„Ðµ */}
                    {booking.foods && booking.foods.length > 0 && (
                      <div className="detail-section">
                        <h4>ðŸ½ï¸ Ð—Ð°ÐºÐ°Ð· Ð¸Ð· ÐºÐ°Ñ„Ðµ</h4>
                        <div className="foods-list">
                          {booking.foods.map((item, index) => (
                            <div key={index} className="food-item">
                              <span className="food-name">{item.food.name}</span>
                              <span className="food-quantity">Ã— {item.count}</span>
                              <span className="food-price">
                                {formatPrice(item.food.price * item.count)}
                              </span>
                            </div>
                          ))}
                          <div className="food-total">
                            <span>Ð˜Ñ‚Ð¾Ð³Ð¾ Ð·Ð° ÐµÐ´Ñƒ:</span>
                            <span className="total-amount">
                              {formatPrice(booking.foods.reduce((sum, item) => 
                                sum + (item.food.price * item.count), 0
                              ))}
                            </span>
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Ð˜Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ Ð¾ Ð¿Ð»Ð°Ñ‚ÐµÐ¶Ðµ */}
                    {booking.payment && (
                      <div className="detail-section payment-info">
                        <h4>ðŸ’³ Ð˜Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ Ð¾ Ð¿Ð»Ð°Ñ‚ÐµÐ¶Ðµ</h4>
                        <div className="detail-grid">
                          <div className="detail-item">
                            <span className="detail-label">ID Ð¿Ð»Ð°Ñ‚ÐµÐ¶Ð°:</span>
                            <span className="detail-value">#{booking.payment.id}</span>
                          </div>
                          <div className="detail-item">
                            <span className="detail-label">Ð¢Ð¸Ð¿ Ð¾Ð¿Ð»Ð°Ñ‚Ñ‹:</span>
                            <span className="detail-value">
                              {booking.payment.payment_type === 'card' ? 'ðŸ’³ ÐšÐ°Ñ€Ñ‚Ð°' : 'ðŸ’µ ÐÐ°Ð»Ð¸Ñ‡Ð½Ñ‹Ðµ'}
                            </span>
                          </div>
                          <div className="detail-item">
                            <span className="detail-label">Ð”Ð°Ñ‚Ð° Ð¿Ð»Ð°Ñ‚ÐµÐ¶Ð°:</span>
                            <span className="detail-value">
                              {formatDateTime(booking.payment.payment_date)}
                            </span>
                          </div>
                          <div className="detail-item full-width">
                            <span className="detail-label">Ð¥ÐµÑˆ Ð¿Ð»Ð°Ñ‚ÐµÐ¶Ð°:</span>
                            <span className="detail-value small">
                              {booking.payment.payment_hash}
                            </span>
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Ð˜Ñ‚Ð¾Ð³Ð¾Ð²Ð°Ñ ÑÑ‚Ð¾Ð¸Ð¼Ð¾ÑÑ‚ÑŒ */}
                    <div className="booking-total">
                      <div className="cost-breakdown">
                        <div className="cost-item">
                          <span>ÐÑ€ÐµÐ½Ð´Ð° ÐºÐ¾Ð¼Ð¿ÑŒÑŽÑ‚ÐµÑ€Ð°:</span>
                          <span>{formatPrice(booking.price_for_pc)}</span>
                        </div>
                        {booking.foods.length > 0 && (
                          <div className="cost-item">
                            <span>Ð—Ð°ÐºÐ°Ð· Ð¸Ð· ÐºÐ°Ñ„Ðµ:</span>
                            <span>{formatPrice(booking.price_for_additions)}</span>
                          </div>
                        )}
                        <div className="cost-total">
                          <span>ðŸ’° Ð˜Ñ‚Ð¾Ð³Ð¾ Ðº Ð¾Ð¿Ð»Ð°Ñ‚Ðµ:</span>
                          <span className="total-amount">
                            {formatPrice(booking.total_price)}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="booking-actions">
                    {isPaymentPending && (
                      <button 
                        onClick={() => handlePayNow(booking.payment.id)}
                        className="btn primary"
                      >
                        ðŸ’³ ÐžÐ¿Ð»Ð°Ñ‚Ð¸Ñ‚ÑŒ ÑÐµÐ¹Ñ‡Ð°Ñ
                      </button>
                    )}
                    
