import React, { useState } from 'react';
import '../styles/Cafe.css';

const Cafe = () => {
  const [menuItems, setMenuItems] = useState([
    { id: 1, name: 'Попкорн', price: 200, quantity: 1 },
    { id: 2, name: 'Попкорн', price: 200, quantity: 1 },
    { id: 3, name: 'Попкорн', price: 200, quantity: 1 },
    { id: 4, name: 'Попкорн', price: 200, quantity: 1 }
  ]);

  const updateQuantity = (id, change) => {
    setMenuItems(prev => prev.map(item => 
      item.id === id 
        ? { ...item, quantity: Math.max(0, item.quantity + change) }
        : item
    ));
  };

  return (
    <section id="cafe" className="cafe-section">
      <div className="container">
        <h2 className="cafe-title">Кафе</h2>
        <div className="menu-list">
          {menuItems.map(item => (
            <div key={item.id} className="menu-item">
              <span className="item-name">{item.name}</span>
              <div className="item-divider"></div>
              <span className="item-price">{item.price} ₽</span>
              <img src="/images/3e0eb51712839c94e484c5509d644d05cd1ee6f5.png" alt="cart icon" className="cart-icon" />
              <div className="quantity-stepper">
                <button 
                  className="stepper-btn"
                  onClick={() => updateQuantity(item.id, -1)}
                >-</button>
                <span className="quantity">{item.quantity}</span>
                <button 
                  className="stepper-btn"
                  onClick={() => updateQuantity(item.id, 1)}
                >+</button>
              </div>
            </div>
          ))}
        </div>
        <div className="cafe-actions">
          <a href="#pricing" className="btn">Назад</a>
          <a href="#booking" className="btn">Перейти к брони</a>
          <a href="#cart" className="btn">Перейти в корзину</a>
        </div>
      </div>
    </section>
  );
};

export default Cafe;
