import React, { useState, useEffect } from 'react';
import { apiService } from '../services/Api';
import '../styles/MyBookings.css';

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [payments, setPayments] = useState([]);
  const [clubs, setClubs] = useState([]);
  const [computers, setComputers] = useState([]);
  const [foods, setFoods] = useState([]);
  const [additionalMenu, setAdditionalMenu] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filterStatus, setFilterStatus] = useState('all'); // –§–∏–ª—å—Ç—Ä –ø–æ —Å—Ç–∞—Ç—É—Å—É
  const [deletingBookingId, setDeletingBookingId] = useState(null); // ID —É–¥–∞–ª—è–µ–º–æ–π –±—Ä–æ–Ω–∏

  useEffect(() => {
    loadUserData();
  }, []);

  const loadUserData = async () => {
    try {
      setLoading(true);
      
      console.log('üîÑ Starting to load user data...');
      
      // –ó–∞–≥—Ä—É–∂–∞–µ–º –≤—Å–µ –Ω–µ–æ–±—Ö–æ–¥–∏–º—ã–µ –¥–∞–Ω–Ω—ã–µ
      const userBookings = await apiService.getBookings();
      console.log('üìã Bookings loaded:', userBookings);
      
      const userPayments = await apiService.getPayments();
