import React, { useState, useEffect } from 'react';
import { apiService } from '../services/Api';
import '../styles/BookingConfirmation.css';

const BookingConfirmation = () => {
  const [bookingData, setBookingData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [processingPayment, setProcessingPayment] = useState(false);
  const [promoCode, setPromoCode] = useState('');
  const [appliedPromoCode, setAppliedPromoCode] = useState(null);
  const [tariffData, setTariffData] = useState(null);
  const [computers, setComputers] = useState([]);
  const [foods, setFoods] = useState([]);
  const [clubs, setClubs] = useState([]);

  // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¸Ð· localStorage Ð¿Ñ€Ð¸ Ð¼Ð¾Ð½Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¸
  useEffect(() => {
    const loadBookingData = () => {
      try {
        const savedData = localStorage.getItem('lastBooking');
        console.log('ðŸ“¦ Loading booking data from localStorage:', savedData);
        
        if (savedData) {
          const parsedData = JSON.parse(savedData);
          setBookingData(parsedData);
          console.log('âœ… Booking data loaded successfully');
        } else {
          console.warn('âŒ No booking data found in localStorage');
        }
      } catch (error) {
        console.error('âŒ Error loading booking data:', error);
      } finally {
        setLoading(false);
      }
    };

    loadBookingData();
  }, []);

  // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð´Ð¾Ð¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ñ API
  useEffect(() => {
    const loadAdditionalData = async () => {
      if (!bookingData) return;
      
      try {
        console.log('ðŸ”„ Loading additional data from API...');
        
        // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð²ÑÐµ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¿Ð°Ñ€Ð°Ð»Ð»ÐµÐ»ÑŒÐ½Ð¾
        const [foodsData, computersData, clubsData] = await Promise.all([
          apiService.getFoods().catch(error => {
            console.error('âŒ Error loading foods:', error);
            return [];
          }),
          apiService.getComputers().catch(error => {
            console.error('âŒ Error loading computers:', error);
            return [];
          }),
          apiService.getClubs().catch(error => {
            console.error('âŒ Error loading clubs:', error);
            return [];
          })
        ]);
        
        console.log('ðŸ“Š API Data loaded:', {
          foods: foodsData.length,
          computers: computersData.length,
          clubs: clubsData.length
        });
        
        setFoods(foodsData);
        setComputers(computersData);
        setClubs(clubsData);
        
        // ÐŸÑ€Ð¾Ð±ÑƒÐµÐ¼ Ð·Ð°Ð³Ñ€ÑƒÐ·Ð¸Ñ‚ÑŒ Ñ‚Ð°Ñ€Ð¸Ñ„
        try {
          // ÐŸÑ€Ð¾Ð±ÑƒÐµÐ¼ Ñ€Ð°Ð·Ð½Ñ‹Ðµ Ð²Ð°Ñ€Ð¸Ð°Ð½Ñ‚Ñ‹ endpoint'Ð¾Ð² Ð´Ð»Ñ Ñ‚Ð°Ñ€Ð¸Ñ„Ð°
          let tariff;
          try {
            tariff = await apiService.getTariff(1);
          } catch (e) {
            console.log('âš ï¸ Trying alternative tariff endpoint...');
            tariff = await apiService.request('/tariffs/1');
          }
          console.log('ðŸ’° Tariff data loaded:', tariff);
          setTariffData(tariff);
        } catch (tariffError) {
          console.error('âŒ Error loading tariff:', tariffError);
          // Ð˜ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ Ð´ÐµÑ„Ð¾Ð»Ñ‚Ð½Ñ‹Ð¹ ÐºÐ¾ÑÑ„Ñ„Ð¸Ñ†Ð¸ÐµÐ½Ñ‚
          setTariffData({ coefficient: 1 });
        }
        
      } catch (error) {
        console.error('âŒ Error loading additional data:', error);
      }
    };

    loadAdditionalData();
  }, [bookingData]);

  // ÐœÐµÑ‚Ð¾Ð´ Ð´Ð»Ñ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ñ Ð»Ð¾ÐºÐ°Ð»ÑŒÐ½Ñ‹Ñ… Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð¾ ÐµÐ´Ðµ
  const saveLocalFoodItems = (bookingId, foodItems) => {
    try {
      const localFoodData = {
        bookingId: bookingId,
        items: foodItems,
        timestamp: new Date().toISOString()
      };
      
      // Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ð² localStorage
      localStorage.setItem(`local_food_${bookingId}`, JSON.stringify(localFoodData));
      console.log('ðŸ’¾ Local food data saved:', localFoodData);
    } catch (error) {
      console.error('âŒ Error saving local food data:', error);
    }
  };

  // Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ ÑÐ²Ð¾Ð´ÐºÑƒ Ð·Ð°ÐºÐ°Ð·Ð° ÐµÐ´Ñ‹
  const saveFoodOrderSummary = (bookingId, cartItems, totalPrice) => {
    try {
      const orderSummary = {
        bookingId: bookingId,
        items: cartItems.map(item => ({
          name: item.name,
          food_id: item.id,
          quantity: item.quantity,
          price: item.price
        })),
        total: totalPrice,
        timestamp: new Date().toISOString()
      };
      
      localStorage.setItem(`food_order_${bookingId}`, JSON.stringify(orderSummary));
      console.log('ðŸ“‹ Food order summary saved for manual processing:', orderSummary);
    } catch (error) {
      console.error('âŒ Error saving food order summary:', error);
    }
  };

  // Ð˜ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¸Ð· ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ
  const data = bookingData || {};

  const {
    formData = {},
    selectedPlace,
    placeRate,
    cartItems = [],
    totalPrice = 0,
    calculatedData = {}
  } = data;

  // Ð”ÐµÑÑ‚Ñ€ÑƒÐºÑ‚ÑƒÑ€Ð¸Ñ€ÑƒÐµÐ¼ formData Ñ Ð·Ð½Ð°Ñ‡ÐµÐ½Ð¸ÑÐ¼Ð¸ Ð¿Ð¾ ÑƒÐ¼Ð¾Ð»Ñ‡Ð°Ð½Ð¸ÑŽ
  const {
    place = '',
    dateFrom = '',
    timeFrom = '',
    dateTo = '',
    timeTo = '',
    address = '',
    computer_id = '',
    club_id = ''
  } = formData;

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ð¸ Ð¾ ÐºÐ¾Ð¼Ð¿ÑŒÑŽÑ‚ÐµÑ€Ðµ
  const getComputerInfo = (computerId) => {
    if (!computerId) return null;
    
    const computer = computers.find(c => c.id == computerId);
    if (!computer) {
      console.log(`âŒ Computer not found for ID: ${computerId}`);
      console.log('Available computers:', computers.map(c => ({ id: c.id, name: c.name })));
      return null;
    }

    return {
      id: computer.id,
      name: computer.name || `ÐšÐ¾Ð¼Ð¿ÑŒÑŽÑ‚ÐµÑ€ ${computer.id}`,
      price: computer.price,
      processor: computer.processor,
      graphicsCard: computer.graphics_card,
      ram: computer.ram,
      monitor: computer.monitor,
      headphones: computer.headphones,
      keyboard: computer.keyboard,
      mouse: computer.mouse
    };
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ð¸ Ð¾ ÐºÐ»ÑƒÐ±Ðµ
  const getClubInfo = (clubId) => {
    if (!clubId) return { address: 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½', name: 'ÐÐµÐ¸Ð·Ð²ÐµÑÑ‚Ð½Ñ‹Ð¹ ÐºÐ»ÑƒÐ±' };
    
    const club = clubs.find(c => c.id == clubId);
