// PlaceDetails.js - –í–ï–†–°–ò–Ø –° –ò–ù–¢–ï–ì–†–ê–¶–ò–ï–ô –ë–≠–ö–ê
import React, { useState, useEffect } from 'react';
import { apiService } from '../services/Api';
import '../styles/PlaceDetails.css';

const PlaceDetails = ({ place, onBack, onSelect, positionInfo }) => {
  const [computerDetails, setComputerDetails] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // –ó–∞–≥—Ä—É–∑–∫–∞ –¥–µ—Ç–∞–ª—å–Ω–æ–π –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ –∫–æ–º–ø—å—é—Ç–µ—Ä–µ
  useEffect(() => {
    const loadComputerDetails = async () => {
      // –ï—Å–ª–∏ —É–∂–µ –µ—Å—Ç—å –ø—Ä–µ–¥–∑–∞–≥—Ä—É–∂–µ–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ, –∏—Å–ø–æ–ª—å–∑—É–µ–º –∏—Ö
      if (positionInfo && positionInfo.computer) {
        console.log('üíª Using preloaded computer data:', positionInfo);
        await formatComputerData(positionInfo);
        return;
      }

      // –ï—Å–ª–∏ –Ω–µ—Ç –ø—Ä–µ–¥–∑–∞–≥—Ä—É–∂–µ–Ω–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö, –∑–∞–≥—Ä—É–∂–∞–µ–º —Å –±—ç–∫–∞
      if (place) {
        setLoading(true);
        setError(null);
        try {
          console.log('üîÑ Loading computer details for place:', place);
          
          // –ó–∞–≥—Ä—É–∂–∞–µ–º –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ –ø–æ–∑–∏—Ü–∏–∏ –∫–æ–º–ø—å—é—Ç–µ—Ä–∞
          const positionResponse = await apiService.getComputerPositions();
          const position = positionResponse.find(pos => 
            pos.number === place.toString() || pos.id === parseInt(place)
          );

          if (position && position.computer_id) {
            // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–µ—Ç–∞–ª—å–Ω—É—é –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ –∫–æ–º–ø—å—é—Ç–µ—Ä–µ —Å–æ —Å–ø–µ—Ü–∏—Ñ–∏–∫–∞—Ü–∏—è–º–∏
            const computer = await apiService.getComputerWithDetails(position.computer_id);
            await formatComputerData({ position, computer });
          } else {
            setError('–ö–æ–º–ø—å—é—Ç–µ—Ä –Ω–µ –Ω–∞–π–¥–µ–Ω –¥–ª—è –¥–∞–Ω–Ω–æ–≥–æ –º–µ—Å—Ç–∞');
          }
        } catch (err) {
          console.error('‚ùå Error loading computer details:', err);
          setError(`–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏: ${err.message}`);
        } finally {
          setLoading(false);
        }
      }
    };

    const formatComputerData = async (data) => {
      const { position, computer } = data;
      
      try {
        // –ó–∞–≥—Ä—É–∂–∞–µ–º —Å–ø–µ—Ü–∏—Ñ–∏–∫–∞—Ü–∏–∏ –∫–æ–º–ø—å—é—Ç–µ—Ä–∞, –µ—Å–ª–∏ –æ–Ω–∏ –µ—Å—Ç—å
        let specs = {};
        if (computer.spec_id) {
          try {
            const specsData = await apiService.getComputerSpec(computer.spec_id);
            specs = specsData || {};
          } catch (specsError) {
            console.warn('‚ö†Ô∏è Could not load computer specs:', specsError);
          }
        }

        // –§–æ—Ä–º–∞—Ç–∏—Ä—É–µ–º –¥–µ—Ç–∞–ª—å–Ω—É—é –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ –∫–æ–º–ø—å—é—Ç–µ—Ä–µ
        const computerWithDetails = {
          ...computer,
          position: position,
          hourlyRate: parseFloat(computer.price) || 100,
          specs: {
