const API_BASE_URL = 'http://127.0.0.1:8000/api';

class ApiService {
    constructor() {
        this.baseURL = API_BASE_URL;
    }

    async request(endpoint, options = {}) {
        const url = `${this.baseURL}${endpoint}`;
        const config = {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
            },
            ...options,
        };

        // Добавляем тело запроса, если это не GET
        if (options.body && config.method !== 'GET') {
            config.body = JSON.stringify(options.body);
        }

        try {
            const response = await fetch(url, config);
            
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            return await response.json();
        } catch (error) {
            console.error('API request failed:', error);
            throw error;
        }
    }

    // Computers API
    async getComputers() {
        return this.request('/computers');
    }

    async getComputer(id) {
        return this.request(`/computers/${id}`);
    }

    // Foods API
    async getFoods() {
        return this.request('/foods');
    }

    // Users API
    async getUsers() {
        return this.request('/users');
    }

    async createUser(userData) {
        return this.request('/users', {
            method: 'POST',
            body: userData
        });
    }

    // Bookings API
    async getBookings() {
        return this.request('/bookings');
    }

    async createBooking(bookingData) {
        return this.request('/bookings', {
            method: 'POST',
            body: bookingData
        });
    }

    async getBooking(id) {
        return this.request(`/bookings/${id}`);
    }

    // Payments API
    async createPayment(paymentData) {
        return this.request('/payments', {
            method: 'POST',
            body: paymentData
        });
    }

    // Computer Positions API
    async getComputerPositions() {
        return this.request('/computer_positions');
    }

    // Tariffs API
    async getTariffs() {
        return this.request('/tarifs'); // Обратите внимание на написание
    }
}

export const apiService = new ApiService();
