// PlaceDetails.js - –ò–°–ü–†–ê–í–õ–ï–ù–ù–ê–Ø –í–ï–†–°–ò–Ø –ë–ï–ó –ö–û–≠–§–§–ò–¶–ò–ï–ù–¢–ê
import React, { useState, useEffect } from 'react';
import '../styles/PlaceDetails.css';

const PlaceDetails = ({ place, onBack, onSelect, positionInfo }) => {
  const [computerDetails, setComputerDetails] = useState(null);

  // –ò—Å–ø–æ–ª—å–∑—É–µ–º –ø–µ—Ä–µ–¥–∞–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –≤–º–µ—Å—Ç–æ –Ω–æ–≤—ã—Ö –∑–∞–ø—Ä–æ—Å–æ–≤
  useEffect(() => {
    if (positionInfo && positionInfo.computer) {
      console.log('üíª Using preloaded computer data:', positionInfo);
      
      const computerPrice = parseFloat(positionInfo.computer.price) || 100;
      
      const computerWithDetails = {
        ...positionInfo.computer,
        position: positionInfo.position,
        // –ò—Å–ø–æ–ª—å–∑—É–µ–º –ø—Ä—è–º—É—é —Ü–µ–Ω—É –±–µ–∑ –∫–æ—ç—Ñ—Ñ–∏—Ü–∏–µ–Ω—Ç–∞
        hourlyRate: computerPrice
      };
      
      setComputerDetails(computerWithDetails);
    }
  }, [positionInfo]);

  const calculatedRate = computerDetails?.hourlyRate || 100;

  if (!positionInfo) {
    return (
      <div className="place-details-overlay">
        <div className="place-details">
          <h2>–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö</h2>
          <p>–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ –º–µ—Å—Ç–µ.</p>
          <button onClick={onBack} className="btn secondary">–ù–∞–∑–∞–¥</button>
        </div>
      </div>
    );
  }

  return (
    <div className="place-details-overlay">
      <div className="place-details">
        <div className="place-details-header">
          <h2>–ú–µ—Å—Ç–æ ‚Ññ{place}</h2>
          <button onClick={onBack} className="close-btn">√ó</button>
        </div>

        <div className="place-info">
          <div className="info-section">
            <h3>–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–æ–∑–∏—Ü–∏–∏</h3>
            <div className="info-grid">
              <div className="info-item">
                <span className="label">–ö–æ–º–Ω–∞—Ç–∞:</span>
                <span className="value">{positionInfo.position?.room || '–ù–µ —É–∫–∞–∑–∞–Ω–∞'}</span>
              </div>
              <div className="info-item">
                <span className="label">–ù–æ–º–µ—Ä –º–µ—Å—Ç–∞:</span>
                <span className="value">{positionInfo.position?.number || place}</span>
              </div>
              <div className="info-item">
                <span className="label">Position ID:</span>
                <span className="value">{positionInfo.position?.id || '–ù–µ —É–∫–∞–∑–∞–Ω'}</span>
              </div>
            </div>
          </div>

          {positionInfo.computer && (
            <div className="info-section">
              <h3>–•–∞—Ä–∞–∫—Ç–µ—Ä–∏—Å—Ç–∏–∫–∏ –∫–æ–º–ø—å—é—Ç–µ—Ä–∞</h3>
              <div className="info-grid">
                <div className="info-item highlight">
                  <span className="label">–°—Ç–æ–∏–º–æ—Å—Ç—å:</span>
                  <span className="value">{calculatedRate} ‚ÇΩ/—á–∞—Å</span>
                </div>
                <div className="info-item">
                  <span className="label">Computer ID:</span>
                  <span className="value">{positionInfo.computer.id}</span>
                </div>
              </div>
            </div>
          )}

          {/* –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ —Ö–∞—Ä–∞–∫—Ç–µ—Ä–∏—Å—Ç–∏–∫–∞—Ö, –µ—Å–ª–∏ –µ—Å—Ç—å */}
          {positionInfo.computer?.specs && (
            <div className="info-section">
              <h3>–¢–µ—Ö–Ω–∏—á–µ—Å–∫–∏–µ —Ö–∞—Ä–∞–∫—Ç–µ—Ä–∏—Å—Ç–∏–∫–∏</h3>
              <div className="specs-grid">
                {positionInfo.computer.specs.processor && (
                  <div className="spec-item">
                    <span className="label">–ü—Ä–æ—Ü–µ—Å—Å–æ—Ä:</span>
                    <span className="value">{positionInfo.computer.specs.processor}</span>
                  </div>
                )}
                {positionInfo.computer.specs.gpu && (
                  <div className="spec-item">
                    <span className="label">–í–∏–¥–µ–æ–∫–∞—Ä—Ç–∞:</span>
                    <span className="value">{positionInfo.computer.specs.gpu}</span>
                  </div>
                )}
                {positionInfo.computer.specs.ram && (
                  <div className="spec-item">
                    <span className="label">–û–ø–µ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–∞–º—è—Ç—å:</span>
                    <span className="value">{positionInfo.computer.specs.ram}</span>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>

        <div className="place-actions">
          <button 
            onClick={() => onSelect(place, calculatedRate)}
            className="btn primary"
          >
            –í—ã–±—Ä–∞—Ç—å —ç—Ç–æ –º–µ—Å—Ç–æ –∑–∞ {calculatedRate} ‚ÇΩ/—á–∞—Å
          </button>
          <button onClick={onBack} className="btn secondary">
            –ù–∞–∑–∞–¥ –∫ –≤—ã–±–æ—Ä—É
          </button>
        </div>
      </div>
    </div>
  );
};

export default PlaceDetails;
