import React, { useState } from 'react';
import '../styles/Cafe.css';

const Cafe = () => {
  const [menuItems, setMenuItems] = useState([
    { id: 1, name: 'Попкорн классический', price: 200, quantity: 0 },
    { id: 2, name: 'Попкорн карамельный', price: 250, quantity: 0 },
    { id: 3, name: 'Чипсы', price: 150, quantity: 0 },
    { id: 4, name: 'Кола', price: 180, quantity: 0 },
    { id: 5, name: 'Кофе', price: 220, quantity: 0 },
    { id: 6, name: 'Шоколад', price: 120, quantity: 0 }
  ]);

const updateQuantity = (id, change) => {
  console.log('Updating quantity:', id, change);
  setMenuItems(prev => {
    const updated = prev.map(item => 
      item.id === id 
        ? { ...item, quantity: Math.max(0, item.quantity + change) }
        : item
    );
    console.log('Updated items:', updated);
    return updated;
  });
};

  // Функция для расчета общей суммы
  const getTotalPrice = () => {
    return menuItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  // Функция для получения общего количества товаров
  const getTotalItems = () => {
    return menuItems.reduce((total, item) => total + item.quantity, 0);
  };

  return (
    <section id="cafe" className="cafe-section">
      <div className="container">
        <h2 className="cafe-title">Кафе</h2>
        
        {/* Информация о корзине */}
        <div className="cart-summary">
          <p>Товаров в корзине: {getTotalItems()}</p>
          <p>Общая сумма: {getTotalPrice()} ₽</p>
        </div>

        <div className="menu-list">
          {menuItems.map(item => (
            <div key={item.id} className="menu-item">
              <span className="item-name">{item.name}</span>
              <div className="item-divider"></div>
              <span className="item-price">{item.price} ₽</span>
              
              <div className="quantity-stepper">
                <button 
                  className="stepper-btn"
                  onClick={() => updateQuantity(item.id, -1)}
                  disabled={item.quantity === 0}
                >-</button>
                <span className="quantity">{item.quantity}</span>
                <button 
                  className="stepper-btn"
                  onClick={() => updateQuantity(item.id, 1)}
                >+</button>
              </div>
              
              {/* Показываем общую стоимость для товара если quantity > 0 */}
              {item.quantity > 0 && (
                <span className="item-total">
                  {item.price * item.quantity} ₽
                </span>
              )}
            </div>
          ))}
        </div>

        <div className="cafe-actions">
          <a href="#pricing" className="btn">Назад</a>
          <a href="#booking" className="btn">Перейти к брони</a>
          <a href="#cart" className="btn">
            Перейти в корзину {getTotalItems() > 0 && `(${getTotalItems()})`}
          </a>
        </div>
      </div>
    </section>
  );
};

export default Cafe;
