import React, { useState, useEffect } from 'react';
import { apiService } from '../services/Api';
import '../styles/PlaceDetails.css';

const PlaceDetails = ({ place, onBack, onSelect }) => {
  const [computerDetails, setComputerDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadComputerDetails();
  }, [place]);

  const loadComputerDetails = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Получаем список всех компьютеров
      const computers = await apiService.getComputers();
      
      // Находим компьютер по номеру места
      const computer = computers.find(comp => {
        // Проверяем, что computer.position существует и имеет номер
        return comp.position && comp.position.number === place;
      });
      
      if (computer) {
        // Загружаем детальную информацию о компьютере
        try {
          const details = await apiService.getComputerWithDetails(computer.id);
          setComputerDetails(details);
        } catch (detailError) {
          // Если не удалось загрузить детали, используем базовую информацию
          console.warn('Could not load computer details, using basic info:', detailError);
          setComputerDetails({
            ...computer,
            specs: computer.specs || {}
          });
        }
      } else {
        setError('Компьютер не найден');
      }
    } catch (error) {
      console.error('Error loading computer details:', error);
      setError('Ошибка загрузки данных');
    } finally {
      setLoading(false);
    }
  };

  // Вспомогательная функция для перевода названий характеристик
  const getSpecLabel = (key) => {
    const labels = {
      processor: "Процессор",
      gpu: "Видеокарта",
      monitor: "Монитор",
      headphones: "Наушники",
      mouse: "Мышь",
      keyboard: "Клавиатура",
      console: "Консоль",
      controller: "Контроллер",
      games: "Игры",
      headset: "VR Шлем",
      pc: "ПК для VR",
      space: "Игровая зона",
      accessories: "Аксессуары",
      dac: "ЦАП",
      amp: "Усилитель",
      source: "Источник",
      cables: "Кабели",
      ram: "Оперативная память"
    };
    return labels[key] || key;
  };

  const handleSelect = () => {
    if (computerDetails) {
      onSelect(place, computerDetails.price);
    }
  };

  if (loading) {
    return (
      <div className="place-details-overlay">
        <div className="place-details">
          <div className="place-details-header">
            <h2>Место {place}</h2>
            <button className="close-btn" onClick={onBack}>×</button>
          </div>
          <div className="loading-message">Загрузка информации о месте...</div>
        </div>
      </div>
    );
  }

  if (error || !computerDetails) {
    return (
      <div className="place-details-overlay">
        <div className="place-details">
          <div className="place-details-header">
            <h2>Место {place}</h2>
            <button className="close-btn" onClick={onBack}>×</button>
          </div>
          <div className="error-message">
            {error || 'Информация о месте не найдена'}
          </div>
          <div className="place-actions">
            <button className="btn secondary" onClick={onBack}>
              Назад к выбору
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="place-details-overlay">
      <div className="place-details">
        <div className="place-details-header">
          <h2>Место {place}</h2>
          <button className="close-btn" onClick={onBack}>×</button>
        </div>
        
        <div className="place-info">
          <div className="place-type">
            <h3>Компьютер #{computerDetails.id}</h3>
            <div className="place-price">{computerDetails.price} ₽/час</div>
          </div>
          
          {computerDetails.specs && Object.keys(computerDetails.specs).length > 0 ? (
            <div className="specs-grid">
              {Object.entries(computerDetails.specs).map(([key, value]) => (
                <div key={key} className="spec-item">
                  <span className="spec-label">{getSpecLabel(key)}:</span>
                  <span className="spec-value">{value}</span>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-specs-message">
              Характеристики не указаны
            </div>
          )}
        </div>
        
        <div className="place-actions">
          <button className="btn secondary" onClick={onBack}>
            Назад к выбору
          </button>
          <button className="btn primary" onClick={handleSelect}>
            Выбрать это место
          </button>
        </div>
      </div>
    </div>
  );
};

export default PlaceDetails;
