import React, { useState, useEffect } from 'react';
import { apiService } from '../services/Api';
import '../styles/BookingConfirmation.css';

const BookingConfirmation = () => {
  const [bookingData, setBookingData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [processingPayment, setProcessingPayment] = useState(false);
  const [promoCode, setPromoCode] = useState('');

  // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ localStorage –ø—Ä–∏ –º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
  useEffect(() => {
    const loadBookingData = () => {
      try {
        const savedData = localStorage.getItem('lastBooking');
        console.log('Loading booking data from localStorage:', savedData);
        
        if (savedData) {
          const parsedData = JSON.parse(savedData);
          setBookingData(parsedData);
        } else {
          console.warn('No booking data found in localStorage');
        }
      } catch (error) {
        console.error('Error loading booking data:', error);
      } finally {
        setLoading(false);
      }
    };

    loadBookingData();
  }, []);

  // –ò—Å–ø–æ–ª—å–∑—É–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ —Å–æ—Å—Ç–æ—è–Ω–∏—è
  const data = bookingData || {};

  console.log('=== BOOKING DATA DEBUG ===');
  console.log('Full booking data:', data);
  console.log('=== END DEBUG ===');

  const {
    formData = {},
    selectedPlace,
    placeRate,
    cartItems = [],
    totalPrice = 0,
    id: bookingId,
    calculatedData = {}
  } = data;

  // –î–µ—Å—Ç—Ä—É–∫—Ç—É—Ä–∏—Ä—É–µ–º formData —Å –∑–Ω–∞—á–µ–Ω–∏—è–º–∏ –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é
  const {
    place = '',
    dateFrom = '',
    timeFrom = '',
    dateTo = '',
    timeTo = '',
    address = '',
    computer_id = '',
    club_id = ''
  } = formData;

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ –º–µ—Å—Ç–µ
  const getPlaceInfo = (placeNumber) => {
    const placeInfo = {
      1: { type: "Gaming PC" },
      2: { type: "Gaming PC" },
      3: { type: "Gaming PC" },
      4: { type: "Gaming PC" },
      5: { type: "Streaming PC" },
      6: { type: "Competitive PC" },
      8: { type: "PlayStation 5" },
      9: { type: "PlayStation 5 Pro" },
      10: { type: "VR Station" },
      11: { type: "Audio Station" },
      12: { type: "PlayStation 4 Pro" },
      13: { type: "Nintendo Switch" },
      14: { type: "Premium Audio" }
    };
    return placeInfo[placeNumber] || { type: "–°—Ç–∞–Ω–¥–∞—Ä—Ç–Ω–æ–µ –º–µ—Å—Ç–æ" };
  };

  const placeInfo = getPlaceInfo(selectedPlace);

  // –ò–°–ü–†–ê–í–õ–ï–ù–ò–ï: –ò—Å–ø–æ–ª—å–∑—É–µ–º —Å—Ç–∞–≤–∫—É –∫–∞–∫ –µ—Å—Ç—å –∏–∑ –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö
  const actualPlaceRate = placeRate;

  // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –∑–∞–≥—Ä—É–∑–∫—É –ø–æ–∫–∞ –¥–∞–Ω–Ω—ã–µ –Ω–µ –≥–æ—Ç–æ–≤—ã
  if (loading) {
    return (
      <div className="booking-confirmation">
        <div className="container">
          <div className="loading-state">
            <h2>–ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è...</h2>
          </div>
        </div>
      </div>
    );
  }

  // –ï—Å–ª–∏ –¥–∞–Ω–Ω—ã—Ö –Ω–µ—Ç, –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± –æ—à–∏–±–∫–µ
  if (!bookingData || !selectedPlace) {
    return (
      <div className="booking-confirmation">
        <div className="container">
          <div className="no-booking-data">
            <h2>–î–∞–Ω–Ω—ã–µ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è –Ω–µ –Ω–∞–π–¥–µ–Ω—ã</h2>
            <p>–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–µ—Ä–Ω–∏—Ç–µ—Å—å –∏ —Å–æ–∑–¥–∞–π—Ç–µ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ –∑–∞–Ω–æ–≤–æ.</p>
            <button 
              className="btn primary"
              onClick={() => window.location.href = '/booking'}
            >
              –í–µ—Ä–Ω—É—Ç—å—Å—è –∫ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—é
            </button>
            <button 
              className="btn secondary"
              onClick={() => window.location.href = '/'}
              style={{marginLeft: '10px'}}
            >
              –ù–∞ –≥–ª–∞–≤–Ω—É—é
            </button>
          </div>
        </div>
      </div>
    );
  }

  console.log('=== RATE DEBUG ===');
  console.log('selectedPlace:', selectedPlace);
  console.log('placeRate from booking data:', placeRate);
  console.log('actualPlaceRate used:', actualPlaceRate);
  console.log('=== END RATE DEBUG ===');

  // –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –¥–∞—Ç—ã –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è
  const formatDateDisplay = (dateString) => {
    if (!dateString) return '';
    const [year, month, day] = dateString.split('-').map(Number);
    const date = new Date(year, month - 1, day);
    
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);
    
    if (date.toDateString() === today.toDateString()) {
      return '–°–µ–≥–æ–¥–Ω—è';
    } else if (date.toDateString() === tomorrow.toDateString()) {
      return '–ó–∞–≤—Ç—Ä–∞';
    } else {
      return date.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'long',
        weekday: 'short'
      });
    }
  };

  // –†–∞—Å—á–µ—Ç –∫–æ–ª–∏—á–µ—Å—Ç–≤–∞ —á–∞—Å–æ–≤
  const getBookingHours = () => {
    if (!dateFrom || !timeFrom || !dateTo || !timeTo) return 0;
    
    try {
      const startDateTime = new Date(`${dateFrom}T${timeFrom}`);
      const endDateTime = new Date(`${dateTo}T${timeTo}`);
      const hoursDiff = (endDateTime - startDateTime) / (1000 * 60 * 60);
      
      return Math.round(hoursDiff * 10) / 10;
    } catch (error) {
      console.error('Error calculating booking hours:', error);
      return 0;
    }
  };

  // –†–∞—Å—á–µ—Ç –æ–±—â–µ–π —Å—Ç–æ–∏–º–æ—Å—Ç–∏ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è
  const calculateBookingCost = () => {
    if (calculatedData && calculatedData.totalCost) {
      return calculatedData.totalCost;
    }
    
    if (!dateFrom || !timeFrom || !dateTo || !timeTo) return 0;
    
    try {
      const startDateTime = new Date(`${dateFrom}T${timeFrom}`);
      const endDateTime = new Date(`${dateTo}T${timeTo}`);
      const hours = (endDateTime - startDateTime) / (1000 * 60 * 60);
      
      console.log('=== COST CALCULATION ===');
      console.log('Hours:', hours);
      console.log('Place rate:', actualPlaceRate);
      console.log('Cart total:', totalPrice);
      console.log('=== END COST CALCULATION ===');

      // –ò–°–ü–†–ê–í–õ–ï–ù–ò–ï: –ò—Å–ø–æ–ª—å–∑—É–µ–º —Å—Ç–∞–≤–∫—É –∫–∞–∫ –µ—Å—Ç—å –±–µ–∑ –ø—Ä–æ–≤–µ—Ä–æ–∫
      const placeCost = Math.round(hours * actualPlaceRate);
      return placeCost + totalPrice;
    } catch (error) {
      console.error('Error calculating booking cost:', error);
      return totalPrice;
    }
  };

  const bookingHours = getBookingHours();
  const bookingCost = calculateBookingCost();
  const placeCost = calculatedData?.placeCost || Math.round(bookingHours * actualPlaceRate);

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø—Ä–∏–º–µ–Ω–µ–Ω–∏—è –ø—Ä–æ–º–æ–∫–æ–¥–∞
  const applyPromoCode = async () => {
    if (!promoCode.trim()) {
      alert('–í–≤–µ–¥–∏—Ç–µ –ø—Ä–æ–º–æ–∫–æ–¥');
      return;
    }

    try {
      console.log('üîç Validating promo code:', promoCode);
      const validationResult = await apiService.validateCode(promoCode);
      console.log('‚úÖ Promo code validated:', validationResult);
      
      // –ó–¥–µ—Å—å –º–æ–∂–Ω–æ –ø—Ä–∏–º–µ–Ω–∏—Ç—å —Å–∫–∏–¥–∫—É –∫ —Å—Ç–æ–∏–º–æ—Å—Ç–∏
      alert(`–ü—Ä–æ–º–æ–∫–æ–¥ "${promoCode}" –ø—Ä–∏–º–µ–Ω–µ–Ω! –°–∫–∏–¥–∫–∞: ${validationResult.discount || '10%'}`);
      
    } catch (error) {
      console.error('Promo code error:', error);
      alert('–ù–µ–≤–µ—Ä–Ω—ã–π –ø—Ä–æ–º–æ–∫–æ–¥ –∏–ª–∏ —Å—Ä–æ–∫ –µ–≥–æ –¥–µ–π—Å—Ç–≤–∏—è –∏—Å—Ç–µ–∫');
    }
  };

  const handleEditBooking = () => {
    window.location.href = '/booking';
  };

  const handleBackToHome = () => {
    window.location.href = '/';
  };

  const handlePayment = async () => {
    try {
      setProcessingPayment(true);
      
      // –°–Ω–∞—á–∞–ª–∞ —Å–æ–∑–¥–∞–µ–º –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ –≤ –±–∞–∑–µ –¥–∞–Ω–Ω—ã—Ö
      console.log('üîÑ Creating booking in database...');
      
      const bookingMinutes = getBookingHours() * 60;
      const placeCostValue = Math.round((bookingMinutes / 60) * actualPlaceRate);
      const totalCostValue = placeCostValue + totalPrice;

      const bookingDataToSend = {
        computer_id: parseInt(computer_id),
        user_id: 1,
        club_id: parseInt(club_id),
        tariff_id: 1,
        code_id: null,
        start_time: `${dateFrom} ${timeFrom}:00`,
        end_time: `${dateTo} ${timeTo}:00`,
        minutes: bookingMinutes,
        price_for_pc: placeCostValue,
        price_for_additions: totalPrice,
        total_price: totalCostValue,
        status: 'pending'
      };

      console.log('üì§ Sending booking to API:', bookingDataToSend);
      
      const bookingResponse = await apiService.createBooking(bookingDataToSend);
      console.log('‚úÖ Booking created:', bookingResponse);

      // –ó–∞—Ç–µ–º —Å–æ–∑–¥–∞–µ–º –ø–ª–∞—Ç–µ–∂
      console.log('üîÑ Creating payment...');
      
      const paymentData = {
        user_id: 1,
        booking_id: bookingResponse.id,
        price: totalCostValue,
        payment_type: 'card',
        status: 'pending',
        payment_hash: `pay_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        payment_date: new Date().toISOString().split('T')[0]
      };

      console.log('üì§ Sending payment to API:', paymentData);

      // –û—Ç–ø—Ä–∞–≤–∫–∞ –∑–∞–ø—Ä–æ—Å–∞ –Ω–∞ —Å–æ–∑–¥–∞–Ω–∏–µ –ø–ª–∞—Ç–µ–∂–∞
      const paymentResponse = await apiService.createPayment(paymentData);
      
      console.log('‚úÖ Payment created:', paymentResponse);

      // –ï—Å–ª–∏ –µ—Å—Ç—å —Ç–æ–≤–∞—Ä—ã –≤ –∫–æ—Ä–∑–∏–Ω–µ, –¥–æ–±–∞–≤–ª—è–µ–º –∏—Ö –≤ additional_menu
      if (cartItems.length > 0) {
        console.log('üîÑ Adding food items to booking...');
        for (const item of cartItems) {
          try {
            await apiService.addFoodToBooking(bookingResponse.id, {
              food_id: item.id,
              count: item.quantity
            });
            console.log(`‚úÖ Added ${item.name} to booking`);
          } catch (foodError) {
            console.error('Error adding food to booking:', foodError);
          }
        }
      }

      // –û–±–Ω–æ–≤–ª—è–µ–º –¥–∞–Ω–Ω—ã–µ –≤ localStorage —Å —Ä–µ–∞–ª—å–Ω—ã–º ID –±—Ä–æ–Ω–∏
      const updatedBookingData = {
        ...bookingData,
        id: bookingResponse.id,
        payment_id: paymentResponse.id,
        status: 'pending_payment'
      };

      localStorage.setItem('lastBooking', JSON.stringify(updatedBookingData));

      // –ó–¥–µ—Å—å –¥–æ–ª–∂–Ω–∞ –±—ã—Ç—å –∏–Ω—Ç–µ–≥—Ä–∞—Ü–∏—è —Å –ø–ª–∞—Ç–µ–∂–Ω–æ–π —Å–∏—Å—Ç–µ–º–æ–π
      // –ü–æ–∫–∞ –∏—Å–ø–æ–ª—å–∑—É–µ–º –∑–∞–≥–ª—É—à–∫—É
      alert('–ü–ª–∞—Ç–µ–∂ –æ–±—Ä–∞–±–æ—Ç–∞–Ω —É—Å–ø–µ—à–Ω–æ! –ë—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–æ.');
      localStorage.removeItem('lastBooking');
      
      // –†–µ–¥–∏—Ä–µ–∫—Ç –Ω–∞ –≥–ª–∞–≤–Ω—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É
      window.location.href = '/';

    } catch (error) {
      console.error('Payment error:', error);
      
      if (error.message.includes('code_id')) {
        alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è: –ø—Ä–æ–±–ª–µ–º–∞ —Å –ø—Ä–æ–º–æ–∫–æ–¥–æ–º. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.');
      } else if (error.message.includes('payment_date')) {
        alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±—Ä–∞–±–æ—Ç–∫–µ –ø–ª–∞—Ç–µ–∂–∞: –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç –¥–∞—Ç–∞ –ø–ª–∞—Ç–µ–∂–∞. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.');
      } else if (error.message.includes('tariff_id')) {
        alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è: –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç —Ç–∞—Ä–∏—Ñ. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.');
      } else {
        alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±—Ä–∞–±–æ—Ç–∫–µ –ø–ª–∞—Ç–µ–∂–∞. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.');
      }
    } finally {
      setProcessingPayment(false);
    }
  };

  // –ê–ª—å—Ç–µ—Ä–Ω–∞—Ç–∏–≤–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è –æ–ø–ª–∞—Ç—ã - —Ç–æ–ª—å–∫–æ —Å–æ–∑–¥–∞–Ω–∏–µ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è
  const handleSimplePayment = async () => {
    try {
      setProcessingPayment(true);
      
      console.log('üîÑ Creating booking without payment...');
      
      const bookingMinutes = getBookingHours() * 60;
      const placeCostValue = Math.round((bookingMinutes / 60) * actualPlaceRate);
      const totalCostValue = placeCostValue + totalPrice;

      const bookingDataToSend = {
        computer_id: parseInt(computer_id),
        user_id: 1,
        club_id: parseInt(club_id),
        tariff_id: 1,
        code_id: null,
        start_time: `${dateFrom} ${timeFrom}:00`,
        end_time: `${dateTo} ${timeTo}:00`,
        minutes: bookingMinutes,
        price_for_pc: placeCostValue,
        price_for_additions: totalPrice,
        total_price: totalCostValue,
        status: 'confirmed'
      };

      console.log('üì§ Sending booking to API:', bookingDataToSend);
      
      const bookingResponse = await apiService.createBooking(bookingDataToSend);
      console.log('‚úÖ Booking created:', bookingResponse);

      // –ï—Å–ª–∏ –µ—Å—Ç—å —Ç–æ–≤–∞—Ä—ã –≤ –∫–æ—Ä–∑–∏–Ω–µ, –¥–æ–±–∞–≤–ª—è–µ–º –∏—Ö –≤ additional_menu
      if (cartItems.length > 0) {
        console.log('üîÑ Adding food items to booking...');
        for (const item of cartItems) {
          try {
            await apiService.addFoodToBooking(bookingResponse.id, {
              food_id: item.id,
              count: item.quantity
            });
            console.log(`‚úÖ Added ${item.name} to booking`);
          } catch (foodError) {
            console.error('Error adding food to booking:', foodError);
          }
        }
      }

      alert('–ë—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ —É—Å–ø–µ—à–Ω–æ —Å–æ–∑–¥–∞–Ω–æ! –ù–æ–º–µ—Ä –±—Ä–æ–Ω–∏: ' + bookingResponse.id);
      localStorage.removeItem('lastBooking');
      
      // –†–µ–¥–∏—Ä–µ–∫—Ç –Ω–∞ –≥–ª–∞–≤–Ω—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É
      window.location.href = '/';

    } catch (error) {
      console.error('Booking creation error:', error);
      
      if (error.message.includes('code_id')) {
        alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è: –ø—Ä–æ–±–ª–µ–º–∞ —Å –ø—Ä–æ–º–æ–∫–æ–¥–æ–º. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.');
      } else {
        alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.');
      }
    } finally {
      setProcessingPayment(false);
    }
  };

  // –ï—Å–ª–∏ –¥–∞–Ω–Ω—ã—Ö –Ω–µ—Ç, –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± –æ—à–∏–±–∫–µ
  if (!bookingData || !selectedPlace) {
    return (
      <div className="booking-confirmation">
        <div className="container">
          <div className="no-booking-data">
            <h2>–î–∞–Ω–Ω—ã–µ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è –Ω–µ –Ω–∞–π–¥–µ–Ω—ã</h2>
