        <div>🔄 Кнопка: <span style={{color: isBookingDisabled ? '#f44336' : '#4CAF50'}}>{isBookingDisabled ? '🔴 ВЫКЛ' : '🟢 ВКЛ'}</span></div>
        {isBookingDisabled && (
          <div style={{ marginTop: '8px', padding: '5px', background: '#f44336', borderRadius: '3px', fontSize: '10px' }}>
            Причина: {getDisabledReason()}
          </div>
        )}
      </div>
      
      <div className="combined-container">
        {/* Левая часть - схема мест */}
        <div className="layout-side">
          <h2 className="section-title">Выберите место</h2>
          <div className="layout-content-box">
            <div className="layout-scaler">
              <div className="decorative-vectors">
                <img src="/images/30_51.svg" alt="decorative shape" className="decorative-shape shape-1" />
              </div>
              
              {/* Кликабельные места - только доступные позиции */}
              {displayPositions.map((positionNumber) => {
                const isAvailable = isPlaceAvailable(positionNumber);
                const positionInfo = getPositionInfo(positionNumber);
                
                return (
                  <div 
                    key={`position-${positionNumber}`} 
                    className={`
                      ${positionNumber <= 6 ? 'pc-setup' : 
                        [8, 9, 12, 13].includes(positionNumber) ? 'console-setup' : 
                        'headphones-setup'
                      } 
                      setup-${positionNumber} 
                      ${selectedPlace === positionNumber ? 'selected' : ''}
                      ${!isAvailable ? 'unavailable' : ''}
                    `}
                    onClick={() => isAvailable && handlePlaceClick(positionNumber)}
                    style={{
                      cursor: isAvailable ? 'pointer' : 'not-allowed',
                      opacity: isAvailable ? 1 : 0.5
                    }}
                  >
                    <img 
                      src={
                        positionNumber <= 6 ? "/images/6356f02b474a41d638cf709af15fe1f7c6dd92c0.png" :
                        [8, 9, 12, 13].includes(positionNumber) ? "/images/1b9fb18a794f8543e1b7ff770153e91c8879c831.png" :
                        "/images/2fc05fccb4c07d9e1bb638c4487609fd22b2f1ec.png"
                      } 
                      alt={`Setup ${positionNumber}`} 
                    />
                    <span className="layout-number">
                      {positionNumber}
                      {!isAvailable && <span className="unavailable-badge">✗</span>}
                    </span>
                  </div>
                );
              })}
            </div>
          </div>
          
          {/* Отладочная информация */}
          <div className="debug-info" style={{
            background: '#f5f5f5',
            padding: '10px',
            margin: '10px 0',
            borderRadius: '5px',
            fontSize: '12px'
          }}>
            <strong>Информация о данных:</strong><br/>
            - Позиций загружено: {positions.length}<br/>
            - Компьютеров загружено: {computers.length}<br/>
            - Доступных мест: {displayPositions.length}<br/>
            - Выбрано место: {selectedPlace || 'нет'}<br/>
            - Computer ID: {formData.computer_id || 'не установлен'}<br/>
            - Статус кнопки: {isBookingDisabled ? '🔴 Заблокирована' : '🟢 Активна'}
          </div>
        </div>

        {/* Правая часть - форма бронирования */}
        <div className="booking-side">
          <h2 className="section-title">Бронь места {selectedPlace || ''}</h2>
          
          {/* Информация о корзине */}
          {getTotalItems() > 0 && (
            <div className="cart-info">
              <h3>Корзина ({getTotalItems()} товаров)</h3>
              <div className="cart-items">
                {cartItems.map(item => (
                  <div key={item.id} className="cart-item">
                    <span>{item.name} x{item.quantity}</span>
                    <div className="cart-item-controls">
                      <button 
                        onClick={() => updateCartItemQuantity(item.id, -1)}
                        className="cart-btn"
                      >-</button>
                      <span>{item.quantity}</span>
                      <button 
                        onClick={() => updateCartItemQuantity(item.id, 1)}
                        className="cart-btn"
                      >+</button>
                      <span className="cart-item-price">{item.price * item.quantity} ₽</span>
                    </div>
                  </div>
                ))}
              </div>
              <div className="cart-total">
                Итого: {getTotalPrice()} ₽
              </div>
            </div>
          )}

          <form className="booking-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="place">Выбранное место</label>
              <input 
                type="text" 
                id="place" 
                name="place"
                value={formData.place}
                onChange={handleChange}
                placeholder="Нажмите на место слева"
                readOnly
                className={selectedPlace ? 'selected-place' : ''}
              />
              {selectedPlace && (
                <div className="place-selected-info">
                  ✓ Место {selectedPlace} выбрано. 
                  <br/>
                  <small style={{color: 'blue', fontWeight: 'bold'}}>
                    Computer ID: {formData.computer_id || 'не установлен'}
                    {!formData.computer_id && <span style={{color: 'red'}}> - ТРЕБУЕТСЯ ВЫБРАТЬ МЕСТО!</span>}
                  </small>
                  <button 
                    type="button" 
                    className="view-details-btn"
                    onClick={() => setShowPlaceDetails(true)}
                  >
                    Посмотреть детали
                  </button>
                </div>
              )}
            </div>

            <div className="datetime-fields">
              <div className="form-group">
                <label>Дата и время начала</label>
                <div className="datetime-inputs-row">
                  <div className="date-input-wrapper">
                    <input 
                      type="text"
                      value={formData.dateFrom ? formatDateDisplay(formData.dateFrom) : 'Выберите дату'}
                      placeholder="Дата начала"
                      readOnly
                      onClick={() => handleDateFieldClick('from')}
                      className="date-input"
                    />
                  </div>
                  <div className="time-input-wrapper">
                    <input 
                      type="text"
                      value={formData.timeFrom || 'Выберите время'}
                      placeholder="Время начала"
                      readOnly
                      onClick={() => handleTimeFieldClick('from')}
                      className="time-input"
                    />
                  </div>
                </div>
              </div>

              <div className="form-group">
                <label>Дата и время окончания</label>
                <div className="datetime-inputs-row">
                  <div className="date-input-wrapper">
                    <input 
                      type="text"
                      value={formData.dateTo ? formatDateDisplay(formData.dateTo) : 'Выберите дату'}
                      placeholder="Дата окончания"
                      readOnly
                      onClick={() => handleDateFieldClick('to')}
                      className="date-input"
                    />
                  </div>
                  <div className="time-input-wrapper">
                    <input 
                      type="text"
                      value={formData.timeTo || 'Выберите время'}
                      placeholder="Время окончания"
                      readOnly
                      onClick={() => handleTimeFieldClick('to')}
                      className="time-input"
                    />
                  </div>
                </div>
              </div>

              {/* Отображение ошибки временного промежутка */}
              {timeError && (
                <div className="time-error-message">
                  ⚠️ {timeError}
                </div>
              )}

              {/* Информация о выбранном промежутке */}
              {formData.dateFrom && formData.timeFrom && formData.dateTo && formData.timeTo && !timeError && (
                <div className="time-range-info">
                  ✅ Выбран промежуток: {formatDateDisplay(formData.dateFrom)} {formData.timeFrom} - {formatDateDisplay(formData.dateTo)} {formData.timeTo}
                </div>
              )}
            </div>
            
            {/* Пicker даты */}
            {showDatePicker && (
              <div className="date-picker-overlay">
                <div className="date-picker">
                  <div className="date-picker-header">
                    <h3>
                      {currentField === 'from' ? 'Выберите дату начала' : 'Выберите дату окончания'}
                    </h3>
                    <button 
                      type="button" 
                      className="date-picker-close"
                      onClick={handleDateCancel}
                    >
                      ×
                    </button>
                  </div>
                  
                  <div className="date-picker-body">
                    <div className="calendar-section">
                      <div className="calendar-header">
                        <button 
                          type="button" 
                          className="calendar-nav prev"
                          onClick={prevMonth}
                        >
                          ‹
                        </button>
                        <div className="calendar-month">
                          {monthNames[currentMonth.getMonth()]} {currentMonth.getFullYear()}
                        </div>
                        <button 
                          type="button" 
                          className="calendar-nav next"
                          onClick={nextMonth}
                        >
                          ›
                        </button>
                      </div>
                      
                      <div className="calendar-grid">
                        {/* Дни недели */}
                        {dayNames.map(day => (
                          <div key={day} className="calendar-day-header">
                            {day}
                          </div>
                        ))}
                        
                        {/* Дни месяца */}
                        {calendarDays.map((date, index) => (
                          <button
                            key={index}
                            type="button"
                            className={`calendar-day ${
                              date ? (
                                isPastDate(date) ? 'past' :
                                selectedDate && date.toDateString() === selectedDate.toDateString() ? 'selected' :
                                isToday(date) ? 'today' : ''
                              ) : 'empty'
                            }`}
                            onClick={() => date && handleDateSelect(date)}
                            disabled={!date || isPastDate(date)}
                          >
                            {date ? date.getDate() : ''}
                            {date && isToday(date) && <div className="today-dot"></div>}
                          </button>
                        ))}
                      </div>
                    </div>
                  </div>
                  
                  <div className="date-picker-footer">
                    <div className="selected-date-preview">
                      {selectedDate ? (
                        `Выбрано: ${formatDateDisplay(
                          `${selectedDate.getFullYear()}-${(selectedDate.getMonth() + 1).toString().padStart(2, '0')}-${selectedDate.getDate().toString().padStart(2, '0')}`
                        )}`
                      ) : (
                        'Выберите дату'
                      )}
                    </div>
                    <div className="date-picker-actions">
                      <button 
                        type="button" 
                        className="btn secondary"
                        onClick={handleDateCancel}
                      >
                        Отмена
                      </button>
                      <button 
                        type="button" 
                        className="btn primary"
                        onClick={handleDateConfirm}
                        disabled={!selectedDate}
                      >
                        Выбрать дату
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            )}
            
            {/* Пicker времени */}
            {showTimePicker && (
              <div className="time-picker-overlay">
                <div className="time-picker">
                  <div className="time-picker-header">
                    <h3>
                      {currentField === 'from' ? 'Выберите время начала' : 'Выберите время окончания'}
                    </h3>
                    <button 
                      type="button" 
                      className="time-picker-close"
                      onClick={handleTimeCancel}
                    >
                      ×
                    </button>
                  </div>
                  
                  <div className="time-picker-body">
                    <div className="time-selectors">
                      <div className="time-selector">
                        <label>Часы</label>
                        <select 
                          value={selectedHour}
                          onChange={(e) => setSelectedHour(e.target.value)}
                          className="time-select"
                        >
                          {hours.map(hour => (
                            <option key={hour} value={hour}>{hour}</option>
                          ))}
                        </select>
                      </div>
                      
                      <div className="time-selector">
                        <label>Минуты</label>
                        <select 
                          value={selectedMinute}
                          onChange={(e) => setSelectedMinute(e.target.value)}
                          className="time-select"
                        >
                          {minutes.map(minute => (
                            <option key={minute} value={minute}>{minute}</option>
                          ))}
                        </select>
                      </div>
                    </div>
                  </div>
                  
                  <div className="time-picker-footer">
                    <div className="selected-time-preview">
                      {selectedHour && selectedMinute ? (
                        `Выбрано: ${selectedHour}:${selectedMinute}`
                      ) : (
                        'Выберите время'
                      )}
                    </div>
                    <div className="time-picker-actions">
                      <button 
                        type="button" 
                        className="btn secondary"
                        onClick={handleTimeCancel}
                      >
                        Отмена
                      </button>
                      <button 
                        type="button" 
                        className="btn primary"
                        onClick={handleTimeConfirm}
                        disabled={!selectedHour || !selectedMinute}
                      >
                        Выбрать время
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            )}
            
            <div className="form-group">
              <label htmlFor="address">Адрес клуба</label>
              <select 
                id="address" 
                name="address"
                value={formData.address}
                onChange={handleClubChange}
                required
              >
                <option value="">Выберите клуб</option>
                {clubs.map(club => (
                  <option key={club.id} value={club.address}>
                    {club.address}
                  </option>
                ))}
              </select>
              {formData.address && (
                <div className="auto-address-indicator">
                  ✓ Адрес автоматически выбран из карточки клуба
                </div>
              )}
            </div>
            
            <div className="booking-actions">
              <button 
                type="submit" 
                className="btn primary"
                disabled={isBookingDisabled}
                title={isBookingDisabled ? getDisabledReason() : "Перейти к подтверждению брони"}
              >
                Перейти к подтверждению
                {isBookingDisabled && (
                  <span style={{fontSize: '12px', display: 'block', marginTop: '5px', color: '#ff6b6b'}}>
                    ({getDisabledReason()})
                  </span>
                )}
              </button>
              <button 
                type="button" 
                className="btn secondary"
                onClick={handleBackToHome}
              >
                На главную
              </button>
              <button 
                type="button" 
                className="btn secondary"
                onClick={scrollToCafe}
              >
                Перейти в кафе
              </button>
              {getTotalItems() > 0 && (
                <button 
                  type="button" 
                  className="btn secondary" 
                  onClick={handleClearCart}
                >
                  Очистить корзину
                </button>
              )}
            </div>
          </form>
        </div>
      </div>

      {/* Модальное окно с деталями места */}
      {showPlaceDetails && selectedPlace && (
        <PlaceDetails 
          place={selectedPlace}
          onBack={handleBackFromDetails}
          onSelect={handlePlaceSelectFromDetails}
          positionInfo={getPositionInfo(selectedPlace)}
        />
      )}
    </section>
  );
};

export default CombinedLayoutBooking;
