                      onClick={() => handleTimeFieldClick('from')}
                      className="time-input"
                    />
                  </div>
                </div>
              </div>

              <div className="form-group">
                <label>–î–∞—Ç–∞ –∏ –≤—Ä–µ–º—è –æ–∫–æ–Ω—á–∞–Ω–∏—è</label>
