import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import { apiService } from '../services/Api';
import PlaceDetails from './PlaceDetails';
import '../styles/CombinedLayout.css';

const CombinedLayoutBooking = () => {
  const { 
    cartItems, 
    updateCartItemQuantity, 
    clearCart, 
    getTotalPrice, 
    getTotalItems,
    getCartSummary 
  } = useCart();
  
  const [formData, setFormData] = useState({
    place: '',
    dateFrom: '',
    dateTo: '',
    timeFrom: '',
    timeTo: '',
    address: '',
    cart: '',
    club_id: '',
    computer_id: ''
  });

  const [selectedPlace, setSelectedPlace] = useState(null);
  const [showPlaceDetails, setShowPlaceDetails] = useState(false);
  const [selectedPlaceRate, setSelectedPlaceRate] = useState(0);
  const [showDatePicker, setShowDatePicker] = useState(false);
  const [showTimePicker, setShowTimePicker] = useState(false);
  const [currentField, setCurrentField] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedHour, setSelectedHour] = useState('12');
  const [selectedMinute, setSelectedMinute] = useState('00');
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [timeError, setTimeError] = useState('');
  const [computers, setComputers] = useState([]);
  const [positions, setPositions] = useState([]);
  const [clubs, setClubs] = useState([]);
  const [loading, setLoading] = useState(true);

  const hours = Array.from({ length: 24 }, (_, i) => i.toString().padStart(2, '0'));
  const minutes = Array.from({ length: 60 }, (_, i) => i.toString().padStart(2, '0'));
  useEffect(() => {
    loadInitialData();
  }, []);

  const loadInitialData = async () => {
    try {
      setLoading(true);
      
      const [computersData, positionsData, clubsData] = await Promise.all([
        apiService.getComputers(),
        apiService.getComputerPositions(),
        apiService.getClubs()
      ]);
      
      setComputers(computersData);
      setPositions(positionsData);
      setClubs(clubsData);
      
      console.log('âœ… All data loaded:', {
        computers: computersData.length,
        positions: positionsData.length,
        clubs: clubsData.length
      });
      
    } catch (error) {
      console.error('Error loading initial data:', error);
    } finally {
      setLoading(false);
    }
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ð¸ Ð¾ Ð¼ÐµÑÑ‚Ðµ (Ð¿Ð¾Ð·Ð¸Ñ†Ð¸Ð¸) Ð¿Ð¾ Ð½Ð¾Ð¼ÐµÑ€Ñƒ
  const getPositionInfo = (positionNumber) => {
    console.log(`ðŸ” Getting info for position number: ${positionNumber}`);
    
    const position = positions.find(pos => pos.number == positionNumber);
    console.log('ðŸ“ Found position:', position);
    
    if (!position) {
      console.log('âŒ Position not found for number:', positionNumber);
      return null;
    }
    
    const computer = computers.find(comp => comp.position_id == position.id);
    console.log('ðŸ’» Found computer:', computer);
    
    if (!computer) {
      console.log('âŒ Computer not found for position_id:', position.id);
      return null;
    }
    
    const result = {
      position,
      computer,
      room: position.room,
      number: position.number,
      computerId: computer.id,
      price: computer.price ? parseFloat(computer.price) : 100
    };
    
    console.log('âœ… Position info result:', result);
    return result;
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð²ÑÐµÑ… Ð´Ð¾ÑÑ‚ÑƒÐ¿Ð½Ñ‹Ñ… Ð½Ð¾Ð¼ÐµÑ€Ð¾Ð² Ð¿Ð¾Ð·Ð¸Ñ†Ð¸Ð¹
  const getAvailablePositionNumbers = () => {
    const uniqueNumbers = [...new Set(positions.map(pos => pos.number))];
    return uniqueNumbers.sort((a, b) => a - b);
  };

  const handlePlaceClick = (positionNumber) => {
    console.log(`ðŸ“ Place ${positionNumber} clicked`);
    const positionInfo = getPositionInfo(positionNumber);
    if (positionInfo && positionInfo.computer) {
      setSelectedPlace(positionNumber);
      setShowPlaceDetails(true);
      console.log(`âœ… Place ${positionNumber} selected, opening details`);
    } else {
      console.log('âŒ No computer found for position:', positionNumber);
      alert('Ð­Ñ‚Ð¾ Ð¼ÐµÑÑ‚Ð¾ Ð²Ñ€ÐµÐ¼ÐµÐ½Ð½Ð¾ Ð½ÐµÐ´Ð¾ÑÑ‚ÑƒÐ¿Ð½Ð¾ Ð´Ð»Ñ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ.');
    }
  };

  const handlePlaceSelectFromDetails = (positionNumber, placeRate) => {
    console.log('ðŸ“ Selecting place from details:', positionNumber);
    
    const positionInfo = getPositionInfo(positionNumber);
    
    if (positionInfo && positionInfo.computer) {
      const newComputerId = positionInfo.computerId.toString();
      
      console.log('ðŸ†” Setting computer_id to:', newComputerId);
      console.log('ðŸ’° Using place rate:', placeRate);
      
      setSelectedPlace(positionNumber);
      setSelectedPlaceRate(placeRate);
      setFormData(prev => ({
        ...prev,
        place: positionNumber.toString(),
        computer_id: newComputerId
      }));
      
      setShowPlaceDetails(false);
      
      console.log('âœ… Place selected successfully!');
      console.log('   - Selected Place:', positionNumber);
      console.log('   - Computer ID:', newComputerId);
      console.log('   - Place rate:', placeRate);
      
      setTimeout(() => {
        console.log('ðŸ”„ State after selection:');
        console.log('   - selectedPlace:', selectedPlace);
        console.log('   - formData.computer_id:', formData.computer_id);
        console.log('   - formData.place:', formData.place);
      }, 100);
      
    } else {
      console.error('âŒ Cannot select place - no computer found');
      alert('ÐžÑˆÐ¸Ð±ÐºÐ°: Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½ ÐºÐ¾Ð¼Ð¿ÑŒÑŽÑ‚ÐµÑ€ Ð´Ð»Ñ ÑÑ‚Ð¾Ð³Ð¾ Ð¼ÐµÑÑ‚Ð°. ÐŸÐ¾Ð¶Ð°Ð»ÑƒÐ¹ÑÑ‚Ð°, Ð²Ñ‹Ð±ÐµÑ€Ð¸Ñ‚Ðµ Ð´Ñ€ÑƒÐ³Ð¾Ðµ Ð¼ÐµÑÑ‚Ð¾.');
    }
  };

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸Ðº Ð²Ð¾Ð·Ð²Ñ€Ð°Ñ‚Ð° Ð¸Ð· Ð´ÐµÑ‚Ð°Ð»ÐµÐ¹
  const handleBackFromDetails = () => {
    setShowPlaceDetails(false);
  };

  const isPlaceAvailable = (positionNumber) => {
    const positionInfo = getPositionInfo(positionNumber);
    return positionInfo && positionInfo.computer !== undefined;
  };

  // Ð“ÐµÐ½ÐµÑ€Ð°Ñ†Ð¸Ñ Ð´Ð½ÐµÐ¹ Ð¼ÐµÑÑÑ†Ð° Ð´Ð»Ñ ÐºÐ°Ð»ÐµÐ½Ð´Ð°Ñ€Ñ
  const generateCalendarDays = () => {
    const year = currentMonth.getFullYear();
    const month = currentMonth.getMonth();
    
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    
    const startDay = firstDay.getDay();
    const days = [];

    for (let i = 0; i < startDay; i++) {
      days.push(null);
    }

    for (let day = 1; day <= daysInMonth; day++) {
      days.push(new Date(year, month, day));
    }

    return days;
  };

  const calendarDays = generateCalendarDays();

  const monthNames = [
    'Ð¯Ð½Ð²Ð°Ñ€ÑŒ', 'Ð¤ÐµÐ²Ñ€Ð°Ð»ÑŒ', 'ÐœÐ°Ñ€Ñ‚', 'ÐÐ¿Ñ€ÐµÐ»ÑŒ', 'ÐœÐ°Ð¹', 'Ð˜ÑŽÐ½ÑŒ',
    'Ð˜ÑŽÐ»ÑŒ', 'ÐÐ²Ð³ÑƒÑÑ‚', 'Ð¡ÐµÐ½Ñ‚ÑÐ±Ñ€ÑŒ', 'ÐžÐºÑ‚ÑÐ±Ñ€ÑŒ', 'ÐÐ¾ÑÐ±Ñ€ÑŒ', 'Ð”ÐµÐºÐ°Ð±Ñ€ÑŒ'
  ];

  const dayNames = ['Ð’Ñ', 'ÐŸÐ½', 'Ð’Ñ‚', 'Ð¡Ñ€', 'Ð§Ñ‚', 'ÐŸÑ‚', 'Ð¡Ð±'];

  const prevMonth = () => {
    setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1, 1));
  };

  const nextMonth = () => {
    setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 1));
  };

  const isToday = (date) => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const checkDate = new Date(date.getFullYear(), date.getMonth(), date.getDate());
    return checkDate.toDateString() === today.toDateString();
  };

  const isPastDate = (date) => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const checkDate = new Date(date.getFullYear(), date.getMonth(), date.getDate());
    return checkDate < today;
  };

  const validateTimeRange = (dateFrom, timeFrom, dateTo, timeTo) => {
    if (!dateFrom || !timeFrom || !dateTo || !timeTo) return true;

    const startDateTime = new Date(`${dateFrom}T${timeFrom}`);
    const endDateTime = new Date(`${dateTo}T${timeTo}`);

    if (endDateTime <= startDateTime) {
      setTimeError('Ð’Ñ€ÐµÐ¼Ñ Ð¾ÐºÐ¾Ð½Ñ‡Ð°Ð½Ð¸Ñ Ð´Ð¾Ð»Ð¶Ð½Ð¾ Ð±Ñ‹Ñ‚ÑŒ Ð¿Ð¾Ð·Ð¶Ðµ Ð²Ñ€ÐµÐ¼ÐµÐ½Ð¸ Ð½Ð°Ñ‡Ð°Ð»Ð°');
      return false;
    }

    const diffInMinutes = (endDateTime - startDateTime) / (1000 * 60);
    if (diffInMinutes < 30) {
      setTimeError('ÐœÐ¸Ð½Ð¸Ð¼Ð°Ð»ÑŒÐ½Ð¾Ðµ Ð²Ñ€ÐµÐ¼Ñ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ - 30 Ð¼Ð¸Ð½ÑƒÑ‚');
      return false;
    }

    if (diffInMinutes > 24 * 60) {
      setTimeError('ÐœÐ°ÐºÑÐ¸Ð¼Ð°Ð»ÑŒÐ½Ð¾Ðµ Ð²Ñ€ÐµÐ¼Ñ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ - 24 Ñ‡Ð°ÑÐ°');
      return false;
    }

    setTimeError('');
    return true;
  };

  useEffect(() => {
    if (formData.dateFrom && formData.timeFrom && formData.dateTo && formData.timeTo) {
      validateTimeRange(formData.dateFrom, formData.timeFrom, formData.dateTo, formData.timeTo);
    } else {
      setTimeError('');
    }
  }, [formData.dateFrom, formData.timeFrom, formData.dateTo, formData.timeTo]);

  useEffect(() => {
    console.log('ðŸ”„ Updating cart summary, preserving computer_id:', formData.computer_id);
    setFormData(prev => ({
      ...prev,
      cart: getCartSummary()
    }));
  }, [cartItems, getCartSummary]);

  useEffect(() => {
    if (selectedPlace && !formData.computer_id) {
      console.log('ðŸ”„ Restoring computer_id for selected place:', selectedPlace);
      const positionInfo = getPositionInfo(selectedPlace);
      if (positionInfo && positionInfo.computer) {
        setFormData(prev => ({
          ...prev,
          computer_id: positionInfo.computerId.toString(),
          place: selectedPlace.toString()
        }));
        console.log('âœ… Computer_id restored:', positionInfo.computerId.toString());
      }
    }
  }, [selectedPlace, formData.computer_id]);

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ ÑƒÑÑ‚Ð°Ð½Ð¾Ð²ÐºÐ¸ Ð°Ð´Ñ€ÐµÑÐ°
  const setAddressFromStorage = () => {
    const savedAddress = localStorage.getItem('selectedClubAddress');
    if (savedAddress) {
      const selectedClub = clubs.find(club => club.address === savedAddress);
      setFormData(prev => ({
        ...prev,
        address: savedAddress,
        club_id: selectedClub ? selectedClub.id : ''
      }));
    }
  };

  useEffect(() => {
    if (clubs.length > 0) {
      setAddressFromStorage();
    }
  }, [clubs]);

  useEffect(() => {
    const handleAddressSelected = () => {
      setAddressFromStorage();
    };

    window.addEventListener('clubAddressSelected', handleAddressSelected);
    
    return () => {
      window.removeEventListener('clubAddressSelected', handleAddressSelected);
    };
  }, [clubs]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    console.log(`ðŸ“ Changing field ${name} to: ${value}, current computer_id: ${formData.computer_id}`);
    
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    if (name === 'address') {
      localStorage.removeItem('selectedClubAddress');
    }
  };

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸Ðº Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ Ð°Ð´Ñ€ÐµÑÐ° ÐºÐ»ÑƒÐ±Ð°
  const handleClubChange = (e) => {
    const { value } = e.target;
    const selectedClub = clubs.find(club => club.address === value);
    
    console.log(`ðŸ¢ Changing club to: ${value}, preserving computer_id: ${formData.computer_id}`);
    
    setFormData(prev => ({
      ...prev,
      address: value,
      club_id: selectedClub ? selectedClub.id : ''
    }));

    localStorage.removeItem('selectedClubAddress');
  };

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸Ðº ÐºÐ»Ð¸ÐºÐ° Ð¿Ð¾ Ð¿Ð¾Ð»ÑŽ Ð´Ð°Ñ‚Ñ‹
  const handleDateFieldClick = (field) => {
    setCurrentField(field);
    setShowDatePicker(true);
    
    if (field === 'from' && formData.dateFrom) {
      const [year, month, day] = formData.dateFrom.split('-').map(Number);
      setSelectedDate(new Date(year, month - 1, day));
    } else if (field === 'to' && formData.dateTo) {
      const [year, month, day] = formData.dateTo.split('-').map(Number);
      setSelectedDate(new Date(year, month - 1, day));
    } else {
      setSelectedDate(new Date());
    }
  };

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸Ðº ÐºÐ»Ð¸ÐºÐ° Ð¿Ð¾ Ð¿Ð¾Ð»ÑŽ Ð²Ñ€ÐµÐ¼ÐµÐ½Ð¸
  const handleTimeFieldClick = (field) => {
    setCurrentField(field);
    setShowTimePicker(true);
    
    const currentTime = field === 'from' ? formData.timeFrom : formData.timeTo;
    if (currentTime && currentTime.includes(':')) {
      const [hour, minute] = currentTime.split(':');
      setSelectedHour(hour);
      setSelectedMinute(minute);
    } else {
      setSelectedHour('12');
      setSelectedMinute('00');
    }
  };

  // Ð’Ñ‹Ð±Ð¾Ñ€ Ð´Ð°Ñ‚Ñ‹ Ð² ÐºÐ°Ð»ÐµÐ½Ð´Ð°Ñ€Ðµ
  const handleDateSelect = (date) => {
    if (isPastDate(date)) return;
    setSelectedDate(date);
  };

  // ÐŸÐ¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð¸Ðµ Ð²Ñ‹Ð±Ð¾Ñ€Ð° Ð´Ð°Ñ‚Ñ‹
  const handleDateConfirm = () => {
    if (selectedDate) {
      const year = selectedDate.getFullYear();
      const month = (selectedDate.getMonth() + 1).toString().padStart(2, '0');
      const day = selectedDate.getDate().toString().padStart(2, '0');
      const dateString = `${year}-${month}-${day}`;
      
      if (currentField === 'from') {
        setFormData(prev => ({
          ...prev,
          dateFrom: dateString
        }));
        
        if (formData.dateTo && new Date(formData.dateTo) < new Date(dateString)) {
          setFormData(prev => ({
            ...prev,
            dateTo: dateString,
            timeTo: ''
          }));
        }
      } else {
        setFormData(prev => ({
          ...prev,
          dateTo: dateString
        }));
        
        if (formData.dateFrom && new Date(formData.dateFrom) > new Date(dateString)) {
          setFormData(prev => ({
            ...prev,
            timeTo: ''
          }));
        }
      }
      
      setShowDatePicker(false);
      setCurrentField(null);
    }
  };

  // ÐŸÐ¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð¸Ðµ Ð²Ñ‹Ð±Ð¾Ñ€Ð° Ð²Ñ€ÐµÐ¼ÐµÐ½Ð¸
  const handleTimeConfirm = () => {
    if (selectedHour && selectedMinute) {
      const timeString = `${selectedHour}:${selectedMinute}`;
      
      if (currentField === 'from') {
        setFormData(prev => ({
          ...prev,
          timeFrom: timeString
        }));
        
        if (formData.timeTo && formData.dateFrom === formData.dateTo) {
          const newStartTime = new Date(`2000-01-01T${timeString}`);
          const currentEndTime = new Date(`2000-01-01T${formData.timeTo}`);
          if (currentEndTime <= newStartTime) {
            setFormData(prev => ({
              ...prev,
              timeTo: ''
            }));
          }
        }
      } else {
        setFormData(prev => ({
          ...prev,
          timeTo: timeString
        }));
      }
      
      setShowTimePicker(false);
      setCurrentField(null);
    }
  };

  // ÐžÑ‚Ð¼ÐµÐ½Ð° Ð²Ñ‹Ð±Ð¾Ñ€Ð° Ð´Ð°Ñ‚Ñ‹
  const handleDateCancel = () => {
    setShowDatePicker(false);
    setCurrentField(null);
    setSelectedDate(null);
  };

  // ÐžÑ‚Ð¼ÐµÐ½Ð° Ð²Ñ‹Ð±Ð¾Ñ€Ð° Ð²Ñ€ÐµÐ¼ÐµÐ½Ð¸
  const handleTimeCancel = () => {
    setShowTimePicker(false);
    setCurrentField(null);
    setSelectedHour('12');
    setSelectedMinute('00');
  };

  // Ð¤Ð¾Ñ€Ð¼Ð°Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ Ð´Ð°Ñ‚Ñ‹ Ð´Ð»Ñ Ð¾Ñ‚Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ñ
  const formatDateDisplay = (dateString) => {
    if (!dateString) return '';
    
    // ÐŸÐ°Ñ€ÑÐ¸Ð¼ Ð´Ð°Ñ‚Ñƒ Ð¿Ñ€Ð°Ð²Ð¸Ð»ÑŒÐ½Ð¾
    const [year, month, day] = dateString.split('-').map(Number);
    const date = new Date(year, month - 1, day);
    
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);
    
    if (date.toDateString() === today.toDateString()) {
      return 'Ð¡ÐµÐ³Ð¾Ð´Ð½Ñ';
    } else if (date.toDateString() === tomorrow.toDateString()) {
      return 'Ð—Ð°Ð²Ñ‚Ñ€Ð°';
    } else {
      return date.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'long',
        weekday: 'short'
      });
    }
  };

  // Ð—Ð°ÐºÑ€Ñ‹Ñ‚Ð¸Ðµ Ð¿Ð¸ÐºÐµÑ€Ð¾Ð² Ð¿Ñ€Ð¸ ÐºÐ»Ð¸ÐºÐµ Ð²Ð½Ðµ Ð¸Ñ…
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (showDatePicker && !e.target.closest('.date-picker')) {
        handleDateCancel();
      }
      if (showTimePicker && !e.target.closest('.time-picker')) {
        handleTimeCancel();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showDatePicker, showTimePicker]);

  // Ð Ð°ÑÑ‡ÐµÑ‚ Ñ‡Ð°ÑÐ¾Ð² Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ
  const getBookingHours = () => {
    if (!formData.dateFrom || !formData.timeFrom || !formData.dateTo || !formData.timeTo) return 0;
    
    const startDateTime = new Date(`${formData.dateFrom}T${formData.timeFrom}`);
    const endDateTime = new Date(`${formData.dateTo}T${formData.timeTo}`);
    const hoursDiff = (endDateTime - startDateTime) / (1000 * 60 * 60);
    
    return Math.round(hoursDiff * 10) / 10;
  };

  // Ð Ð°ÑÑ‡ÐµÑ‚ Ð¼Ð¸Ð½ÑƒÑ‚ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ
  const getBookingMinutes = () => {
    if (!formData.dateFrom || !formData.timeFrom || !formData.dateTo || !formData.timeTo) return 0;
    
    const startDateTime = new Date(`${formData.dateFrom}T${formData.timeFrom}`);
    const endDateTime = new Date(`${formData.dateTo}T${formData.timeTo}`);
    return Math.round((endDateTime - startDateTime) / (1000 * 60));
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¾Ñ‚Ð»Ð°Ð´ÐºÐ¸ ÑÑ‚Ð°Ñ‚ÑƒÑÐ° Ð±Ñ€Ð¾Ð½Ð¸
  const debugBookingStatus = () => {
    console.log('=== DEBUG BOOKING STATUS ===');
    console.log('selectedPlace:', selectedPlace);
    console.log('dateFrom:', formData.dateFrom);
    console.log('timeFrom:', formData.timeFrom);
    console.log('dateTo:', formData.dateTo);
    console.log('timeTo:', formData.timeTo);
    console.log('address:', formData.address);
    console.log('computer_id:', formData.computer_id);
    console.log('timeError:', timeError);
    console.log('cartItems:', cartItems.length, 'items');
    console.log('isBookingDisabled:', isBookingDisabled);
    console.log('============================');
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð¿Ñ€Ð¸Ñ‡Ð¸Ð½Ñ‹ Ð±Ð»Ð¾ÐºÐ¸Ñ€Ð¾Ð²ÐºÐ¸ ÐºÐ½Ð¾Ð¿ÐºÐ¸
  const getDisabledReason = () => {
    const reasons = [];
    
    if (!selectedPlace) reasons.push("ÐÐµ Ð²Ñ‹Ð±Ñ€Ð°Ð½Ð¾ Ð¼ÐµÑÑ‚Ð¾");
    if (!formData.dateFrom) reasons.push("ÐÐµ Ð²Ñ‹Ð±Ñ€Ð°Ð½Ð° Ð´Ð°Ñ‚Ð° Ð½Ð°Ñ‡Ð°Ð»Ð°");
    if (!formData.timeFrom) reasons.push("ÐÐµ Ð²Ñ‹Ð±Ñ€Ð°Ð½Ð¾ Ð²Ñ€ÐµÐ¼Ñ Ð½Ð°Ñ‡Ð°Ð»Ð°");
    if (!formData.dateTo) reasons.push("ÐÐµ Ð²Ñ‹Ð±Ñ€Ð°Ð½Ð° Ð´Ð°Ñ‚Ð° Ð¾ÐºÐ¾Ð½Ñ‡Ð°Ð½Ð¸Ñ");
    if (!formData.timeTo) reasons.push("ÐÐµ Ð²Ñ‹Ð±Ñ€Ð°Ð½Ð¾ Ð²Ñ€ÐµÐ¼Ñ Ð¾ÐºÐ¾Ð½Ñ‡Ð°Ð½Ð¸Ñ");
    if (!formData.address) reasons.push("ÐÐµ Ð²Ñ‹Ð±Ñ€Ð°Ð½ Ð°Ð´Ñ€ÐµÑ ÐºÐ»ÑƒÐ±Ð°");
    if (!formData.computer_id) reasons.push("ÐÐµ ÑƒÑÑ‚Ð°Ð½Ð¾Ð²Ð»ÐµÐ½ computer_id");
    if (timeError) reasons.push(timeError);
    
    return reasons.join(", ");
  };

  // ÐŸÑ€Ð¾Ð²ÐµÑ€ÐºÐ° Ð´Ð¾ÑÑ‚ÑƒÐ¿Ð½Ð¾ÑÑ‚Ð¸ ÐºÐ½Ð¾Ð¿ÐºÐ¸ Ð±Ñ€Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ
  const isBookingDisabled = !selectedPlace || 
    !formData.dateFrom || !formData.timeFrom || 
    !formData.dateTo || !formData.timeTo ||
    !formData.address ||
    !formData.computer_id ||
    timeError;

  // Ð’Ñ‹Ð·Ñ‹Ð²Ð°ÐµÐ¼ Ð¾Ñ‚Ð»Ð°Ð´ÐºÑƒ Ð¿Ñ€Ð¸ Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ð¸ ÐºÐ»ÑŽÑ‡ÐµÐ²Ñ‹Ñ… ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ð¹
  useEffect(() => {
    debugBookingStatus();
  }, [selectedPlace, formData, timeError, cartItems, isBookingDisabled]);

  // ÐžÐ‘ÐÐžÐ’Ð›Ð•ÐÐÐ«Ð™ handleSubmit - Ñ Ð·Ð°Ñ‰Ð¸Ñ‚Ð¾Ð¹ computer_id
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('=== SUBMIT BUTTON CLICKED ===');
    
    // Ð¤Ð¸Ð½Ð°Ð»ÑŒÐ½Ð°Ñ Ð¿Ñ€Ð¾Ð²ÐµÑ€ÐºÐ° Ð¿ÐµÑ€ÐµÐ´ Ð¾Ñ‚Ð¿Ñ€Ð°Ð²ÐºÐ¾Ð¹
    if (!validateTimeRange(formData.dateFrom, formData.timeFrom, formData.dateTo, formData.timeTo)) {
      console.log('Validation failed');
      return;
    }

    // Ð”Ð¾Ð¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð°Ñ Ð¿Ñ€Ð¾Ð²ÐµÑ€ÐºÐ° computer_id
    if (!formData.computer_id && selectedPlace) {
      console.log('ðŸ”„ Attempting to recover computer_id...');
      const positionInfo = getPositionInfo(selectedPlace);
      if (positionInfo && positionInfo.computer) {
        const recoveredComputerId = positionInfo.computerId.toString();
        console.log('âœ… Recovered computer_id:', recoveredComputerId);
        setFormData(prev => ({
          ...prev,
          computer_id: recoveredComputerId
        }));
        
        // Ð–Ð´ÐµÐ¼ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ñ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ
        setTimeout(() => {
