import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import '../styles/CombinedLayout.css';

const CombinedLayoutBooking = () => {
  const { 
    cartItems, 
    updateCartItemQuantity, 
    clearCart, 
    getTotalPrice, 
    getTotalItems,
    getCartSummary 
  } = useCart();
  
  const [formData, setFormData] = useState({
    place: '',
    date: '',
    timeFrom: '',
    timeTo: '',
    address: '',
    cart: ''
  });

  const [selectedPlace, setSelectedPlace] = useState(null);
  const [showDateTimePicker, setShowDateTimePicker] = useState(false);
  const [currentField, setCurrentField] = useState(null); // 'timeFrom' или 'timeTo'
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedHour, setSelectedHour] = useState(null);
  const [selectedMinute, setSelectedMinute] = useState(null);
  const [currentMonth, setCurrentMonth] = useState(new Date());

  // Часы и минуты для выбора
  const hours = Array.from({ length: 24 }, (_, i) => i.toString().padStart(2, '0'));
  const minutes = ['00', '15', '30', '45'];

  // Генерация дней месяца для календаря
  const generateCalendarDays = () => {
    const year = currentMonth.getFullYear();
    const month = currentMonth.getMonth();
    
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    
    const startDay = firstDay.getDay();
    const days = [];

    // Пустые ячейки для начала месяца
    for (let i = 0; i < startDay; i++) {
      days.push(null);
    }

    // Дни месяца
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(new Date(year, month, day));
    }

    return days;
  };

  const calendarDays = generateCalendarDays();

  // Названия месяцев
  const monthNames = [
    'Январь', 'Февраль', 'Март', 'Апрель', 'Май', 'Июнь',
    'Июль', 'Август', 'Сентябрь', 'Октябрь', 'Ноябрь', 'Декабрь'
  ];

  // Названия дней недели
  const dayNames = ['Вс', 'Пн', 'Вт', 'Ср', 'Чт', 'Пт', 'Сб'];

  // Переход к предыдущему месяцу
  const prevMonth = () => {
    setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1, 1));
  };

  // Переход к следующему месяцу
  const nextMonth = () => {
    setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 1));
  };

  // Проверка, является ли дата сегодняшней
  const isToday = (date) => {
    const today = new Date();
    return date.toDateString() === today.toDateString();
  };

  // Проверка, является ли дата прошедшей
  const isPastDate = (date) => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    return date < today;
  };

  // Автоматически обновляем поле корзины при изменении cartItems
  useEffect(() => {
    setFormData(prev => ({
      ...prev,
      cart: getCartSummary()
    }));
  }, [cartItems, getCartSummary]);

  // Функция для установки адреса
  const setAddressFromStorage = () => {
    const savedAddress = localStorage.getItem('selectedClubAddress');
    if (savedAddress) {
      setFormData(prev => ({
        ...prev,
        address: savedAddress
      }));
    }
  };

  // Загружаем адрес при монтировании компонента
  useEffect(() => {
    setAddressFromStorage();
  }, []);

  // Слушаем событие выбора адреса
  useEffect(() => {
    const handleAddressSelected = () => {
      setAddressFromStorage();
    };

    window.addEventListener('clubAddressSelected', handleAddressSelected);
    
    return () => {
      window.removeEventListener('clubAddressSelected', handleAddressSelected);
    };
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    if (name === 'address') {
      localStorage.removeItem('selectedClubAddress');
    }
  };

  const handlePlaceSelect = (placeNumber) => {
    setSelectedPlace(placeNumber);
    setFormData(prev => ({
      ...prev,
      place: placeNumber.toString()
    }));
  };

  // Обработчик клика по полю времени
  const handleTimeFieldClick = (fieldName) => {
    setCurrentField(fieldName);
    setShowDateTimePicker(true);
    
    // Если в поле уже есть дата и время, разбираем их
    const currentDate = formData.date;
    const currentTime = formData[fieldName];
    
    if (currentDate) {
      setSelectedDate(new Date(currentDate));
    } else {
      setSelectedDate(new Date());
    }
    
    if (currentTime && currentTime.includes(':')) {
      const [hour, minute] = currentTime.split(':');
      setSelectedHour(hour);
      setSelectedMinute(minute);
    } else {
      setSelectedHour('12');
      setSelectedMinute('00');
    }
  };

  // Выбор даты в календаре
  const handleDateSelect = (date) => {
    if (isPastDate(date)) return; // Не позволяем выбирать прошедшие даты
    setSelectedDate(date);
  };

  // Выбор часа
  const handleHourSelect = (hour) => {
    setSelectedHour(hour);
  };

  // Выбор минуты
  const handleMinuteSelect = (minute) => {
    setSelectedMinute(minute);
  };

  // Подтверждение выбора даты и времени
  const handleDateTimeConfirm = () => {
    if (selectedDate && selectedHour && selectedMinute) {
      const dateString = selectedDate.toISOString().split('T')[0];
      const timeString = `${selectedHour}:${selectedMinute}`;
      
      setFormData(prev => ({
        ...prev,
        date: dateString,
        [currentField]: timeString
      }));
      
      setShowDateTimePicker(false);
      setCurrentField(null);
    }
  };

  // Отмена выбора
  const handleDateTimeCancel = () => {
    setShowDateTimePicker(false);
    setCurrentField(null);
    setSelectedDate(null);
    setSelectedHour(null);
    setSelectedMinute(null);
  };

  // Форматирование даты для отображения
  const formatDateDisplay = (dateString) => {
    if (!dateString) return '';
    
    const date = new Date(dateString);
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);
    
    if (date.toDateString() === today.toDateString()) {
      return 'Сегодня';
    } else if (date.toDateString() === tomorrow.toDateString()) {
      return 'Завтра';
    } else {
      return date.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'long',
        weekday: 'short'
      });
    }
  };

  // Форматирование времени для отображения
  const formatTimeDisplay = (timeString) => {
    if (!timeString) return '';
    return timeString;
  };

  // Закрытие пикера при клике вне его
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (showDateTimePicker && !e.target.closest('.datetime-picker')) {
        handleDateTimeCancel();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showDateTimePicker]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const bookingData = {
      ...formData,
      selectedPlace,
      cartItems,
      totalPrice: getTotalPrice()
    };
    console.log('Booking data:', bookingData);
    localStorage.removeItem('selectedClubAddress');
  };

  const handleClearCart = () => {
    clearCart();
  };

  return (
    <section id="combined-booking" className="combined-booking-section">
      <div className="background-container">
        <img src="/images/67f504fdfc00ad2f7d384258d27391b08ef7aabd.png" alt="Abstract background" className="bg-image" />
        <div className="bg-overlay"></div>
      </div>
      
      <div className="combined-container">
        {/* Левая часть - схема мест */}
        <div className="layout-side">
          <h2 className="section-title">Выберите место</h2>
          <div className="layout-content-box">
            <div className="layout-scaler">
              <div className="decorative-vectors">
                <img src="/images/30_51.svg" alt="decorative shape" className="decorative-shape shape-1" />
              </div>
              
              {/* Кликабельные места */}
              {[1, 2, 3, 4, 5, 6].map((num) => (
                <div 
                  key={`pc-${num}`} 
                  className={`pc-setup setup-${num} ${selectedPlace === num ? 'selected' : ''}`}
                  onClick={() => handlePlaceSelect(num)}
                >
                  <img src="/images/6356f02b474a41d638cf709af15fe1f7c6dd92c0.png" alt={`PC setup ${num}`} />
                  <span className="layout-number">{num}</span>
                </div>
              ))}
              
              {[8, 9, 12, 13].map((num) => (
                <div 
                  key={`console-${num}`} 
                  className={`console-setup setup-${num} ${selectedPlace === num ? 'selected' : ''}`}
                  onClick={() => handlePlaceSelect(num)}
                >
                  <img src="/images/1b9fb18a794f8543e1b7ff770153e91c8879c831.png" alt={`Console setup ${num}`} />
                  <span className="layout-number">{num}</span>
                </div>
              ))}
              
              {[10, 11, 14].map((num) => (
                <div 
                  key={`headphones-${num}`} 
                  className={`headphones-setup setup-${num} ${selectedPlace === num ? 'selected' : ''}`}
                  onClick={() => handlePlaceSelect(num)}
                >
                  <img src="/images/2fc05fccb4c07d9e1bb638c4487609fd22b2f1ec.png" alt={`Headphones ${num}`} />
                  <span className="layout-number">{num}</span>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Правая часть - форма бронирования */}
        <div className="booking-side">
          <h2 className="section-title">Бронь места {selectedPlace || ''}</h2>
          
          {/* Информация о корзине */}
          {getTotalItems() > 0 && (
            <div className="cart-info">
              <h3>Корзина ({getTotalItems()} товаров)</h3>
              <div className="cart-items">
                {cartItems.map(item => (
                  <div key={item.id} className="cart-item">
                    <span>{item.name} x{item.quantity}</span>
                    <div className="cart-item-controls">
                      <button 
                        onClick={() => updateCartItemQuantity(item.id, -1)}
                        className="cart-btn"
                      >-</button>
                      <span>{item.quantity}</span>
                      <button 
                        onClick={() => updateCartItemQuantity(item.id, 1)}
                        className="cart-btn"
                      >+</button>
                      <span className="cart-item-price">{item.price * item.quantity} ₽</span>
                    </div>
                  </div>
                ))}
              </div>
              <div className="cart-total">
                Итого: {getTotalPrice()} ₽
              </div>
            </div>
          )}

          <form className="booking-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="place">Выбранное место</label>
              <input 
                type="text" 
                id="place" 
                name="place"
                value={formData.place}
                onChange={handleChange}
                placeholder="Нажмите на место слева"
                readOnly
                className={selectedPlace ? 'selected-place' : ''}
              />
            </div>

            <div className="datetime-fields">
              <div className="form-group">
                <label>Дата и время начала</label>
                <div className="datetime-input-wrapper">
                  <input 
                    type="text"
                    value={`${formatDateDisplay(formData.date)} ${formatTimeDisplay(formData.timeFrom)}`}
                    placeholder="Выберите дату и время"
                    readOnly
                    onClick={() => handleTimeFieldClick('timeFrom')}
                    className="datetime-input"
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Дата и время окончания</label>
                <div className="datetime-input-wrapper">
                  <input 
                    type="text"
                    value={`${formatDateDisplay(formData.date)} ${formatTimeDisplay(formData.timeTo)}`}
                    placeholder="Выберите дату и время"
                    readOnly
                    onClick={() => handleTimeFieldClick('timeTo')}
                    className="datetime-input"
                  />
                </div>
              </div>
            </div>
            
            {/* Комбинированный пикер даты и времени */}
            {showDateTimePicker && (
              <div className="datetime-picker-overlay">
                <div className="datetime-picker">
                  <div className="datetime-picker-header">
                    <h3>
                      {currentField === 'timeFrom' ? 'Выберите начало брони' : 'Выберите окончание брони'}
                    </h3>
                    <button 
                      type="button" 
                      className="datetime-picker-close"
                      onClick={handleDateTimeCancel}
                    >
                      ×
                    </button>
                  </div>
                  
                  <div className="datetime-picker-body">
                    {/* Календарь */}
                    <div className="calendar-section">
                      <div className="calendar-header">
                        <button 
                          type="button" 
                          className="calendar-nav prev"
                          onClick={prevMonth}
                        >
                          ‹
                        </button>
                        <div className="calendar-month">
                          {monthNames[currentMonth.getMonth()]} {currentMonth.getFullYear()}
                        </div>
                        <button 
                          type="button" 
                          className="calendar-nav next"
                          onClick={nextMonth}
                        >
                          ›
                        </button>
                      </div>
                      
                      <div className="calendar-grid">
                        {/* Дни недели */}
                        {dayNames.map(day => (
                          <div key={day} className="calendar-day-header">
                            {day}
                          </div>
                        ))}
                        
                        {/* Дни месяца */}
                        {calendarDays.map((date, index) => (
                          <button
                            key={index}
                            type="button"
                            className={`calendar-day ${
                              date ? (
                                isPastDate(date) ? 'past' :
                                selectedDate && date.toDateString() === selectedDate.toDateString() ? 'selected' :
                                isToday(date) ? 'today' : ''
                              ) : 'empty'
                            }`}
                            onClick={() => date && handleDateSelect(date)}
                            disabled={!date || isPastDate(date)}
                          >
                            {date ? date.getDate() : ''}
                            {date && isToday(date) && <div className="today-dot"></div>}
                          </button>
                        ))}
                      </div>
                    </div>
                    
                    {/* Выбор времени */}
                    <div className="time-section">
                      <div className="time-selectors">
                        <div className="time-selector">
                          <h4>Часы</h4>
                          <div className="time-scroll hours-scroll">
                            {hours.map(hour => (
                              <button
                                key={hour}
                                type="button"
                                className={`time-option ${selectedHour === hour ? 'selected' : ''}`}
                                onClick={() => handleHourSelect(hour)}
                              >
                                {hour}
                              </button>
                            ))}
                          </div>
                        </div>
                        
                        <div className="time-selector">
                          <h4>Минуты</h4>
                          <div className="time-scroll minutes-scroll">
                            {minutes.map(minute => (
                              <button
                                key={minute}
                                type="button"
                                className={`time-option ${selectedMinute === minute ? 'selected' : ''}`}
                                onClick={() => handleMinuteSelect(minute)}
                              >
                                {minute}
                              </button>
                            ))}
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                  
                  <div className="datetime-picker-footer">
                    <div className="selected-datetime-preview">
                      {selectedDate && selectedHour && selectedMinute ? (
                        `Выбрано: ${formatDateDisplay(selectedDate.toISOString().split('T')[0])} в ${selectedHour}:${selectedMinute}`
                      ) : (
                        'Выберите дату и время'
                      )}
                    </div>
                    <div className="datetime-picker-actions">
                      <button 
                        type="button" 
                        className="btn secondary"
                        onClick={handleDateTimeCancel}
                      >
                        Отмена
                      </button>
                      <button 
                        type="button" 
                        className="btn primary"
                        onClick={handleDateTimeConfirm}
                        disabled={!selectedDate || !selectedHour || !selectedMinute}
                      >
                        Выбрать
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            )}
            
            <div className="form-group">
              <label htmlFor="address">Адрес клуба</label>
              <select 
                id="address" 
                name="address"
                value={formData.address}
                onChange={handleChange}
                required
              >
                <option value="">Выберите клуб</option>
                <option value="ул. Рахова, 53">ул. Рахова, 53</option>
                <option value="ул. Астраханская, 15/8">ул. Астраханская, 15/8</option>
                <option value="ул. Московская, 11">ул. Московская, 11</option>
              </select>
              {formData.address && (
                <div className="auto-address-indicator">
                  ✓ Адрес автоматически выбран из карточки клуба
                </div>
              )}
            </div>
            
            <div className="booking-actions">
              <button 
                type="submit" 
                className="btn primary"
                disabled={!selectedPlace || !formData.date || !formData.timeFrom || !formData.timeTo}
              >
                Забронировать место {selectedPlace || ''}
              </button>
              <a href="#cafe" className="btn secondary">Добавить из кафе</a>
              {getTotalItems() > 0 && (
                <button 
                  type="button" 
                  className="btn secondary" 
                  onClick={handleClearCart}
                >
                  Очистить корзину
                </button>
              )}
            </div>
          </form>
        </div>
      </div>
    </section>
  );
};

export default CombinedLayoutBooking;
