import React from 'react';
import Button from './Button';
import '../styles/Pricing.css';

const Pricing = () => {
  const pricingData = [
    {
      title: "GAME ROOM",
      details: `1 ч. - 100 ₽

3 ч. - 150 ₽

c 08:00 до 14:00
3 ч.  - 240 ₽

Пакет «Ночь» ВС-ЧТ - 350 ₽

Пакет «Ночь» ПТ-СБ - 400 ₽

(Время действия пакета «Ночь» с 23:00 до 08:00)`
    },
    {
      title: "PS4, PS5",
      details: `1 ч. - 100 ₽

3 ч. - 150 ₽

c 08:00 до 14:00
3 ч.  - 240 ₽

Пакет «Ночь» ВС-ЧТ - 350 ₽

Пакет «Ночь» ПТ-СБ - 400 ₽

(Время действия пакета «Ночь» с 23:00 до 08:00)`
    },
    {
      title: "STREAM ROOM",
      details: `1 ч. - 100 ₽

3 ч. - 150 ₽

c 08:00 до 14:00
3 ч.  - 240 ₽

Пакет «Ночь» ВС-ЧТ - 350 ₽

Пакет «Ночь» ПТ-СБ - 400 ₽

(Время действия пакета «Ночь» с 23:00 до 08:00)`
    }
  ];

  const handlePriceSelect = (priceType) => {
    console.log(`Выбран тариф: ${priceType}`);
    // Здесь можно добавить логику выбора тарифа
  };

  return (
    <section id="pricing" className="pricing-section">
      <div className="container">
        <div className="title-wrapper">
          <img src="/images/-78.svg" alt="decorative line" className="title-line" />
          <h2 className="section-title">Прайс</h2>
          <img src="/images/-79.svg" alt="decorative line" className="title-line" />
        </div>
        <div className="pricing-grid">
          {pricingData.map((price, index) => (
            <div key={index} className="price-card">
              <h3 className="price-card-title">{price.title}</h3>
              <pre className="price-details">{price.details}</pre>
              <Button onClick={() => handlePriceSelect(price.title)}>
                Выбрать клуб
              </Button>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Pricing;
