import React from 'react';
import Button from './Button';
import '../styles/Hero.css';

const Hero = () => {
  return (
    <section id="hero" className="hero-section">
      <div className="hero-background">
        <img src="/images/-4.png" alt="Gaming keyboard background" />
      </div>
      <div className="container hero-container">
        <div className="hero-content">
          <h1 className="hero-subtitle">Сеть компьютерных клубов г. Саратов</h1>
          <h2 className="hero-title">ИГРОВЫЕ ПК, PS-4, PS-5К</h2>
          <Button href="#pricing">Забронировать</Button>
        </div>
        <div className="hero-info-card">
          <h3 className="card-title">3 клуба в городе</h3>
          <p className="card-address">
            ул. Б. Казачья, 67<br />
            ул. Рабочая, 53<br />
            ул. Шелковичная, 29/35
          </p>
          <Button href="#clubs">Выбрать клуб</Button>
        </div>
      </div>
    </section>
  );
};

export default Hero;
